INSERT INTO `yoruba_devotions` (`id`, `current_day`, `headline`, `topic`, `text`, `text_reading`, `prayer`, `date_uploaded`) VALUES
(3, '2023-01-01', '01, SUNDAY JANUARY', 'IMOLE OLORUN', 'Ibi Kika: Genesisi 1:3', 'Imole Olorun ninu aye eniyan tumo si opin okunkun, rukerudo, idamu ati iyapa. Imole Olorun nma fi opin si okunkun ninu aye eniyan.', 'ADURA:\r\n*Imole Olorun yio mole si ona mi loni ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Genesisi 1-3', '2023-04-06'),
(4, '2023-01-02', '01, SUNDAY JANUARY', 'IBERE OTUN', 'Ibi Kika: Genesisi 1:2', 'Nipase  iranwo Olorun nikan soso ni eniyan ti fi ni ibere otun. Ibere otun tumo si opin rudurudu, ati ijakule ninu aye eniyan. Otun tumo si opin igba kan ati ibere omiran.', 'ADURA: \r\n*Ko si aye fun ofo ati okunkun ninu aye mi ninu odun yi lâ€™oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Genesis 4-6', '2023-04-14'),
(5, '2023-01-03', '03, TUESDAY JANUARY', 'ITONI A Tâ€™OKE WA', 'Ibi Kika: Orin Dafidi 37:23', 'Bibeli wipe itoni Olorun n ma pa onigbagbo mo kuro ninu isubu sinu pakute ota.', 'ADURA: \r\n*Olorun yio ma to isise mi lâ€™oni. Emi ki yio rii sinu pakute ota ni oruko Jesu.\r\nBIBELI KIKA OJOJUMO: Genesisi 7-10', '2023-04-14'),
(6, '2023-01-04', '04, WEDNESDAY JANUARY', 'IRE ATI ANU', 'Ibi Kika: Orin Dafidi 23:6', 'Ko si eni ti o le ri ire ati anu gba lowo eniyan biko sepe Olorun ba fifun. Eyi ni nitoripe o ma n san lati odo Olorun nipase eniyansi eniyan.', 'ADURA: \r\n*Emi yo ri ire ati anu Olorun gba lekun rere loni ati lose yi ni oruko Jesu.', '2023-04-14'),
(7, '2023-01-05', '05, THURSDAY JANUARY', 'IBUKUN SISO ESO', 'Ibi Kika: Genesisi 1:22', 'Siso eso tumosi nini esi rere. O si tumo si aisi ni ipo agan ni gbogbo ona. O je ayo Olorun lati ri wipe awon omo re n so eso laye.', 'ADURA:\r\n*Gbogbo idawole mi yo so eso rere lâ€™oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Genesisi 14-16', '2023-04-14'),
(8, '2023-01-06', '06, FRIDAY JANUARY', 'MA SE Fâ€™OWO KAN', 'Ibi Kika: 1 Samueli 18:12', 'Eniyan ti o ba ti gba Jesu Oluwa gege bi Olorun re, ti o si n rin ninu ife re, yio di ko se fowo kan fun okunkun.', 'ADURA:\r\n*Ki enikeni mase fowo kan mi nitori mo ni iwalaye Olorun ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Genesisi 17-20', '2023-04-14'),
(9, '2023-01-07', '07, SATURDAY JANUARY', 'IPONJU KII SE IPIN MI', 'Ibi Kika: Marku 5:7-8', 'Obinrin onisun eje wa ninu iponju aisan fun odun mejila. Beni were arakunrin Gadara ni a pon loju lati owo awon emi aimo. Sugbon, Jesu wo won san nipa agbara Olorun.', 'ADURA: \r\n*Emi yio gbe igbeaye ti o bori arekere esu ni oruko Jesu.\r\n\r\n\r\nBIBELI KIKA/OJOJUMO: Genesisi 21-23', '2023-04-14'),
(10, '2023-01-08', '08, SUNDAY JANUARY', 'ASIRI OLUWA', 'Ibi Kika: Deuteronomi 29:29', 'ko si ohun to pamo niwaju Eleda orun ati aye. Nitorina, wa si odo Olorun eniti o n fi asiri gbogbo han.', 'ADURA:\r\n*A o fi asiri Oluwa han mi loruko Jesu.\r\nBIBIBELI KIKA/OJOJUMO: Genesisi 24-25', '2023-04-14'),
(11, '2023-01-09', '09, MONDAY JANUARY', 'IBUKUN OLUWA', 'Ibi Kika: Genesisi 12:2', 'Ibukun Oluwa ni mu ni la, lai fi lala kun. Oluwa pe Abrahamu, o si bukun fun. Wa si odo Olorun bi o ti wa, aye re ki yio wa bakan naa.', 'ADURA:\r\n*Ibukun Oluwa yi o je ipin mi.\r\nBIBELI KIKA/OJOJUMO: Genesisi 26-28', '2023-04-14'),
(12, '2023-01-10', '10, TUESDAY JANUARY', 'IRIN AJO IRE', 'Ibi Kika: Genesisi 24:21', 'Opolopo onigbagbo ni won rin irin ajo ti ko ni ire ninu, nitoripe won ko lati pe Olorun sinu irin ajo won. Bi o ti n jade lo loni, pe Olorun sinu irin ajo re.', 'ADURA:\r\n*Olorun yio se ona mi ni rere loni ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Genesisi 29-31', '2023-04-14'),
(13, '2023-01-11', '11, WEDNESDAY JANUARY', 'OJO ANU', 'Ibi Kika: Romu 11:30-32', 'Gbogbo ile Israeli ni a gbala ti won si ria nu Olorun gba nipase iku ati ajinde Jesu Christi Oluwa.', 'ADURA:\r\n*Ojo aanu yo ro si ori mi ati idile mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Genesisi 32-34', '2023-04-14'),
(14, '2023-01-12', '12, THURSDAY JANUARY', 'ISEGUN EMI', 'Ibi Kika: Jeremiah 8:22', 'Iwe orin CAC 405 wipe onisegun nla wa nihin. Fun gbogbo awon ti won woju Oluwa fun iwosan. Jesu wa nihin lati wo yin san. Ohun ti o nilo ni lati beere ogun emi ti o nilo fun iwosan re.', 'ADURA:\r\n*Ase tin be ninu eje odo aguntan yio wo arun mi san ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Genesisi 35-37', '2023-04-14'),
(15, '2023-01-13', '13, FRIDAY JANUARY', 'IYI NIWAJU AWON OTA', 'Text: Eksodu 14:4', 'Olorun bu iyi fun Mordicai ni iwaju Hamani. Bee gege ni Olorun bu iyin fun ara re ni iwaju Pharaoh nipase mimu oro re se.', 'ADURA:\r\n*Olorun yo gba ogo lori awon alantako mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Genesis 38-40', '2023-04-14'),
(16, '2023-01-14', '14, SATURDAY JANUARY', 'IPASE TO DARA', 'Text: John 21:5-6', 'Olorun ni oba ti o dari ipase awon olododo. Niwon Igba ti o ba wa ninu Olorun, ti o si tele ife re, o daju wipe ipase re yio dara.', 'ADURA:\r\n*Olorun yio to ipase mi loni ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Genesisi 41-44', '2023-04-14'),
(17, '2023-01-15', '15, SUNDAY JANUARY', 'ADURA OLODODO', 'Eko Kika: 1Samueli 1:15-17', 'Olorun ri okan gbogbo eniyan, paapaa ti ododo, osi n gbo adura won. O gbo adura Hanna, o si fi ase nipase Eli.', 'ADURA:\r\n*Olorun yio dahun adura mi, yio si fi alaafia fun okan mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Genesisi 45-47', '2023-04-14'),
(18, '2023-01-16', '16, MONDAY JANUARY', 'ISEGUN PATAPATA', 'Ibi Kika: Genesisi 26 : 15', 'Olorun fun Isaaki ni isegun patapata lori awon ara Filistini nigbati o gbe kanga ti o gbeyin.', 'ADURA: \r\n*Olorun yo fun mi ni isegun ti ko wopo lori awon asoju okunkun ti o bo kanga ogo mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Genesisi 48-50', '2023-04-14'),
(19, '2023-01-17', '17, TUESDAY JANUARY', 'ERE TI O DARA', 'Ibi Kika: Isaiah 48:17', 'Pelu ogbon Olorun nikan ni a fi le ni ere ti o dara lori gbogbo idawole wa. Olorun nikan soso ni olumo ohun gbogbo ati onisiro agba.', 'ADURA:\r\n*Olorun yo ko mi lati ni ere rere ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Eksodu 1-4', '2023-04-14'),
(20, '2023-01-18', '18, WEDNESDAY JANUARY', 'LATI INU TUBU SII AFIN OGO', 'Ibi Kika: Genesisi 41:9-14', 'A mu Josefu kuro ninu tubu lo sinu afin ogo lehin igbati o tumo ala Farao.', 'ADURA:\r\n*Olorun yo mu mi kuro ninu tubu satani lo sinu afin ogo ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Eksodu 5-7', '2023-04-14'),
(21, '2023-01-19', '19, THURSDAY JANUARY', 'IBI PAPA OKO TUTU', 'Ibi Kika: Psalms 23:2', 'Oluso agutan rere (Jesu) n dari awon agutan re (onigbagbo) lo si ibi abo, nibiti won yo ti je ajeyo, ti a o si toju won daradara ti a o si pa won mo.', 'ADURA: \r\n*Oluwa yo to mi lo sibi papa oko tutu ni oruko Jesu.\r\nBIBELI KIKA OJOJUMO: Eksodu 8-11', '2023-04-14'),
(22, '2023-01-20', '20, FRIDAY JANUARY', 'AYETA TI EMI', 'Ibi Kika: Ephesians 6:10', 'Bibeli wipe; Ko si ohun ija oloro ti a se lodi si wa ti yio se rere. A ni lati gbe ihamora Olorun wo lojojumo. Eyi ni ayeta emi wa.', 'ADURA:\r\n*Ko si ohun ija oloro ota ti a se lodi si wa ti yo se rere ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Eksodu 12-16', '2023-04-14'),
(23, '2023-01-21', '21, SATURDAY JANUARY', 'OGON OLORUN', 'Ibi Kika: Jakobu 1:5', 'SATURDAY, JANUARY 21\r\nOgbon Olorun ko se fowo ra tabi ni nipase ojo ori, sugbon, a le ni nipase Emi Olorun. Eyi ju ogbon eniyan lo.', 'ADURA:\r\n*Olorun yio bukun mi pelu ogbon nla ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Eksodu 17-19', '2023-04-14'),
(24, '2023-01-22', '22, SUNDAY JANUARY', 'EJE TO Sâ€™OWON', 'Ibi Kika: Heberu 9:22', 'Oni eje ti o n soro rere ju eje Abeli lo. Eje Jesu kin fohun esan biko se aanu.', 'ADURA:\r\n*Eje Jesu ti o sowon yio we mi nu kuro ninu gbogbo arekereke esu ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Eksodu 20-22', '2023-04-14'),
(25, '2023-01-23', '23, MONDAY JANUARY', 'AKOKO ERI', 'Ibi Kika: Ifihan 12:11', 'Eri tumo si fifi ore Olorun han ninu aye wa. Eri ma fi han bi satani ti je koto nkan ninu aye onigbagbo.', 'ADURA:\r\n*Nipa ore ofe Olorun, eri mi yio pariwo, yio si tobi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Eksodu 23-25', '2023-04-14'),
(26, '2023-01-24', '24, TUESDAY JANUARY', 'IFE OLORUN', 'Ibi Kika: Galatia 1:15-16', 'Ife Olorun tumosi mimo eto Olorun ati imuse re fun aye eniyan. O se pataki lati tete mo ife Olorun ki a si mu se ni ona to to.', 'ADURA: \r\n*Ife Olorun ni pipe yio wa si imuse ninu aye mi ni oruko Jesu\r\nBIBELI KIKA/OJOJUMO: Eksodu 26-29', '2023-04-14'),
(27, '2023-01-25', '25, WEDNESDAY JANUARY', 'OGO OLORUN', 'Ibi Kika: Genesisi 45:13', 'Ogo Olorun farahan ninu aye Josefu pelu pelu ohun gbogbo ti o la koja lati owo awon arakunrin re. Nje o ha ni iru iriri bi ti Josefu bi? Mase so ireti nu. Ogo Olorun fere farahan ninu aye re ni oruko Jesu.', 'ADURA: \r\n*Ogo Olorun yio farahan ninu aye mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Eksodu 30-32', '2023-04-14'),
(28, '2023-01-26', '26, THURSDAY JANUARY', 'OWO RERE OLORUN', 'Ibi Kika: Nehemiah 2:18', 'Bi owo rere Olorun ba wa si ori aye eniyan, igbe aye re yio gba iyipa ayeraye. Eyi sele ninu aye Nehemiah. O le sele ninu aye re na.', 'ADURA:\r\n*Owo rere Olorun yio wa lara mi bi mo tin jade lo loni ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Eksodu 33-36', '2023-04-14'),
(29, '2023-01-27', '27, FRIDAY JANUARY', 'ESE DARADARA', 'Ibi Kika: Isaiah 52:7', 'Gbogbo eni ti o polongo alaafia ati ihinrere ni Bibeli pe won ni eni ti o ni ese daradara. Ibi ti o n lo, ni yio so ti ewa ese re se ma ri.', 'ADURA:\r\n*Olorun yo sure si ese mi fun ise akin ninu odun yi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Eksodu 37-38', '2023-04-14'),
(30, '2023-01-28', '28, SATURDAY JANUARY', 'IMOLE ATOKE WA', 'Ibi Kika: Eksodu 13:21', 'Olorun baa won omo Isaeli rin lojojumo ninu imole re. irin ajo aye wa yio ya bi eniyan ba nrin pelu imole Olorun.', 'ADURA:\r\n*Imole Olorun yio mole si ipaona mi loni ni oruko Jesu\r\nBIBELI KIKA/OJOJUMO: Eksodu 39-40', '2023-04-14'),
(31, '2023-01-29', '29, SUNDAY JANUARY', 'OWO INA', 'Ibi Kika: Eksodu 13:21-22', 'Olorun baa won omo Israeli rin oru pelu owo ina. Eyi so won di kose mu fun ota won. Olorun pawon mo kuro lowo ikolu awon ota won.', 'ADURA:\r\n*Owo ina Oluwa yio lo siwaju mi, yio si jo awon ota mi run ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Matteu 1-3', '2023-04-14'),
(32, '2023-01-30', '30, MONDAY JANUARY', 'OGUN EMI ATI TI ARA', 'Ibi Kika: Eksodu 14:14', 'Gbogbo ogun ti o o farahan ninu aye eniyan je ohun ti ota ti pari ninu emi. Abajade re ni o farahan ninu ara. Won ti pari re ninu emi ki o to farahan ni gbangban.', 'ADURA:\r\n*Olorun alagbara yi o ja fun mi nipa ti ara ati ninu emi\r\nBIBELI KIKA/OJOJUMO: Matteu 4-5', '2023-04-14'),
(33, '2023-01-31', '31, TUESDAY JANUARY', 'ISE RERE', 'Ibi Kika: Matteu 5:16', 'Ise rere eniyan a di mimo nigbati imole ba mole si. Olorun yio di ayin logo nigbati ise rere wa ba farahan si Olorun ati eniyan.', 'ADURA:\r\n*Aseda aye ati orun yio ranti mi yo si san eesan fun ise rere mi loni ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Matteu 6-7', '2023-04-14'),
(34, '2023-02-01', '01, WEDNESDAY FEBRUARY', 'IRANTI', 'Ibi Kika: Genesisi 8:1', 'Olorun ranti Noah ati gbogbo ohun alaye ninu oko. Nibiti a ti gbagbe re, Olorun yio ranti re ni oruko Jesu.', 'ADURA: \r\n*Olorun ti o ranti Noah, yio ranti mi sir ere loni ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Matteu 8-9', '2023-04-14'),
(35, '2023-02-02', '02, THURSDAY FEBRUARY', 'ISOPO ATâ€™ORUN WA', 'Ibi Kika: 1Samueli 16:18-21', 'Fun gbogbo ipele ti eniyan le da laye, a nilo isopo ti o ni ere ninu. Lati ni isopo atâ€™orun wa, a nilo ibasepo ti o ni ere ninu. Nitoripe gbogbo Ibukun ti o ti owo Olorun wa ma n waye lati odo eniyan si eniyan.', 'ADURA: \r\n*Olorun ti o so Dafidi po mo Saalu yio so mi po mo Oluranlowo ayanmo mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Matteu 10-11', '2023-04-14'),
(36, '2023-02-03', '03, FRIDAY FEBRUARY', 'EMI TI O TAYO', 'Ibi Kika: Daniel 1:19-20', 'Olorun fun Danieli ni ami ororo emi totayo. Eleyi mu tayo ni gbogbo ona aye re, o si wa ninu ijoba oba meta ni Babiloni. Nipase emi titayo Olorun ma n lana fun eniyan lati duro niwaju oba ki se eniyan lasan.', 'ADURA:\r\n*Olorun ti o fun Danieli ni emi to tayo, yio fun mi ni emi titayo ti ko wopo ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Matteu 12-13', '2023-04-14'),
(38, '2023-02-04', '04, SATURDAY FEBRUARY', 'ASO OGO', 'Ibi Kika: Genesisi 41:40-42', 'Josefu wo aso ewon sugbon pelu idasi Olorun, a boa so ewon lorun re, a si wo ni aso ogo.', 'ADURA:\r\n*Olorun Abrahamu, Isaaki ati Jakobu ti o wo Josefu ni aso ogo, yio wo mi ni aso ogo ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Matteu 14-16', '2023-04-14'),
(39, '2023-02-05', '05, SUNDAY FEBRUARY', 'IRI ORUN', 'Ibi Kika: Genesisi 27:28', 'Ise iri ni lati mu itura wa. Nigbati iri orun ba se si aye eniyan, yio tu yan lara, yio si mu gbogbo ipo ti ko dara di didara.', 'ADURA:\r\n*Olorun ti o da orun ati aye yio mu ki iri orun se si mi lori ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Matteu 17-19', '2023-04-14'),
(40, '2023-02-06', '06, MONDAY FEBRUARY', 'KINIUN EYA JUDAH', 'Ibi Kka: Genesisi 49:9', 'Kiniun je eranko ti ko ni iberu. A pe ni oba gbogbo eranko ninu igbo. Bi a ba gboriyin fun kiniun lasan, melomelo ni Jesu ti n se kiniun Eya Judah. Mo titobi re ninu aye re, yio si ru mo gbogbo ota ti pon aye re loju.', 'ADURA:\r\n*Jesu, kiniun Eya Judah yio fun mi ni isegun lori kiniun okunkun tio yo aye mi lenu ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Matteu 20-21', '2023-04-14'),
(41, '2023-02-07', '07, TUESDAY FEBRUARY', 'MASE FI OWO KAN ENI AMIORORO', 'Ibi Kika: Orin Dafidi 105:15', 'Eni amiororo Olorun ni awon omo Olorun ti o ni ami Olorun ni ara won. Nigba kugba ti asoju okunkun ba ri ami yi ni ara onigbagbo, won ki yio le sumo won.', 'ADURA:\r\n*Gbogbo ow ibi ti o kan mi tabi sise lodi si mi, yio parun ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Matteu 22-23', '2023-04-14'),
(42, '2023-02-08', '08, WEDNESDAY FEBRUARY', 'IPINNU IBI KI YIO DURO', 'Ibi Kika: Esteri 7:9-10', 'Ipinnu ibi je imo ibi ti awon ota gbero lodi si awon omo Olorun. Ipinnu Hamani ni lati pa awon ara Juu lar ani ile Susani. Ipinnu ibi na kuna nitori Olorun dasi oro na nipase Esteri.', 'ADURA:\r\n*Olorun ti o yi ipinnu ibi Hamani le lori yio yi ipinnu ibi ota mi le won lori ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Matteu 24-25', '2023-04-14'),
(43, '2023-02-09', '09, THURSDAY FEBRUARY', 'GBIGBOGUN TI AWON OLOTE IBI', 'Ibi Kika: 2Samueli 15:12', 'Absalomu ati Ahithopel dite lodi si Dafidi, sugbon Olorun so ipinnu re di asan.', 'THURSDAY, FEBURARY 9\r\nADURA: \r\n*Olorun ti o fun Dafidi ni isegun lori awon olote, yio fun mi ni isegun pipe lori awon olote, yio si pawon lenu mo.\r\nBIBELI KIKA/OJOJUMO: Matteu 26', '2023-04-14'),
(44, '2023-02-10', '10, FRIDAY FEBRUARY', 'ONA ABAYO ATâ€™OKE WA', 'Ibi Kika: Genesisi 31:9-12', 'Eniyan a ma dajuko ipo tabi isoro ti o ko ja ogbon ori eniyan. Nitori na, a nilo ona abayo atokewa.', 'ADURA:\r\n*Olorun ti o fun Jakobu ni ona abayo ni ile Labani, yio fun mi ni ona abayo ti o to ni akoko ni oruko Jesu. \r\nBIBELI KIKA/OJOJUMO: Matteu 27-28', '2023-04-14'),
(45, '2023-02-11', '11, SATURDAY FEBRUARY', 'IRANLOWO OLORUN', 'Ibi Kika: Orin Dafidi 121:1-2', 'Gbogbo eniyan ni o nilo iranlowo Olorun. Eniyan ko le ran ni lowo bikose pe a seto re lati owo Olorun.', 'ADURA:\r\n*Iranlowo atokewa yi o dide fun mi ninu odun yi ni oruko Jesu.\r\nBIBELI KIKA/OJUJUMO: Marku 1-3', '2023-04-14'),
(46, '2023-02-12', '12, SUNDAY FEBRUARY', 'IPESE LOPOLOPO', 'Ibi Kika: Joeli 2:25-26', 'A sin Olorun ti ko le gbagbe awon omo re. Olorun setan lati bukun fun awon omo re ati lati pese fun aini won lopolopo.', 'ADURA:\r\n*Olorun olupese yio pese fun aini mi gbogbo ninu ose yi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Marku 4-6', '2023-04-14'),
(47, '2023-02-13', '13, MONDAY FEBRUARY', 'WURA ATI FADAKA', 'Ibi Kika: Haggai 2:8', 'Wura ati fadaka tunmo si owo ninu iwe mimo. O je eto Olorun fun awon omo re lati ni oro.', 'ADURA:\r\n*Olorun yio ran awon eniyan rere simi leni lati tami lore owo loni ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Marku 7-10', '2023-04-14'),
(48, '2023-02-14', '14, TUESDAY FEBRUARY', 'OJURERE ATOKEWA', 'Ibi Kika: Esteri 5:2', 'Esteri ri ojurere lodo oba Ahaswerusi. Bi eniyan bale sâ€™ojurere fun eniyan, Olorun yio ran awon eniyan si o lati sâ€™ojurere fun o loruko Jesu.', 'ADURA:\r\n*Ojurere Olorun yio wa mi ri loni ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Marku 11-13', '2023-04-14'),
(49, '2023-02-15', '15, WEDNESDAY FEBRUARY', 'ORORO IDUNNU', 'Ibi Kika: Orin Dafidi 45:7', 'Ororo idunnu fi han wipe ofo, ibanuje ati iku ti dopin.', 'ADURA:\r\n*Opin ti deba ofo ati ibanuje ninu aye ati ebi mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Marku 14-16', '2023-04-14'),
(50, '2023-02-16', '16, THURSDAY FEBRUARY', 'OLUSO AGUTAN RERE', 'Ibi Kika: John 10:11', 'Oluso agutan rere a ma toju awon agutan. A ma pawon mo lowo gbogbo ikolu ota.', 'ADURA:\r\n*Jesu, olus agutan rere mi yio toju mi ati idile mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Luku 1-2', '2023-04-14'),
(51, '2023-02-17', '17, FRIDAY FEBRUARY', 'OLURANLOWO AKOKO', 'Ibi Kika: Eksodu 2:17', 'Mose je oluranlowo ti o bo si akoko fun awon omo Jethro. Nigbati oluranlowo ti o bo si akoko b aba aye eniyan pade, yio mu ki irin ajo eniyan ya kankan.', 'ADURA:\r\n*Oluranlowo ti o bo si akoko yo dide fun mi loni ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Luku 3-4', '2023-04-14'),
(52, '2023-02-18', '18, SATURDAY FEBRUARY', 'OLORUN TI N PA MAJEMU MO', 'Ibi Kika: Orin Dafidi 89:34', 'A n sin Olorun ti ki n kuna. Ko da majemu re ri. Ohun kohun ti o ba so nipa tire yo wa si imuse.', 'ADURA:\r\n*Majemu rere Olorun fun aye mi yio wa si imuse ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Luku 5-6', '2023-04-14'),
(53, '2023-02-19', '19, SUNDAY FEBRUARY', 'ESO RERE', 'Ibi Kika: 43:11', 'Eso rere tumosi abajade rere tabi ikore rere loni ise eniyan.', 'ADURA:\r\n*Ni oruko Olorun Baba, Omo ati Emi Mimo, emi yo je eso rere lori ise mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Luku 7-8', '2023-04-14'),
(54, '2023-02-20', '20, MONDAY FEBRUARY', 'ASO EGAN', 'Text: Joshua 5:9.', 'Reproach simply means disgrace or shame. God told the children of Israel that He has rolled away their reproach. When God take away the reproach of a man, he will obtain favour from God and men.', 'PRAYER:\r\n*God will take away all the garment of reproach in my life and family in Jesus Name.\r\nBIBLE READING/DAY: Luke 9-10', '2023-04-14'),
(55, '2023-02-21', '21, TUESDAY FEBRUARY', 'AHON TITUN', 'Ibi Kika: Marku 16:27', 'Ahon titun je okan lara ifarahan agbara Emi Mimo. Fifi ahon titun soro n ma fun onigbagbo ni agbara. O je ohun ija iporuru lodi esu ati awon asoju re.', 'ADURA:\r\n*Mo gba ahon titun loni ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Luku 11-12', '2023-04-14'),
(56, '2023-02-22', '22, WEDNESDAY FEBRUARY', 'ERI TI KO SE GBE PAMO', 'Ibi Kika: Acts 4:14-16', 'Awon eniyan ko le jiyan iyanu ti o sele nipase Peteru ati Johannu. Awon eri kan wa ti ko se gbe pamo. Iru eyi ti o sele ni enu ona Daradara.', 'ADURA:\r\n*Ko si ohun kan laye ti yo ni agbara lati bo eri mi mole ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Luku 13-16', '2023-04-14'),
(57, '2023-02-23', '23, THURSDAY FEBRUARY', 'AWON ILERI RERE', 'Ibi Kika: 2Korinti 1:20', 'Gbogbo ileri Olorun ni beeni ati be ni yio ri. Kosi ileri Olorun kan ti o je beeko fun eni ti o gbagbo ti o si n tele ilana re.', 'ADURA:\r\n*Gbogbo ileri rere Olorun ninu aye mi yio wa si imuse ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Luku 17-19', '2023-04-14'),
(58, '2023-02-24', '24, FRIDAY FEBRUARY', 'ODI JERIKO', 'Ibi Kika: Joshua 6:1', 'Gbogbo olugbe ile Jeriko ni a hamo ninu odi. Ko si eni ti o jade tabi wole. Eleyi tumosi idena si ayanmo awon ti o fe lo si ibi imuse.', 'ADURA:\r\n*Gbogbo odi Jeriko ti o duro lodi si itesiwaju mi yio subu ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Luku 20-21', '2023-04-14'),
(59, '2023-02-25', '25, SATURDAY FEBRUARY', 'EBO IBI', 'Ibi Kika: Proverbs 21:27', 'Awon ebo ibi wa t iota ma ngbe lati ba aye eniyan, ogo tabi ayanmo je. Ero ika eniyan, ibi ni o ma n je. Ko si ibi kibi ti won ko le de lati ba aye eniyan je. Won koni ni agbara lori aye re loruko Jesu.', 'ADURA:\r\n*Gbogbo ebo ibi niwaju mi, gbina ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Luku 22', '2023-04-14'),
(60, '2023-02-26', '26, SUNDAY FEBRUARY', 'AABO OLORUN', 'Ibi Kika: Orin Dafidi 34:7', 'Ami aabo wa lori gbogbo awon omo Olorun. Eleyi so wo di kosemu fun okunkun.', 'ADURA:\r\n*Awon angeli Olorun yio rogba yi mika ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Luku 23-24', '2023-04-14'),
(61, '2023-02-27', '27, MONDAY FEBRUARY', 'OKUN PUPA YIO PINYA', 'Ibi Kika: Eksodu 14:21-22', 'Okun pupa tumosi idena. Sugbon nigbati awon Israeli de eti okun pupa, o pinya, won si koja si odi keji. A mu gbogbo idena si igbega re kuro ni oruko Jesu.', 'ADURA:\r\n*Okun pupa yio pinya fun mi lati koja lo si ile ileri mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Lefitiku 1-4', '2023-04-14'),
(62, '2023-02-28', '28, TUESDAY FEBRUARY', 'LATI INU IKORO SI ADUN', 'Ibi Kika: Eksodu 15 : 22-25', 'Nigbati awon omo Israeli de Marah, omi naa koro, sugbon Olorun yi pada si adun. Olorun n ma fi titobi re han nipa yiyi ipo aidara si didara.', 'ADURA:\r\n*Gbogbo ikoro aye mi yio di adun ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Lefitiku 5-7', '2023-04-17'),
(63, '2023-03-01', '01, WEDNESDAY MARCH', 'OGO MI KO NI SE BO', 'Ibi Kika: Genesisi 45:13', 'Awon ota Josefu ko ri ogo Josefu bo. Awon egbon Josefu ni ota re, won gbinyanju lati pa ogo re, sugbon gbogbo igbinyanju won ti josefu de inu imuse ogo.', 'ADURA:\r\n*Ogo ti Oluwa damomi yio farahan, eniyan okunkun koni ni ipa lati bori ogo mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Lefitiku 8:11', '2023-04-17'),
(64, '2023-03-02', '02, THURSDAY MARCH', 'IFE OLUWA', 'Ibi Kika: Galatia 1:5', 'Ife Oluwa ni ere idi ati eto ayeraye fun aye wa. Ose pataki fun eniyan lati sawari ife Olorun ni kutukutu aye re ko ma ba kabamo ni ikeyin.', 'ADURA:\r\n*Ojulowo ife Oluwa fun aye mi yio wa si imuse loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Lefitiku 12:14', '2023-04-17'),
(65, '2023-03-03', '03, FRIDAY MARCH', 'ISEGUN LORI AWON OLOTE', 'Ibi Kika: 2Samueli 15:12', 'Absalomu, omo Dafidi dite mo baba re, Olorun si so imo re di asan.', 'ADURA: \r\n*Gbogbo ero ibi awon olote lodi si mi yio di asan ati gbogbo ibi won yio pada sori won loruko Jesu.Ibi kika: Leviticus 15-17', '2023-04-17'),
(66, '2023-03-04', '04, SATURDAY MARCH', 'ONA TITO', 'Ibi Kika: Isaiah 45:2', 'Nigbati ona onigbagbo ba je ni tito, eyi tumo si pe ko si aaye fun idaduro, awon idiwo tabi eyikeyi iru ipalara. Irin-ajo iru eni bee yoo dun, yoo si de ibi ayanmo ni akoko.', 'ADURA: \r\nNi oruko nla Jesu Kristi ona mi yio je tito, emi ki yio si ni iriri isoro laelae.\r\nBIBELI KIKA/OJOJUMO: Lefitku 18-20', '2023-04-17'),
(67, '2023-03-05', '05, SUNDAY MARCH', 'IRANLOWO OLOHUN', 'Ibi Kika: Orin Dafidi 121:1-2', 'Ni ti eda, awon eniyan je onimotara-eni-nikan, nitori naa lati ri iranlowo gba lodo won, Olorun ni lati seto ree lona atorunwa, nipa fifi oruko, aworan tabi sisopo oluranlowo ati eni ti o nilo iranlowo papo.', 'ADURA:\r\n*Iranlowo Olorun yoo dide fun mi loni ko si si esu ti yoo da duro loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Lefitiku 21-24', '2023-04-17'),
(68, '2023-03-06', '06, MONDAY MARCH', 'AABO OLORUN', 'Ibi Kika: Isaiah 43:2', 'A wa ni akoko idibo, ati pe Olorun nikan ni o le dabobo wa kuro ninu gbogbo iru ibi. O je ojuse wa lati fi ara wa ati awon idile wa lojoojumo labe aabo Olorun, eyi je nitoripe aabo eniyan ni odiwon.', 'ADURA:\r\n*Aabo Olorun yoo wa lori emi ati idile mi ni gbogbo ona aye wa ni Oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Lefitiku25-27', '2023-04-17'),
(69, '2023-03-07', '07, TUESDAY MARCH', 'ISURA IKOKO', 'Ibi Kika: Isaiah 45:3', 'Lati inu ibi kika wa, o je mimo ni eto pe awon isura ti o farapamo wa lori ile-aye yii ti Olorun ti seleri lati fi fun awon omo re. Ojuse wa ni lati gbadura pe ki Olorun la oju wa lati ri ibi ti awon ohun-ini wonyi farasin si.', 'ADURA:\r\n*Ni oruko Jesu Kristi ti o niyebiye, ni gbogbo isura ti o farasin yoo han si mi, emi o si tobi ninu Oluwa.\r\nBIBELI KIKA/OJOJUMO: Numeri 1-2', '2023-04-17'),
(70, '2023-03-08', '08, WEDNESDAY MARCH', 'SÃTÃNÃŒ SUBU', 'Ibi Kika: Isaiah 14:12', 'Bibeli so pe â€˜ko si si aye kankan mo fun Satani ni orun. Eyi ni yoo je eri re ni oruko Jesu.', 'ADURA:\r\n*Mo gbadura ni oruko Jesu Kristi Satani ati ijoba re yio subu ninu aye ati idile mi.\r\nBIBELI KIKA/OJOJUMO: Numeri 3-4', '2023-04-17'),
(71, '2023-03-09', '09, THURSDAY MARCH', 'EJE NSEGUN', 'Ibi Kika: Ifihan 12:11', 'Eje  Jesu tunmosi igbesi aye re gan-gan ti o fi lele fun eda eniyan. áº¸jáº¹ ráº¹ bori ati pe o yi gbogbo áº¹jáº¹ miiran pada. Nitori naa, gege bi onigbagbo, a ni isegun nipa eje re.', 'ADURA:\r\n*Emi o bori gbogbo ikolu esu nipase agbara ninu eje Jesu Kristi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Numeri 5-7', '2023-04-17'),
(72, '2023-03-10', '10, FRIDAY MARCH', 'ORI, KII SE IRU', 'Ibi Kika: Deuteronomi 28:13', 'Ibukun wa nigbagbogbo fun iru igboran eyikeyi gegebi oro Olorun. Nitorinaa, fun wa lati wa loke nikan, e je ki a gboran si gbogbo awon ilana Olorun.', 'ADURA:\r\n*Oore-ofe pataki lati tele gbogbo ilana Olorun, Mo gba ni oruko Jesu.\r\n*Mo gba ororo nla Olorun lati je ori kii se iru ni gbogbo igbe ati igbesi aye mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Numeri 8-10', '2023-04-17'),
(73, '2023-03-11', '11, SATURDAY MARCH', 'ILE MI', 'Ibi Kika: Eksodu 1:21', 'Awon agbebi ni ile Egipti ko te le ilana Farao fun won nipa pipa gbogbo omokunrin awon omo Israeli. Nipa sise bee, Olorun ko ile fun won. Eyi fi han pe nigbakigba ti a ba n tele awon itoni tabi itosona, a ko ni inira lati ni ile wa.', 'ADURA:\r\n*Oluwa ati Olorun mi yio ko ile temi fun mi, yio si gbe ipele mi laruge lati ayalegbe de onile tabi iyalegbe ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Numeri 11-13', '2023-04-17'),
(74, '2023-03-12', '12, SUNDAY MARCH', 'ANGELI MI YIO LO SIWAJU RE', 'Ibi Kika: Eksodu 23:23', 'Okan ninu ipa baba ti Olorun nse ni igbesi aye awon omo re ni lati daabobo won kuro lowo ikolu eyikeyi ti awon ota, ati la ti mu won lo si ona tito. O ti so pe angeli re yoo saju lati ko gbogbo idiwo kuro ni ona wa. A nireti lati di á»rá» yii mu ki a si gbaa gbá».', 'ADURA:\r\n*Awon angeli Oluwa yio saju mi, won o si mu mi lo si ona aseyori loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Numeri 14-15', '2023-04-17'),
(75, '2023-03-13', '13, MONDAY MARCH', 'IKUNRA GILEADI', 'Ibi Kika: Jeremiah 8:22', 'Okunra je ohun itunu, iwosan tabi nkan itunu ti a lo ni ilera lati se iwosan aisan ninu ara eniyan. Sugbon okunra didun kan wa ti o le wo awon aisan ti ara ati ti emi san, iyen si ni Oro Olorun. Wa oro ti o nilo fun iwosan ráº¹ loni.', 'ADURA:\r\n*Oro olorun yoo wo mi san ninu gbogbo aarun ati aisan loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Numeri 16-18', '2023-04-17'),
(76, '2023-03-14', '14, TUESDAY MARCH', 'KINI O RI?', 'Ibi Kika: Jeremiah 1:11', 'Oro kan wa pe \'\'Ohun ti o rii ni o ni, ati ohun ti o ko le fojuinu wo iwo ko le ni\'\'. Nitorina, je ki okan re saroye titobi, igbega ati giga ti Olorun n mu o lo si.', 'ADURA:\r\n*Awon ohun ologo ti mo ri yoo wa si imuse nitoto ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Numeri 19-21', '2023-04-17'),
(77, '2023-03-15', '15, WEDNESDAY MARCH', 'EPE PADA SI AWON INUNIBINI', 'Ibi Kika: Deuteronomi 30:7', 'Nitori opolopo awon ti o wa ni iha ti Oluwa, laibikita, ko si inunibinii tabi epe lati odo awon ota le po to, won yoo ma bori nigbagbogbo. Ema se wo inunibini ti e n gba lowo awon ota yin, sugbon e wo isegun ti Oluwa ti gbe kale niwaju yin.', 'ADURA:\r\n*Gbogbo egun awon ota ati awon inunibini mi yoo pada si odo won loruko Jesu.\r\nBÃBÃ‰LÃŒ LOJOJUMO: NÃºmÃ©rÃ¬ 22-25', '2023-04-17'),
(78, '2023-03-16', '16, THURSDAY MARCH', 'IGBEGA OLORUN', 'Ibi Kika: Genesisi 39-44', 'Josefu ni igbega leyin to tumo ala Farao Ohun kan gbodo wa ti o le se ti Olorun yoo mi lori lati fun o ni igbega ti o nilo ni igbesi to kan ni aye re. Na owo lori nkan yii.', 'ADURA:\r\n*Agbara Olorun yio gbe ise owo mi laruge si aafin ogo loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Numeri 26-28', '2023-04-17'),
(79, '2023-03-17', '17, FRIDAY MARCH', 'JEHOVAH JIRE', 'Ibi Kika: Genesisi 22:8-14', 'Fun gbogbo ipese ti awon omo Olorun nilo, o wa ni agbara Olorun lati se won. Gbogbo ohun ti a nilo lati se ni ka gbekele.', 'ADURA:\r\n*Olorun, Jehovah Jireh yoo pade mi ni aaye aini mi loni ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Numeri 29-30', '2023-04-17'),
(80, '2023-03-18', '18, SATURDAY MARCH', 'ASO EGAN', 'Ibi Kika: Josua 5:9', 'áº¸gan tumá» si itiju. O je nkan tabi ipo ti ko dun tabi ti ale gbadun. O maa n mu itiju wa si igbesi aye eyikeyi eniyan ti o wa ninu iru ipo bee. Ko rorun lati mu un lo, ayafi ti a ba ke pe Olorun fun iranlowo.', 'ADURA:\r\n*Ni oruko nla Jesu Kristi, mo yo aso egan kuro ninu aye ati idile mi loruko Jesu\r\nBIBELI KIKA/OJOJUMO: Numeri 31-32', '2023-04-17'),
(81, '2023-03-19', '19, SUNDAY MARCH', 'IROYIN RERE', 'Ibi Kika: Awon Onidajo 13:2-3', 'AngelÃ¬ Olorun si fara han iyawo Manoa, o si waasu ihin rere fun un pe o loyun omokunrin kan. O je ibewo airotele, sugbon iroyin rere yen yi igbesi aye won pada', 'ADURA:\r\n* Nipa oore-ofe pataki Olorun, mo gba iroyin ayo ti yoo yi aye mi pada ni Oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Numeri 33-34', '2023-04-17'),
(82, '2023-03-20', '20, MONDAY MARCH', 'OLOGBON ODODO', 'Ibi Kika: Eksodu 18:19', 'Kii á¹£e gbogbo eniyan jáº¹ erekusu ti imá». Baba ana Mose je bi oludamoran tito fun ise iranse Mose. Eyi mu ise-iranse re ni iyipada tuntun ati ilosiwaju tuntun. Fun okowo re, ise ati ibatan pelu awon eniyan, le ni lati mu apere ti o dara julo, o nilo imoran ti oludamoran ti o to.', 'ADURA:\r\n*Olorun rere yoo so mi po mo oludamoran to pe ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Numeri 35-36', '2023-04-17'),
(83, '2023-03-21', '21, TUESDAY MARCH', 'IYONU AWON OTA MI', 'Ibi Kika: Genesisi 45:3', 'Egbon Josefu bere si ni idamu leyin to so iru eni toun je fun won. Nigba ti Olorun ba gbe eniyan laruge, igbega naa ma ndi wahala fun awon ota wa. Igbega ti yoo mu wahala ati iyonu ba aye awon ota wa, Olorun yoo fi han ninu aye wa loruko Jesu.', 'ADURA:\r\n*Ogo ti yoo yo awon ota mi lenu yoo farahan ni aye mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Deuteronomi 1-3', '2023-04-17'),
(84, '2023-03-22', '22, WEDNESDAY MARCH', 'OKAN TI KRISTI', 'Ibi Kika: Filippi 2:5', 'Okan Kristi jáº¹ á»kan ti iráº¹láº¹. Lati ni okan Kristi gegebi eni kookan a nilo lati ko eko nipa re lati inu oro re, ati lati fi awon iwa re se lojoojumo.', 'ADURA:\r\n*Baba, fun mi ni okan kan naa ti o fun Jesu Omo re, ki o ran mi lowo lati gbe igbe aye gegebi oro re ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Deuteronomi 4-6', '2023-04-17'),
(85, '2023-03-23', '23, THURSDAY MARCH', 'MAJEMU RERE', 'Ibi Kika: Genesisi 6:18', 'Olorun ba Noa ati idile re da majemu. Paapaa nigbati agbaye n segbe, won je iyokuro ninu egbe na.', 'ADURA:\r\n*Majemu rere Olorun fun idile mi yoo se ni igbesi aye mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Deuteronomi 7-9', '2023-04-17'),
(86, '2023-03-24', '24, FRIDAY MARCH', 'Ami rere', 'Ibi Kika: Marku 16:17', 'Orisun agbara awon onigbagbo ni awon ami rere ti o se afihan ni Marku 16:17. Fun gbogbo onigbagbo otito, awon ami wonyi gbodo farahan ni igbesi aye won.', 'ADURA:\r\n*Ami rere Olorun yoo farahan ninu aye mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Deuteronomi 10-12', '2023-04-17'),
(87, '2023-03-25', '25, SATURDAY MARCH', 'OPIN WA', 'Ibi Kika: Iwe Owe 23:18', 'Ohun gbogbo ti o ni ibere ni opin. Fun gbogbo awon ipo ati isoro ti ko dun, dajudaju yio pari. Gbekele Olorun, waa si ri opin isoro yen.', 'ADURA:\r\n*Mo fi opin si gbogbo ikuna, ijakule, gbese, aini ati awon miiran ninu igbesi aye mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Deuteronomi 13-16', '2023-04-17'),
(88, '2023-03-26', '26, SUNDAY MARCH', 'ORO BUBURU', 'Ibi Kika: Ekun Jeremiah 3:37', 'Awon oro buburu wa ti awon ota re le ti so lori re. O je ojuse wa gegebi awon omo oga-ogo julo lati fi opin si won, nipase Oro Olorun.', 'ADURA:\r\n*Gbogbo oro ibi ti o duro lodi si mi ko ni duro loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: DeuteronomÃ¬ 17-20', '2023-04-17'),
(89, '2023-03-27', '27, MONDAY MARCH', 'AKOKO TO YE LODO OLORUN', 'Ibi Kika: Galatia 4:4', 'Gbogbo eto Olorun ni akoko tire. Akoko ibukun ati imuse wa fun olukuluku eniyan. Ohun gbogbo ti a gbodo se ni ki a mo akoko ifihan Olorun fun igbesi aye wa, nitorinaa a ko gbodo sare siwaju Olorun.', 'ADURA:\r\n*Akoko Olorun to ye lati bukun mi ti de, ao bukun mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: DeuteronomÃ¬ 21-24', '2023-04-17'),
(90, '2023-03-28', '28, TUESDAY MARCH', 'ETO ATORUN WA', 'Ibi Kika: 1Samueli 9:14-17', 'Igbesi aye Saulu yipada nipase ipade re pelu Samueli eyiti o je eto atorunwa lati odo Olorun. Saulu, eeyan lasan di oba. Awon eniyan kan wa ti o nilo lati pade fun igbesi aye re lati yipada. Eyi le sese nipase eto Olorun nikan.', 'ADURA:\r\n*Awon ti mo nilo lati pade fun igbesi aye mi lati gba yipada atorun wa, Oluwa so mi po mo won ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Deuteronomi 25-27', '2023-04-17'),
(91, '2023-03-29', '29, WEDNESDAY MARCH', 'AI LE SUN', 'Ibi Kika: Esteri 6:1', 'Oba ko le sun nitori Modekai, okan re ko bale titi a fi fun Modekai ni ohun ti oye fun.', 'ADURA:\r\n*Oluranlowo tabi oninuure mi koni sun titi won o fi bukun mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Deuteronomi28-30', '2023-04-17'),
(92, '2023-03-30', '30, THURSDAY MARCH', 'IWE IRANTI', 'Ibi Kika: Esteri 6:1-3', 'A ranti ise rere ti Modekai se, Oba Ahaswerusi si san ere nla fun un. Gbogbo ise rere re ni a ko gbagbe, mase ronupiwada ni rere; nitori akoko ere re yoo de.', 'ADURA:\r\n*Ise rere mi ti a ti gbagbe ni a o ranti ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: DeuteronomÃ¬ 31-34', '2023-04-17'),
(93, '2023-03-31', '31, FRIDAY MARCH', 'LATI ITIJU DE OLA', 'Ibi Kika: Esteri 6:6-8', 'Ero Hamani si Mordekai je ohun itiju, Hamani ti pese idajo iku fun Mordekai, sugbon Olorun so o di owo.', 'ADURA:\r\n*Mo yi gbogbo eto itiju ota pada si ola ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Johannu 1-3', '2023-04-17'),
(94, '2023-04-01', '01, SATURDAY APRIL', 'Agbara Ajinde', 'Ibi Kika: Johannu 11:25', 'Jesu ni ajinde ati iye. Jesu ni eni ti o le mu gbogbo ohun rere ti o ti ku pada si aaye ati lati tun sii gbogbo ilekun ti o tii lodi si igbega wa.', 'ADURA:\r\n*Agbara ajinde yio si ilekun ojurere, ati aanu fun mi loni loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Johannu 4-5', '2023-04-17'),
(95, '2023-04-02', '02, SUNDAY APRIL', 'Mio ni ku', 'Ibi Kika: Orin Dafidi 118:17', 'Oro Olorun ti je ki o ye wa pe, a ko ni ku. Olorun ti fun wa ni emi ti igboya ati idaniloju pe a ko le ku ni odo. O ti san owo fun emi wa, nitorina emi gigun aye wa, je idaniloju ninu re.', 'ADURA:\r\n*Gbogbo ohun ija iku ninu aye mi parun loruko Jesu Kristi.\r\nBIBELI KIKA/OJOJUMO: Johannu 6-8', '2023-04-17'),
(96, '2023-04-03', '03, MONDAY APRIL', 'AKOBA TO LODI', 'Ibi Kika: 2Samueli 6:16', 'áº¸nu Mikali koba niwaju Dafidi á»ba. O so ohun ti ko to ni akoko ti ko to, eyi koba aye re lona to lodi. Oun nIkan losi yagan ninu awon aya Dafidi.', 'ADURA:\r\n*Mo gbadura ni oruko Jesu Kristi; enu mi ko ni koba mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Johannu 9-10', '2023-04-17'),
(97, '2023-04-04', '04, TUESDAY APRIL', 'OGBON OLORUN', 'Ibi Kika: 1Awon Oba 3:28', 'Ogbon Olorun farahan ninu igbesi-aye Solomoni. O se pataki fun onigbagbo lati beere lowo Olorun fun ogbon, eyiti o bori ogbon eniyan.', 'ADURA:\r\n *Ogbon nla Olorun yoo fun mi ni ojutuu loni ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Johaanu 11-13', '2023-04-17'),
(98, '2023-04-05', '05, WEDNESDAY APRIL', 'YI OKUTA NA KURO.', 'Ibi Kika: Luku 24:2', 'A mu okuta ti o bo iboji Jesu Kristi kuro. Eniyan ko le se ojurere tabi se iranlowo ti ilekun si igbega re ba wa ni pipade. Bo ti wu ki enÃ¬yan je olooto to, bi won ba ti ilekun igbega re, ko le ri oju rere gba. Saaju ajinde Jesu, okuta ti o le duro bi idena fun ijade re ni a ti yi kuro.', 'ADURA:\r\n *Gbogbo okuta ti o bo ona tabi aseyori mi ni a yi kuro lo ni oruko nla Jesu Kristi.\r\nBIBELI KIKA/OJOJUMO: Johannu 14-16', '2023-04-17'),
(99, '2023-04-06', '06, THURSDAY APRIL', 'MIMO OLORUN', 'Ibi Kika: 1Peteru 1:15', 'á»Œlá»run jáº¹ mimá» ati pe o nireti pe iwá» ati emi jáº¹ mimá». Olorun ni ki gbogbo awon omoleyin re da bi oun. Iwa-mimo ni kokoro lati wo iwaju Olorun.', 'ADURA:\r\n*Mo gba oore-ofe lati rin ni mimo ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Johannu 16-18', '2023-04-17'),
(100, '2023-04-07', '07, FRIDAY APRIL', 'IFAMI ORORO YAN FUN ISODI NLA', 'Ibi Kika: 1Samueli 16:12-13', 'Dafidi gba ami-ororo fun isodi, lati ojo naa lo, a fun ni agbara lati se ise ti a pe lati se. Nigbati Olorun ba fi ororo yan o fun titobi, iwo yoo ni agbara lati se ohun ti o ko le se tele', 'ADURA:\r\n *Mo gbadura ni oruko Jesu Kristi, mo gba ororo fun titobi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Johaanu 19-21', '2023-04-17'),
(101, '2023-04-08', '08, SATURDAY APRIL', 'ISEGUN LORI EMI ALAYANLE', 'Ibi Kika: 1Samueli 20:1', 'Saulu Oba n wo bi Dafidi se le lo fun iparun, amo Olorun daabo bo o ko si fi emi Dafidi le Saulu lowo. Olorun le se bakan naa fun iwo naa', 'ADURA:\r\n*Emi yoo ni isegun lori gbogbo emi alayanle ti o se abojuto igbesi aye mi fun iparun ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Ise Awon Aposteli 1-2', '2023-04-17'),
(102, '2023-04-09', '09, SUNDAY APRIL', 'OLURANLOWO TOTOO', 'Ibi Kika: 1Samueli 18:3', 'Jonatani ni oluranlowo tooto fun Dafidi. O ran DafidÃ¬ lowo titi de ipo ite re laika gbogbo isoro si, paapaa nigbati emi re le bo sii. O nilo oluranlowo totoo, ti yoo ran o lowo titi iwo o fi de ipo ti Olorun ti pinnu fun o lati wa.', 'ADURA:\r\n*Olorun yo so mi po mo oluranlowo olododo ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Ise Awon Aposteli 3-4', '2023-04-17'),
(103, '2023-04-10', '10, MONDAY APRIL', 'OMODO ALAILERE', 'Ibi Kika: 2Awon Oba 5:25-26', 'Gehasi je iranse alailere fun oga re, nitori pe o te le Naamani, o fi han pe o je eni to ni ojukokoro, o si fe ebun ti Naamani mu wa. Ojukokoro re si fun u ni egun ayeraye.', 'ADURA:\r\n*Mo ko lati je iranse alailere si Olorun mi ati gbogbo agbaye ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Ise Awon Aposteli 5-6', '2023-04-17'),
(104, '2023-04-11', '11, TUESDAY APRIL', 'AMI ATI IYANU', 'Ibi Kika: Isaiah 8:18', 'Wolii Isaiah so pe oun ati awon omo re wa fun ise ami ati ise iyanu. Eyi je ikede ti o ye ki won so nipa wa ati ebi wa. So bee ki o si gbagbo, yio si wasi imuse.', 'ADURA:\r\n*Mo pase ni oruko Olorun Baba, Omo ati Emi Mimo pe, Emi ati idile mi a wa fun ise ami ati ise iyanu ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Ise Awon Aposteli 7-8', '2023-04-17'),
(105, '2023-04-12', '12, WEDNESDAY APRIL', 'SATANI LABE ESE MI.', 'Ibi Kika: Romu 16:20', 'Ifihan eyi so pe, Olorun alaafia yoo fi Satani si abe re. A ti fo Satani, ti segun patapata o si mu ko di alaini le sise kankan ninu aye wa, nitorinaa mase wo isogo re ninu aye re, o je ota ti a ti segun re tele. Ma beru!', 'ADURA:\r\n*Mo pase ni oruko Jesu Kristi ota ile yoo wa labe ese mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Ise Awon Aposteli 9-10', '2023-04-17'),
(106, '2023-04-13', '13, THURSDAY APRIL', 'OREOFE NLA', 'Ibi Kika: Ise Awon Aposteli 4:33', 'Oore-ofe ni ojurere atorunwa si eniyan; o si je aanu Olorun, gege bi iyato si idajo olododo re. Awá»n Aposteli Jesu Oluwa sise ninu ati pelu oore-ofe nla ti a da sori won lopolopo nipase Jesu. O le gbadun oore-ofe yii pelu ninu Kristi Jesu.', 'ADURA:\r\n*Mo gba oore-ofe nla Olorun fun ise akin nla ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Ise Awon Aposteli 11-12', '2023-04-17'),
(107, '2023-04-14', '14, FRIDAY APRIL', 'AHON IBI', 'Ibi Kika: Jakobu 3:6-7', 'Ahon je eya ara kekere ninu ara eniyan, sugbon o kun fun ina. Ahá»n le tun tabi ba ayanmá» jáº¹.', 'ADURA:\r\n*Gbogbo ahon buburu ti o fohun lodi si mi, ku ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Ise Awon Aposteli 13-14', '2023-04-17'),
(108, '2023-04-15', '15, SATURDAY APRIL', 'IMOLE OLORUN', 'Ibi Kika: Johannu 1: 4-5', 'Jesu ni imole aye. Eyi tumo si pe awon agbara Satani ati okunkun ko bori oro ti nse Oluwa wa Jesu. A je asoju Kristi lori ile, nitorinaa a je imole re lori ile, ati nipase wa, okunkun ko le ni ijoba ninu igbesi aye ati ebi wa.', 'ADURA:\r\n*Nipase mi opin de ba okunkun ni idile mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Ise Awon Aposteli 15-17', '2023-04-17'),
(109, '2023-04-16', '16, SUNDAY APRIL', 'AYO OLUWA.', 'Ibi Kika: Filippi 4:4', 'Ayá» jáº¹ á»kan ninu awá»n eso ti emi. O se koko fun gbogbo Kristeni lati ni ayo laika ohun ti a n la koja. Iwa ti igbesi aye Onigbagbo ye ki o je okan ti nyo ni gbobgo igba.', 'ADURA:\r\n*Mo gba ayo Oluwa sinu okan mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Ise Awon Aposteli 18-19', '2023-04-17'),
(110, '2023-04-17', '17, MONDAY APRIL', 'IKOLO MI YOO PADA', 'Ibi Kika: Orin Dafidi 126:1', 'Nigba ti Olorun da ikolo Israeli pada, o da bi ala loju won, nitori pe, won ko le gba gbo pe Olorun le je ki won pada si ile won leyin igba pipe, eyi ti o je afowo fa won. Ohun yoowu ti a le padanu yala nipase ese tabi aimokan ni a le mu pada bo sipo bi a ba ronu piwada ni tooto.', 'ADURA:\r\n*Mo gba gbogbo ikolo mi ti ota ti gba loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Ise Awon Aposteli 20-21', '2023-04-17'),
(111, '2023-04-18', '18, TUESDAY APRIL', 'PAPA OKO TUTU', 'Ibi Kika: Orin Dafidi 23:2', 'Nigbakugba ti awá»n agutan ba ti yo, nwon dubule. Nitori itoju Oluwa lori wa, a ni ite`lorun ipese ti ara ati ti emi ti o wa lati odo re, bayi a gba isinmi wa ni ojiji itoju re. Nitorina, papa oko tutu jáº¹ aaye isinmi ati itunu.', 'ADURA:\r\n*Mo gba oore-ofe pataki lati gbe ni papa oko tutu ti Olorun Olodumare ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Ise Awon Aposteli 22-25', '2023-04-17'),
(112, '2023-04-19', '19, WEDNESDAY APRIL', 'ITONI TOTOO', 'Ibi Kika: Johannu 21:5', 'Peteru ati die ninu awon omo-ehin si lo fun ipeja, nwon ko si le ri eja. Sugbon Jesu ran won lowo nipa didari won lo si ibi ti won ti le ri eja. Olorun ni eto aye wa ni owo re, ati pe nipase re nikan ni a le gba itosona gangan fun igbesi aye wa. Ni ibasepo pelu re, ki o le to o lo si ona ti o to.', 'ADURA:\r\n* Emi yoo lo si ona ti o to ti Olorun fun igbesi aye mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Ise Awon Aposteli 26-27', '2023-04-17'),
(113, '2023-04-20', '20, THURSDAY APRIL', 'ODI JERIKO SUBU', 'Ibi Kika: Josua 6:20', 'Ogiri Jeriko wo lule niwaju awon omo Israeli, eyi si je ki won ni ominira lati rin la aarin re wo ile ileri won. Nigbati Olorun ba mu awon idiwo kuro ti o duro bi odi saaju gbigbeja re, lehinna yoo rorun fun o lati wo inu imuse re.', 'ADURA:\r\n*Mo gbadura ni oruko Jesu Kristi odi ti o duro laarin emi ati oluranlowo mi subu ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Ise Awon Aposteli 28', '2023-04-17'),
(114, '2023-04-21', '21, FRIDAY APRIL', 'OYE TOYEKOROO', 'Ibi Kika: Danieli 9:2', 'Awon omo Israeli gbodo lo aadorin odun ni ile ajeji, Danieli si loye hÃ¬n ise naa fun ominira won. Nigbati o ba ni oye ti o daju nipa ohun ti Olorun ti pinnu fun o lati se, gbogbo oro Esu kii yoo tan o pelu oro re.', 'PRAYER: \r\n*Mo gba oye ti o daju nipa Olorun lati se ise nla ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Roomu 1-3', '2023-04-17'),
(115, '2023-04-22', '22, SATURDAY APRIL', 'ALAFIA OLORUN', 'Ibi Kika: Johannu 14:27', 'Jesu Kristi ni alade alaafia ati olufunni ni alafia. O ti se ileri alafia re fun wa gege bi o ti se fun awon omo-ehin re. A wa ninu aye nibiti idarudapo ati awon iji nigbagbogbo wa, sugbon mase beru, Olorun wa pelu re paapaa laaarin iji.', 'ADURA:\r\n*Alaafia Jesu Kristi Oluwa yoo wa sori emi ati idile mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Roomu 4-6', '2023-04-17'),
(116, '2023-04-23', '23, SUNDAY APRIL', 'IKORE NLA', 'Ibi Kika: Genesisi 26:12', 'Isaaki niriri ikore nla ni akoko re. O gbÃ¬n ni akoko iyan, o si kore pupo. E ma se wo isuna oro aje orile-ede tabi awawi awon eeyan, niwon igba ti Olorun ba wa pelu re, o ko le ni ipin ninu re.', 'ADURA:\r\n* Lori ise owo mi, emi yoo kore eso rere ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Roomu 7-9', '2023-04-17'),
(117, '2023-04-24', '24, MONDAY APRIL', 'AKOKO ERI MI', 'Ibi Kika: Ifihan 12:11', 'Ijeri je ohun ija nla ti o pa ijoba okunkun run ti o si fi ogo fun Olorun. O tun je agbedemeji lati kede titobi Olorun fun agbaye.', 'ADURA:\r\n*Ni oruko nla Jesu, awon eniyan yoo gbo eri mi, won o si ri eri ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Roomu 10-12', '2023-04-17'),
(118, '2023-04-25', '25, TUESDAY APRIL', 'EJE ODO AGUNTAN', 'Ibi Kika: Heberu 9:22', 'áº¸jáº¹ Jesu Kristi jáº¹ iyebiye ati agbara lati á¹£e ohun gbogbo. Nipase eje re, idariji awon ese wa, ati fifo gbogbo majemu buburu ti o duro lodi si igbesi aye wa.', 'ADURA:\r\n*Agbara ninu eje Jesu Kristi yoo gba mi lowo gbogbo majemu buburu ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Roomu 13-16', '2023-04-17'),
(119, '2023-04-26', '26, WEDNESDAY APRIL', 'ORO OLORUN', 'Ibi Kika: Orin Dafidi 107:20', 'Oro Olorun je oogun ti o ngbanila lowo iparun gbogbo. O kun fun agbara lati wo gbogbo awon aisan ti emi tabi ti ara larada.', 'ADURA:\r\n*Mo gba oogun ti emi Olorun fun iwosan mi ni oruko Jesu.\r\n BIBELI KIKA/OJOJUMO: Josua 1-4', '2023-04-17'),
(120, '2023-04-27', '27, THURSDAY APRIL', 'OORE-OFE IGI OPE', 'Ibi Kika: Orin Dafidi 92:12', 'Lara awon igi ti o wa ninu igbo, igi ope je pataki laarin won. Eyi je nitori, gbogbo igi naa wulo ati pe ko si ohun ti a le so kuro ninu re. Lati oro wa, igi ope tumosi iserere. Olorun fe ki awon omo re je olorire nÃ­ gbogbo ona ki won ma si di eni asan.', 'ADURA:\r\n*Emi o gbile bi igi-ope, emi o si je anfaani rere ninu Olorun loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Josua 5-7', '2023-04-17'),
(121, '2023-04-28', '28, FRIDAY APRIL', 'OWO RERE OLORUN', 'Ibi Kika: Nehemiah 2:18', 'Nehemiah ni owo rere Olorun lori igbesi aye re, o si saseyori ninu kiko odi Jerusalemu. Niwon igba ti owo Olorun ba wa lara re fun ise akin, dajudaju iwo yoo saseyori laika alatako si.', 'ADURA:\r\n*Owo rere Olorun yoo wa lara mi fun ise akin ti ko wopo loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Josua 8-10', '2023-04-17'),
(122, '2023-04-29', '29, SATURDAY APRIL', 'ORUN TO SI', 'Ibi Kika: Matteu 3:16', 'Leyin ti Jesu Kristi seribomi, orun si sile lati fi idi iwa saye re mule lori ile aye. Lati igba na lo, O bere sini se ise-iranse re. Nigbati orun re ba sii, awon nkan yoo bere si sise fun o.', 'ADURA:\r\n*Orun ise iyanu nla yoo si sile fun mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Josua 11-14', '2023-04-17'),
(123, '2023-04-30', '30, SUNDAY APRIL', 'IJE OLOOTO', 'Ibi Kika: 1Korinti 4:2', 'Olorun nbere otito lowo iwo ati emi ki a to le gbadun ibukun re. Nigba ti a ba ri iranse kan ni oloootito yala ni ofiisi re, ile ijosin tabi nibikibi miiran, iru eniyan bee yoo gba ere nitooto lati odo Olorun ati eniyan.', 'ADURA:\r\n*Mo gba emi otito lati oke wa ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Josua 15-18', '2023-04-17'),
(124, '2023-05-01', '01, MONDAY MAY', 'ILE RERE', 'Ibi Kika: Deuteronomi 8:7', 'Olorun ni o mu awon omo Israeli lo si ile rere ti n san fun wara ati oyin. Bayi o je akoko re lati dari si ile ti o dara ti ibukun.', 'ADURA: *Owo agbara Olorun yoo mu mi de ile rere ti imuse loruko nla Jesu Kristi.\r\nBIBELI KIKA/OJOJUMO: Josua 19-21', '2023-04-17'),
(125, '2023-05-02', '02, TUESDAY MAY', 'OJU TI EMI', 'Ibi Kika: Jeremiah 1:11-12', 'Adupe lowo Olorun fun woli Jeremiah ti o ri ohun ti Olorun fe ki o ri. Mase wo ipo buburu yen bi buburu, yi oju-iwoye re pada, ki o wo awon ohun rere ti yoo jade ninu re. Akoko re re lati rii awon ohun rere fun igbesi aye re.', 'ADURA:\r\n*Mo gba oore-ofe nla lati rii koja ohun ti ara ni oruko Jesu iyebiye. AMIN\r\nBIBELI KIKA/OJOJUMO:  Josua 22-24', '2023-04-17'),
(126, '2023-05-03', '03, WEDNESDAY MAY', 'LETA IROYIN AYO', 'Ibi Kika: Iwe Owe 25:25', 'Ohun nla ni fun okunrin tabi obinrin lati gba leta iroyin ayo. Leta iroyin ayo nyi irisi buburu eniyan pada si eyi ti o dara. Olorun yoo ka o ye fun iroyin ayo.', 'ADURA:\r\n*Nipa agbara ajinde, emi o gba leta iroyin ayo loni ni oruko nla Jesu. Amin\r\nBIBELI KIKA/OJOJUMO:  Awon Onidaajo 1-4', '2023-04-17'),
(127, '2023-05-04', '04, THURSDAY MAY', 'IWE IRANTI', 'Ibi Kika: Esteri 6:1', 'Olorun gba orun kuro loju Ahaswerusi oba, o si nifee si iwe akosile awon oba. Eyi sele ki a le fi gbe Mordekai ga. Bi Olorun ko ba mu ki awon ti o ti ran lowo ki won ma le sun, iwe iranti re yoo wa ni titi.', 'ADURA:\r\n*Oluwa je ki awon oluranlowo mi ati awon ti mo ti ran lowo ranti mi fun rere ni oruko Jesu Kristi.\r\nBIBELI KIKA/OJOJUMO: Awon Onidaajo 5-7', '2023-04-17'),
(128, '2023-05-05', '05, FRIDAY MAY', 'IMOLE ATOKEWA', 'Ibi Kika: Orin Dafidi 119:105', 'Saaju ki o to le saseyori irin-ajo aye, o nilo imole Olorun lati tan imole si ona re. Gba Olorun laaye ninu aye re, ki O le fun o ni imole re ni eyikeyi ipo idamu ti o le wa bayi.', 'ADURA:\r\n*Mo gba imole Olorun lori irin ajo mi ninu aye ni oruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: Awon Onidaajo 8-10', '2023-04-17'),
(129, '2023-05-06', '06, SATURDAY MAY', 'ISINMI LATI ODO OLORUN', 'Ibi Kika: 1Kronika 23:25', 'Olorun fun awon omo Israeli ni isinmi ni Jerusalemu titi di isakoso Solomoni. Olorun nikan ni Baba ti o ni kokoro isinmi atoke wa.', 'ADURA:\r\n*Mo wonu isimi latoke wa loni loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Awon Onidaajo 11-13', '2023-04-17'),
(130, '2023-05-07', '07, SUNDAY MAY', 'ISEGUN LORI ERO BUBURU', 'Ibi Kika: Jeremiah 17:9', 'Ni ti eda, okan eniyan buru jai, o si maa n ronu nipa ibi nigba miiran. Olorun so fun wa ninu iwe mimo ki a pa okan wa mo lodi si ifowosowopo ati ibaje Satani.', 'ADURA:\r\n*Mo gba oore-ofe pataki lati dari okan mi kuro ninu awon ero buburu ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Awon Onidaajo 14-17', '2023-04-17'),
(131, '2023-05-08', '08, MONDAY MAY', 'AWON ILEKUN TO SI', 'Ibi Kika: Orin Dafidi 24:7-10', 'Opolopo ilekun aanu, ojurere, ati iyorisi re ni a ti ti si opolopo eniyan, sugbon agbara Olorun yoo si won sile nigba ti o ba pase fun won nipase ase ninu Kristi Jesu.', 'ADURA:\r\n*Ni oruko Jesu Kristi gbogbo ilekun aanu, oju rere, ati igbejade, si sile fun mi loni ni oruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: Awon Onidaajo 18-21', '2023-04-17'),
(132, '2023-05-09', '09, TUESDAY MAY', 'IMISI ATORUNWA', 'Ibi Kika: Jobu 32:8', 'Olorun a ma misi emi eniyan lati le jise Olorun, boya nipa kiko, tabi siso.imisi yii, kin se nipa ojo ori, titobi tabi ogbon. Abajade ipari imisi Olorun ni pe o funni ni oye.', 'ADURA:\r\n*Mo gba imisi atorunwa lati odo Olorun lati le ni oye ase Olorun fun aye mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 1Korinti 1-4', '2023-04-17');
INSERT INTO `yoruba_devotions` (`id`, `current_day`, `headline`, `topic`, `text`, `text_reading`, `prayer`, `date_uploaded`) VALUES
(133, '2023-05-10', '10, WEDNESDAY MAY', 'AWON ENIYAN TOTO', 'Ibi Kika: Iá¹£e Awon Aposteli 18:24-26', 'Akuila ati Pirisila je eeyan to to ni igbesi aye Apolo, won si n samona re lona tito ninu awon nnkan ti Olorun, eyi si je ko tubo loye nipa Olorun. Nigbati o ba ni awon eniyan ti o to ni igbesi aye re, iwo ko le se asise ni gbogbo awon ona re.', 'ADURA:\r\n*Olorun rere yoo so mi po mo awon eniyan ti o se pataki si aye mi ni oruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: 1Korinti 5-7', '2023-04-17'),
(134, '2023-05-11', '11, THURSDAY MAY', 'ORIN IYIN', 'Ibi Kika: Orin Dafidi 150:1-opin', 'Iyin je fifi ope wa han si Olorun fun gbogbo ohun ti O se. O je ohun ti o dara lati korin iyin si Olorun, nitori pe o mu ki Olorun fe lati se awon ohun ologo sii fun wa.', 'ADURA:\r\n*Orin iyin titun yoo kun enu mi loruko Jesu Kristi. Amin.\r\nBIBELI KIKA/OJOJUMO: 1Korinti 8-11', '2023-04-17'),
(135, '2023-05-12', '12, FRIDAY MAY', 'ISOPO AROKEWA', 'Ibi Kika: 1Samueli 16:17-21', 'Isopo pelu Olorun lo mu Dafidi lo si aafin Saulu Oba. Dafidi ni isopo pelu Saulu latorunwa nipase okan lara awon iranse re. Ko si enikeni ti Olorun ko le lo lati so o po mo oluranlowo re, nitorina, mase fi oju tembelu enikeni.', 'ADURA:\r\n*Owo agbara Olorun yoo so mi po mo awon eniyan ti yoo ran mi lowo si aafin ogo loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 1Korinti 12-14', '2023-04-17'),
(136, '2023-05-13', '13, SATURDAY MAY', 'TITELE ITONI OLORUN', 'Ibi Kika: Johaanu 2:5', 'Awon omo odo ni igbeyawo kana tele itoni Jesu nipa pipon omi si inu nikoko, Jesu si so omi na di waini. Ko si bi itoni Olorun tile je omugo, sa ri daju pe o tele. Ranti, omugo re san ju ogbon eniyan lo.', 'ADURA:\r\n*Oluwa, fun mi ni akanse ore ofe latitele gbogbo itoni re ni gbogbo ona mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 1Korinti 15-16', '2023-04-17'),
(137, '2023-05-14', '14, SUNDAY MAY', 'ISEGUN LORI EMI KIKUN', 'Ibi Kika: Eksodu 15:24', 'Awon omo odo ni igbeyawo kana tele itoni Jesu nipa pipon omi si inu nikoko, Jesu si so omi na di waini. Ko si bi itoni Olorun tile je omugo, sa ri daju pe o tele. Ranti, omugo re san ju ogbon eniyan lo.', 'ADURA:\r\n*Mo pa emi kikun ni inu saye mi ni oruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: 2Korinti 1-4', '2023-04-17'),
(138, '2023-05-15', '15, MONDAY MAY', 'IFIHAN', 'Ibi Kika: 1Korinti 14:6', 'Ifihan je ise fifi ohun kan han ni gbangba. O je ibaraenisoro ti imo si eniyan nipase Olohun ti o ga julo. O je okan lara opolopo ona ti Olorun fi n fi ara re han, ati ilana igbe aye wa han fun wa.', 'ADURA:\r\n*Ifihan Olorun yio dari ona mi loruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: 2 Korinti 5-9', '2023-04-17'),
(139, '2023-05-16', '16, TUESDAY MAY', 'IDUPE', 'Ibi Kika: Orin Dafidi 92:1-2', 'Ohun ti o dara ni lati dupe lowo Eleda wa lori ohun ti O se fun wa. Idupe fun Olorun je ki O fe lati se sii fun wa, lai bere lowo re.', 'ADURA:\r\n*Mo gba oore-ofe pataki lati dupe lowo Olorun mi ni oruko Jesu iyebiye.\r\nBIBELI KIKA/OJOJUMO: 2Korinti 10-13', '2023-04-17'),
(140, '2023-05-17', '17, WEDNESDAY MAY', 'BIBORI OKUN PUPA', 'Ibi Kika: Eksodu 14:21-22', 'Olorun fun awon omo Israeli ni isegun lori okun Pupa. Wo bi Olorun se da awon nnkan lati mu ife re se. Ipinya okun Pupa je ise iyanu atorunwa, ki i se ohun kan ti agbara iseda lasan ti se.', 'ADURA:\r\n*Mo gba oore-ofe pataki lati bori gbogbo okun pupa ti o duro niwaju mi ni oruko nla ti Jesu Kristi. Amin\r\nBIBELI KIKA/OJOJUMO:  Galatia 1-3', '2023-04-17'),
(141, '2023-05-18', '18, THURSDAY MAY', 'EMI KI YIO KU', 'Ibi Kika: Orin Dafidi 118:17', 'Olorun ti fun wa ni aye, ki a le kede ise Re. Iku ojiji kii se ipin awon omo Olorun dipo awon ota wa.', 'ADURA:\r\n*Mo ko majemu iku ojiji ninu aye mi ati idile mi ni oruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: Galatia 4-6', '2023-04-17'),
(142, '2023-05-19', '19, FRIDAY MAY', 'JEHOVA JIRE', 'Ibi Kika: Genesisi 22:12-14', 'Jire tumo si olupese nla. Olupese nla ni Olorun wa, O pese agbo kan fun iropo Isaaki. O tun le pade awon aini re.', 'ADURA:\r\n*Olorun to ba aini Abraham pade ni aginju yoo pade gbogbo aini mi pade loruko Jesu.\r\nBIBELEI KIKA/OJOJUMO: Ruutu 1-2', '2023-04-17'),
(143, '2023-05-20', '20, SATURDAY MAY', 'IFE OLORUN', 'Ibi Kika: Galatia 1:15-16', 'Olorun mu ife ipilese re se ninu aye Aposteli Paulu. Fun gbogbo eniyan, ojulowo Ife Olorun ti wa ni akosile ti o gbodo wa si imuse ninu aye wa.', 'ADURA:\r\n*Ife atileba ti Olorun fun igbesi aye mi yoo se ni oruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: Ruutu 3-4', '2023-04-17'),
(144, '2023-05-21', '21, SUNDAY MAY', 'MO KO ASO EGAN', 'Ibi Kika: Josua 5:9', 'Egan je nkan ti a da lebi, itiju tabi eleya. A wo aso egan fun awon omo Israeli ni ile Egipti, sugbon Olorun bo kuro lorun won. Aso egan le je ifasehin tabi aini. Omo Olorun gbodo ko o sile.', 'ADURA:\r\n*Mo da Ina sun gbogbo aso egan ninu aye mi ni oruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: 1Samueli 1-2', '2023-04-17'),
(145, '2023-05-22', '22, MONDAY MAY', 'ITUSILE KURO NINU EWON', 'Ibi Kika: Ise Awon Aposteli 12:5-9', 'Won fi Peteru sewon Herodu, amo angeli Olorun mu un jade. Ewon aye eniyan le je ilekun titi, osi tabi inira. Awon ijo Olorun gbadura fun Peteru, titi o fi di eni itusile. O nilo lati kigbe si Olorun fun idande tire paapaa.', 'ADURA:\r\n*A gba mi lowo tubu ikuna, ijakule, aisan, aini, agan ni oruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: 1Samueli 3-5', '2023-04-17'),
(146, '2023-05-23', '23, TUESDAY MAY', 'EJE IYEBIYE', 'Ibi Kika: Heberu 9:22', 'Eje Jesu Kristi je eje iyebiye ti o se awon ohun ologo ni igbesi aye awon onigbagbo. O we wa mo kuro ninu ese wa, o so wa di pipe, o si tun n ba awon majemu buburu je lori aye wa.', 'ADURA:\r\n*Mo gbadura, eje odo aguntan yoo gba mi lowo gbogbo ajaga ese loruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: 1Samueli 5-8', '2023-04-17'),
(147, '2023-05-24', '24, WEDNESDAY MAY', 'ENI TOOTO', 'Ibi Kika: Iwe Owe 13:20', 'Iwe-mimo so pe, eniti o ba awon ologbon rin yoo je ologbon. Nigbati o ba darapo pelu awon eniyan ti o ni ero-rere, dajudaju iwo yoo safarawe ihuwasi tito won, ati pe eyi le si ilekun nla fun o.', 'ADURA:	\r\n*Olorun yoo so mi po mo awon ologbon eniyan ti o se iranlowo fun mi de ibi imuse mi laye ni oruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: 1Samueli 9', '2023-04-17'),
(148, '2023-05-25', '25, THURSDAY MAY', 'OPE ATI IGI KEDARI', 'IbI Kika: Orin Dafidi 92:12', 'Ope ati igi kedari je awon igi iyalenu ti o gbadun gbogbo awon akoko emi gigun lori ile aye. Eyi je idaniloju lati odo Olorun pe a oo pe lori ile aye, a o si se aseyori.', 'ADURA:\r\n*Mo gbadura, emi o gbadun ogo Olorun ni gbogbo igba ati emi gigun ni ipin mi ni oruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: 1Samueli 10-11', '2023-04-17'),
(149, '2023-05-26', '26, FRIDAY MAY', 'EBUN OLORUN', 'Ibi Kika: Orin Dafidi 92:12', 'Olorun Israeli wa pelu DafidÃ¬ ni gbogbo ona lati gbooro si i, a mu ebun wa fun un. Nigbati Olorun ba wa pelu re, o ni idaniloju pe iwo kii yoo saini ohun rere kankan laelae. Olorun yoo ma fun eniyan ni iyanju nigbagbogbo lati bukun fun o.', 'ADURA:\r\n*Olorun yoo ran awon eniyan rere ran ebun rere si mi ni oruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: 1Samueli 12-13', '2023-04-17'),
(150, '2023-05-27', '27, SATURDAY MAY', 'IKORE RERE', 'Ibi Kika Genesisi 26:12-14', 'Isaaki gbin si ile ajeji, o si kore ni ilopo ogorun-un ni odun kan naa. Ikore ti o dara je ami ilosoke nla. Fun gbogbo ise re, iwo yoo ni ikore rere ni oruko Jesu.', 'ADURA:\r\n*Emi o kore ogoogorun lori ise mi ni odun yii ni oruko Jesu Kristi.\r\nBIBELI KIKA/OJOJUMO: 1Samueli 14', '2023-04-17'),
(151, '2023-05-28', '28, SUNDAY MAY', 'ISEGUN LORI EMI IBERU', 'Ibi Kika: Job 3:25, 2Timoteu 1:7', 'Iberu tumo si ijayatabi imolara ohun tin ba ni leru. O se idiwo fun eniyan lati saseyori abi lati gbe ohun ilepa kan se. O n pa iran, o si je okan lara awon ohun elo ti Satani n lo lati fi kolu awon omo Olorun.', 'ADURA:\r\n*Mo gba ara mi lowo gbogbo emi iberu, mo si gbe aso igboiya wo ni oruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: 1Samueli 15-16', '2023-04-17'),
(152, '2023-05-29', '29, MONDAY MAY', 'OLORI KOKORO', 'Ibi Kika: Matteu 16:19', 'Awon kokoro je aami ase.  Kokoro bi a ti so ninu ese Bibeli yii kii se fun Peteru nikan, sugbon fun gbogbo onigbagbo. Nipase re, a le sii atunle fi ti gbogbo ilekun.', 'ADURA:\r\n*Mo gba kokoro lati de ati lati tule lori aye ni oruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: 1Samueli 17-18', '2023-04-17'),
(153, '2023-05-30', '30, TUESDAY MAY', 'ERI', 'Ibi Kika: Luku 5:14', 'Eri je ohun ija to n ko itiju ba Satani ati ijoba re. O je ikede isotito Olorun lori aye wa o si n yin oruko Jesu Kristi logo.', 'ADURA:\r\n*Eri rere mi yoo fi awon ota mi sinu itiju ainipekun ni oruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: 1Samueli 19-20', '2023-04-17'),
(154, '2023-05-31', '31, WEDNESDAY MAY', 'APOTI ERI', 'Ibi Kika: 1Samueli 5:3-4', 'Won gbe apoti eri lo si ile Dagoni, orisa awon eniyan ni Asidodi, sugbon Dagoni subu niwaju apoti eri Oluwa. Apoti Olorun nigbagbogbo n toka si iwalaaye á»Œlá»run. Dagoni ko le duro niwaju Olorun; nitorina, o subu lati jewo Oluwa Olorun. Awa ni apoti alagbeerin ti Oluwa, iwalaaye Olorun si ngbe inu wa, nitori naa, nigba ti awon ijoye ati alagbara ba ri wa, won yio wariri, won yoo si subu.', 'ADURA:\r\n*Nitori mo gbe ekunrere Olorun, je ki gbogbo Dagoni subu niwaju mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 1Samueli 21-22', '2023-04-17'),
(155, '2023-06-01', '01, THURSDAY JUN', 'OLUTUNU NLA', 'Ibi Kika: John 14:16-18, 26', 'Emi Mimo ni olutunu nla ti emi ati iwo nilo lati gbadun irorun ni aye. Christi ti se ileri fun wa wipe, ohun ki yo fiwa sile bi omo alaini baba, nitori na ni o fi fun wani Emi mimo lati fi ona ti Baba (Olorun) han wa, ati lati tuwa larani gbogbo ona.', 'ADURA:\r\n*Mo gba oreofe nla ti Emi Mimo lati gbadun irorun ti Olorun ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 1Samueli 23-24', '2023-04-18'),
(156, '2023-06-02', '02, FRIDAY JUN', 'ILERI TI BABA', 'Ibi Kika: Luku 24:49', 'Awa sin Baba olotito ti kin ye ileri re.  O seâ€™leri fun wa lati fun wa ni Emi Mimo, ti yio ma towa ninu aye. Iri bomi Emi Mimo je ileri Baba fun gbogbo onigbagbo. O je ekunrere agbara lati se ise Christi.', 'ADURA:\r\n*Baba mu ileri re se ninu aye mi nipa fifun mi ni Emi Mimo re ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 1Samueli 25-26', '2023-04-18'),
(157, '2023-06-03', '03, SATURDAY JUN', 'ISURA IKOKO', 'Ibi Kika: Isaiah 45:1-3', 'Awon isura ikoko nbe lowo Olorun. Olorun ni ipa lati fifun ni ati lati gba lowo eni. O ti seleri ninu oro re lati fun w ani awonoro nibi ikoko. Awon ohun ologo ti a gba lowo w ani Oluwa yio fihan, yio si dawon pada loruko Jesu.', 'ADURA:\r\n*Oluwa, fi awon isura ikoko re han fun mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 1Samueli 27-29', '2023-04-18'),
(158, '2023-06-04', '04, SUNDAY JUN', 'OWO RERE OLUWA', 'Ibi Kika: Nehemiah 2:18', 'Owo rere Oluwa wa lara Nehemiah, o si se aseyori ni gbogbo ona re. o ko odi Jerusalemu laarin ojo mejileniaadota. Ohun ti owo Olorun maa nse ninu aye eniyan nipe yio mu irin ajo eniyan YA KANKAN. Ohun ti eniyan ko le se ni odun mewa, owo Olorun yio mu se larin osu kan. Gbagbo pe yio se se.', 'ADURA:\r\n*Owo Olorun yio wa lara mi fun ise akin nla ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 1Samueli 30-31', '2023-04-18'),
(159, '2023-06-05', '05, MONDAY JUN', 'OJO OJU RERE RE', 'Ibi Kika: Esteri 5:1-3', 'Ayaba Esteri ri oju rere gba lodo oba nitori o je ojo oju rere re. ko laeto lati lo siwaju oba lai se pe oba ranse si. Eyi iba yori si iku. Sugbon, o lo, o si ye nitori o ri oju rere eniyan gba, o gbodo duro dede niwaju Olorun ki o ba le ri oju rere eniyan gba.', 'ADURA:\r\n*Oluwa, je ki ojo ju rere mi de loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 2Samueli 1-2', '2023-04-18'),
(160, '2023-06-06', '06, TUESDAY JUN', 'IRANLOWO ATOKE WA', 'Ibi Kika: Orin Dafidi 121:1-2', 'Olorun nikan ni o le fun ni ni iranwo ti o pe titi. Orin Dafidi yi ko wa lati gbekele Olorun nigba isoro. Ko si bi okan re tile daru to, ko lati ma wo Olorun dipo isoro re. Ohun ni o le mu isoro na kuro.', 'ADURA: \r\n*Mo gba iranwo lati oke wa loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 2Samuel 3-5', '2023-04-18'),
(161, '2023-06-07', '07, WEDNESDAY JUN', 'SATANI LABE ATELESE RE', 'Ibi Kika: Romu 16:20', 'Olorun Eleda wa yio fi satani si abe atelese wa titi lai lai. Eyi tunmo si pe, a o segun satani patapata. Yio si di alailagbara ninu aye wa. Nitori na, niwon igba ti a je omo Olorun, satani je ota ti a ti bori.', 'ADURA:\r\n*Satani ati awon omo ogun re yio w ani abe atelese mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 2Samueli 6-7', '2023-04-18'),
(162, '2023-06-08', '08, THURSDAY JUN', 'OJIYA IKUNRA TI EMI', 'Ibi Kika: Orin Dafidi 107:20', 'Oro Olorun je ojiya ikunra ti o wo ni san lowo gbogbo aisan ati aarun. Laisi ariyanjiyan, agbara n be ninu oro Olorun. Nigbakugba ti aisan ba n be ninu ago ara re, lo ojiya ikunra tin se oro Olorun.', 'ADURA:\r\n*Agbara tin be ninu oro Olorun yi o tu mi sile lowo gbogbo aisan ati arun ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 2Samueli 8-10', '2023-04-18'),
(163, '2023-06-09', '09, FRIDAY JUN', 'OKUN TI EMI', 'Ibi Kika: Philippians 4:13', 'Olorun nikan ni o ni kokoro okun lowo. Nigbakugba ti aare ba mu o, gbekele Oluwa, ki o si ma wo oju re fun okun. Ninu okun Olorun ni a le se ohun gbogbo.', 'ADURA:\r\n*Mo gba okun emi ti Olorun fun ise akin ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 2Samueli 11-12', '2023-04-18'),
(164, '2023-06-10', '10, SATURDAY JUN', 'OKUTA IYE', 'Ibi Kika: 1Peter 2:5', 'Kristiani je okuta iye nitori okuta ti a ko aye wa le lori wa laye. Iwe mimo je ko ye wa pe, okuta iye ni a je. Ma se gbagbe wipe ko si ohun ti o le pa okuta lara. Nitori na, a je kose fowokan fun okunokun.', 'ADURA:\r\n*Mo gba ore ofe lati je okuta iye ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 2Samueli 13-14', '2023-04-18'),
(165, '2023-06-11', '11, SUNDAY JUN', 'AYO OLUWA', 'Ibi Kika: Nehemiah 8:10', 'Enikeni ti o b ani ore ofe lati ni ayo Olorun ninu okan re ki yio mo ibauje. Ayo dabi onfa ti n fa awon ire miran paapaa, ilera pipe.', 'ADURA:\r\n*Oluwa, je ki ekunrere ayo re kunu okan mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 2Samueli 15-16', '2023-04-18'),
(166, '2023-06-12', '12, MONDAY JUN', 'ISEGUN LORI OFA ESU', 'Ibi Kika: Matthew 13:25', 'Opolopo ikolu esu saba n sele ni oru. Igba oru je akoko ti eniyan rewesi, ti won si n sun. Eleyi fun satani ni anfanni lati sise. Sugbon fun awon ti won lo igba oru won daradara paapaa lati gbadura, won yio di asegun.', 'ADURA:\r\n*Gbogbo ofa t iota ti gbin sinu aye mi, ma parun ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 2Samueli 17-18', '2023-04-18'),
(167, '2023-06-13', '13, TUESDAY JUN', 'OLUKONI ATOKE WA', 'Ibi Kika: John 14:26', 'Emi Mimo ni olukoni atokewa ti yio ko o ni ohun gbogbo ti o nilo lati mol aye. Nje o ni isoro oye iwe mimo bi? Beere iranlowo lowo Emi Mimo. Nje o ko mo bi o ti fe ririn ajo aye re? Beere lowo Emi Mimo.', 'ADURA:\r\n*Emi Mimo, iwo ni Olukoni Agba, wa ko mi ni ohun ti o ye kin mo nipa aye mi ati ayanmo mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 2Samueli 19-20', '2023-04-18'),
(168, '2023-06-14', '14, WEDNESDAY JUN', 'ORUKO TI O TAYO', 'Ibi Kika: Hebrews 1:4', 'Oruko Jesu se Pataki ninu aye wa gegebi onigbagbo. Oruko re ma n lana fun opolopo ohun rere ninu aye wa. Nje o fe serere laye, tabi o fe je eniyan ti o ni ipa rere laye bi? Ke pe oruko ti o tayo yi?', 'ADURA:\r\n*Oluwa, mo kepe oruko Jesu ti o tayo, mu mi je eniyan ti a fin se iyi laye ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 2Samueli 21-22', '2023-04-18'),
(169, '2023-06-15', '15, THURSDAY JUN', 'ORUKO JESU KRISTI', 'Ibi Kika: Iwe Owe 18:10, Acts 2:21', 'Bi ile iso giga se n fun ni ni abo nigbs ti ilu ba wa labe ikolu ota, beni oruko Oluwa fun ni ni abo ti o daju ninu aye adaanu yi. Oruko Oluwa ni agbara. Ohun ni idahun si ohun gbogbo.', 'ADURA:\r\n*Oruko Jesu yio sise fun mi ni gbogbo ona mi.\r\nBIBELI KIKA/OJOJUMO: 2Samueli 23-24', '2023-04-18'),
(170, '2023-06-16', '16, FRIDAY JUN', 'ITUSILE LOWO ESE', 'Ibi Kika: 2Kronika 7:14', 'Ese ma n baye eniyan je. O si ma y ani nipa kuro lodo Olorun. Ona abayo ti o daju kuro lowo ese ni irunupiwada tooto ati iwopale. Bi Olorun yio b agba eniyan lowo ese, yio da lori ijoloto, irele ati igboran si Olorun.', 'ADURA:\r\n*Oluwa, mo jewo ese mi, mo si ko o sile. Darijimi, sa anu fun mi, ki o si wo ile mi san ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Efesu 1-3', '2023-04-18'),
(171, '2023-06-17', '17, SATURDAY JUN', 'PA IPEJOPO IBI RUN', 'Ibi Kika: Isaiah 8:9-10', 'Opolopo awon eniyan ibi ni won kora wo jopo lodi si o. bi o ba le koju ija si won pelu ohun ija adura, isegun re daju.', 'ADURA:\r\n*Ni oruko Jesu, mo dide lodi si gbogbo ipejopo ibi si mi. e ma tuka ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Efesu 4-6', '2023-04-18'),
(172, '2023-06-18', '18, SUNDAY JUN', 'SORA FUN AHITOFELI', 'Ibi Kika: 2 Samueli 15:31', 'Ahitofeli je olugbani nimoran fun oba Dafidi, sugbon o sote si Dafidi oga re. Sora fun awon eniyan ti won sote si o sugbon ti won di bon bi olugbani nimoran. Mo gbadura wipe imoran Ahitofeli lori aiye re yi o di asan loruko Jesu.', 'ADURA:\r\n*Emi ibi ko ni lomi lodi si oluranlowo mi. emi yio ni isegun lori gbogbo Ahitofeli ti o yimika ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Filippi 1-4', '2023-04-18'),
(173, '2023-06-19', '19, MONDAY JUN', 'IROPO', 'Ibi Kika: 1Samueli 16:14', 'Iwe mimo je ko ye wa pe, Emi Olorun fi Saulu sile, emi esu si n yo lenu. Emi buburu ropo Emi Olorun ninu aye re. Sa fun ese ti o le ropo Emi Olorun ninu aye re. Eyi ki yio je ipin re ni oruko Jesu.', 'ADURA:\r\n*Emi buburu ko ni ropo Emi Olorun ninu aye wa ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Kolosse 1-4', '2023-04-18'),
(174, '2023-06-20', '20, TUESDAY JUN', 'ADALU EMI', 'Ibi Kika: Eksodu 12:38', 'Nigbati awon omo Israeli kuro ni Egipti lo si ile ileri, awon adalu emi tele won. Awon wonyi si won lona, won si mu won se si Olorun. Ise awon adalu emi wonyi ni lati mu irin ajo eniyan lo si ile ileri gun ju bi o ti ye lo.', 'ADURA:\r\n*Oluwa dide, ki o si pin emi ati awon adalu emi niya ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 1 Awon Oba 1-2', '2023-04-18'),
(175, '2023-06-21', '21, WEDNESDAY JUN', 'ILE ILERI MI', 'Ibi Kika: Deuteronomi 6:3', 'Olorun seleri fun awonomo Israeli lati mu won de ile ti o san fun wara ati oyin. Ni opin re, Olorun mu ileri re se. Nje o ti seleri fun o bi? Ma mikan, Olorun ki n se eniyan lati puro. Yio mu oro re se ninu aye re.', 'ADURA:\r\n*Gbogbo ileri Olorun ninu aye mi yio se ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 1 Awon Oba 3-4', '2023-04-18'),
(176, '2023-06-22', '22, THURSDAY JUN', 'OGUN IJINLE', 'Ibi Kika: Joshua 23:10', 'Bawo ni eniyan kan se le bori egberun eniyan laaisi atileyin Olorun? Eyi ko seese lati. Sugbon Olorun seleri fun awon Israeli pe ohun yoo ja fun won. Lati bori ogun ijinle ti o lodi si o bi aisaa, aini, iwalojukan, aini ojurere o nilo atilehin Olorun.', 'ADURA:\r\n*Mo gba atilehin Olorun lati bori gbogbo awon ota mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 1Awon Oba 5-7', '2023-04-18'),
(177, '2023-06-23', '23, FRIDAY JUN', 'ONA TI O LODI', 'Ibi Kika: Deuteronomi 2:1-3', 'Ko si eni ti o gba ona ti o lodi to le de ibi imuse laye. Opin re, a buru jai. Niitorina ki o to gbe igbese lori irin ajo, ke pe Olorun lati fi ona han o. ki o le de ibi ayanmo re ni asiko.', 'ADURA:\r\n*Mo gba akanse ore-ofe lati ri ati lati sise ni ona Olorun fun aye mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 1 Awon Oba 8', '2023-04-18'),
(178, '2023-06-24', '24, SATURDAY JUN', 'IDAPADA LEKUNRERE', 'Ibi Kika: Deuteronomi 2:1-3', 'Ninu iwe Joeli, Olorun seleri lati da gbogbo odun t iota ti baje pada ninu aye re. Ni gbogbo ibi ti o ti ni ijakunle tabi ofo, Olorun ti seleri lati fun o ni idapada lekunrere.', 'ADURA:\r\n*Gbogbo odun rere ti ota ti baje ninu aye mi, mo gbaa pada ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 1Awon Oba 9-10', '2023-04-18'),
(179, '2023-06-25', '25,SUNDAY JUN', 'ILERA PIPE', 'Ibi Kika: 3John 1:2', 'Okan ninu ife Olorun fun wani lati ni iwosan ninu ago ara wa ati ilera pipe. Bi eyi ko ba n se ife Olorun fun wa, ko bani so fun wa nipase Johannu Olufe. Pelu oye yi, ti o ko b ani ilera pipe, lo ase re ninu Kristi ki o si gba ilera pipe re.', 'ADURA:\r\n*Gbogbo oro odi ti a ran simi pada si bi to ti n bo loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 1 Awon Oba 11-12', '2023-04-18'),
(180, '2023-06-26', '26, MONDAY JUN', 'ORO ODI KI YIO DURO', 'Ibi Kika: Ekun Jeremiah 3:37', 'Gbogbo oro odi ti ko ti odo Olorun wa, ki yio duro. Nigba miran, a ma so oro odi si ara wa nitori ohun ti a n la koja. Mo gbadura pe gbogbo oro odi ti a so lodi si o ki yio duro ni oruko Jesu.', 'ADURA:\r\n*Gbogbo oro odi ti a ran simi pada si bi to ti nbo loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 1 Awon Oba 13-14', '2023-04-18'),
(181, '2023-06-27', '27, TUESDAY JUN', 'IFAMIORORO YAN OBA', 'Ibi Kika: 1Samueli 16:12-13', 'Dafidi eniyan lasan di eniyan pataki nipase ifamiororo yan oba. Ifamiroro yan lati odo Olorun ma n mu ki eniyan le se ohun ti eniyan lasan ko le se. lehin igba ti Samueli fi ororo yan Dafidi, Emi Olorun woo ni agbara lati se ise ti a gbe le lowo. O nilo ifamiororo yan oba lati se ise akin ti o tayo ju enikeni ninu idile re.', 'ADURA:\r\n*Mo gba ifamiororo yan oba fun ogo ti o tayo ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 1 Awon Oba 15-16', '2023-04-18'),
(182, '2023-06-28', '28, WEDNESDAY JUN', 'ERONGBA IBI YIO PARUN', 'Ibi Kika: 2Chronicles 32:2', 'Erongba ibi sennakeribu fun Jerusalemu ni lati paarun. Sugbon Olorun ba imo won je. Niwon igba ti a si wa ninu aye yi, awon eniyan wa ti o ni ero ibi si wa. Sugbon imo ibi won ki yio se.', 'ADURA:\r\n*Gbogbo erongba ibi ota si aye mi, ati idile mi ki yio se ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 1 Awon Oba 17-18', '2023-04-18'),
(183, '2023-06-29', '29, THURSDAY JUN', 'EGBE TI O LODI', 'Ibi Kika: 1Samueli 22:2', 'Dafidi je eni ti o ni ifamiororo yan Olorun lori re, sugbon o wa laarin awon egbe ti o lodi. Ogo ni fun Olorun ti o fun ni ogbon lati ba won lo.', 'ADURA:\r\n*Olorun yio gba mi laarin awon egbe ti o lodi ti ko le mu ohun rere ba aye mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 1 Awon Oba 19-20', '2023-04-18'),
(184, '2023-06-30', '30, FRIDAY JUN', 'EMI YI O SE OHUN TITUN', 'Ibi Kika: Isaiah 43:19', 'Iwe Isaiah je ko ye wa pe, Olorun yio se ohun titun ninu aye re. Gbagbe nipa ohun ti o n la koja, ki o si tejumo ohun titun ti Olorun yio se ninu aye re.', 'ADURA:\r\n*Olorun yio se ohun titun ninu aye mi loni ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 1 Awon Oba 21-22', '2023-04-18'),
(185, '2023-07-01', '01, SATURDAY JULY', 'ONGBE FUN OHUN TI EMI', 'Ibi Kika: Johannu 4:13-15', 'Emi Mimo ni omi ti emi ti Jesu Kristi n toka si. Awon onigbagbo nilo emi mimo lati fopin si oungbe ti emi won.', 'ADURA:\r\n*Oluwa fi Emi Mimo kun mi lati fopin si ongbe emi mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 2Awon Oba 1-2', '2023-04-18'),
(186, '2023-07-02', '02, SUNDAY JULY', 'OHUN ASIRI', 'Ibi Kika: Deuteronomi 29:27-29', 'Gbogbo ohun ikoko aiye yi je ti Olorun. Awon asiri ti o jinle wa nipa ayanmo wa ti a ko mo nipa re, paapaa nipa awon idile wa. Nitorinaa, awon ohun aramada n sele ninu aye wa, o le dara tabi buru, sugbon si wa, ko se salaye. E je ki a lo sodo Olorun, eniti o je olufihan gbogbo awon asiri lati fi won han fun wa.', 'ADURA:\r\n*Oluwa tu gbogbo nkan ti o farasin nipa aye mi han fun mi, ki o sit u erongba awon ota mi ni oruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: 2 Awon Oba 3-4', '2023-04-18'),
(187, '2023-07-03', '03, MONDAY JULY', 'OYE OLORUN', 'Ibi Kika: Danieli 9:1-5', 'Nipase oye oniwa-bi-Olorun, Danieli mo lati inu iwe ti o ka pe won yoo lo aadorin odun ninu igbekun. Ti o ko ba ni oye ti Olorun nipa apere aye re ati ayanmo, esu yoo yaneje yoo si tun tan o je nipase eto re ti o ga julo ti o ni ninu Kristi Jesu.', 'ADURA:\r\n*Oluwa fun mi ni oye re lati mo ohun ti o to lati se ni akoko ti o to ni oruko Jesu Kristi. Amin.\r\nBIBELI KIKA/OJOJUMO: 2Awon Oba 5-6', '2023-04-18'),
(188, '2023-07-04', '04, TUESDAY JULY', 'ITE ANU', 'Ibi Kika: Eksodu 37: 4-6', 'Ijoko aanu toka si pe aanu ati oore-ofe siji bo ofin. Awon ipo kan wa ti a wa ninu re, ogbon, imo tabi agbara wa ko le mu wa jade ninu re, sugbon nipase ANU nikan. Fun gbogbo ohun igbesan ti o nfo si o, aanu Olorun yoo pa won lenu mo loruko Jesu.', 'ADURA:\r\n*Olorun, joko sori ibujoko aanu nitori oro mi ki o si saanu fun mi ni oruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: 2Awon oba 7-8', '2023-04-18'),
(189, '2023-07-05', '05, WEDNESDAY JULY', 'AWOSANMO OGO', 'Ibi Kika: Eksodu 40:34-38', 'Nigbakugba ti Olorun ba sabewo si ago ti awon omo Israeli ko, awosanma yoo bo gbogbo ibe, eyi fihan iwaalaye Olorun. A wa ni akoko ti Emi Mimo ati pe ara wa si ni tempili Re.', 'ADURA:\r\n*Baba, emi ni tempili re, be mi ati gbogbo ise mi wo pelu awosanma ogo ni oruko Jesu Kristi.\r\nBIBELIKIKA/OJOJUMO: 2Awon Oba 9-10', '2023-04-18'),
(190, '2023-07-06', '06, THURSDAY JULY', 'IBUKUN BABA', 'Ibi Kika: Lefitiku 9:22-24', 'Mose ati Aaroni sure fun awon omo Israeli leyin ti won ti gba ore lowo won. Gege bi omo Olorun, nigbakigba ti a ba mu awon ebun wa wa si iwaju Olorun, ibukun kan wa ti Olorun tu sori wa. Awon ibukun wonyi le wa bi adura taara lati odo Olorun tabi lati odo awon iranse Re.', 'ADURA:\r\n*Oluwa je ki adura re nipase iranse re sise ninu aye mi ni oruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: 2Awon Oba 11-13', '2023-04-18'),
(191, '2023-07-07', '07, FRIDAY JULY', 'INA AJEJI', 'Ibi Kika: Lefitiku 10:1-3', 'Awon omo Aroni mejeeji gbe ina ajeji lo si ile Olorun, ina Olorun si jo won run. Ko si tempili ti ara ni bayi nibiti a ti n sun turari bi ti majemu atijo, eyi je nitori pe ara wa ni tempili Olorun. Nje a ko gba ifekufe, igberaga, oti-waini tabi gbogbo ohun ti ko to sinu ara wa bi? Iwonyi ni a tun pe ni ina ajeji, ati pe o le pa wa run niwaju Olorun.', 'ADURA:\r\n*Mo lo ina Emi Mimo lati pa gbogbo ina ajeji run ni aye mi ati idile mi ni oruko Jesu Kristi.\r\nBIBELI KIKA/OJOJUMO: 2Awon Oba 14-15', '2023-04-18'),
(192, '2023-07-08', '08, SATURDAY JULY', 'DI TITOBI SI', 'Ibi Kika: Johannu 14:12', 'Lati tobi tumo si lati faagun tabi dagba. Jesu so fun awon omo eyin re pe oun ni opo nnkan lati sofun won, sugbon eya ara won ko le gba won. Bawo ni eyi se tumosi aye wa, opolopo awon ohun ni Olorun fe lati fi han wa, ibi ogo tuntun wa ti Olorun fe lati mu wa lo, sugbon eya alailagbara ti won wa ko le de ibe tabi se ise  iyansile yen. Nitori naa, a ni lati je ki agbara wa po si lati ni opolopo Olorun nipa gbigbadura, kÃ­kekoo oro re ati ki a ma se gba eran ara laaye.', 'BIBELI KIKA/OJOJUMO: 2 Awon Oba 16-17', '2023-04-18'),
(193, '2023-07-09', '09, SUNDAY JULY', 'KINI MO LE SE FUN O?', 'Ibi Kika: Marku 10:46-51', 'Batimeu afoju mo ohun to fe ki Jesu se fun oun gan-an, tori naa, oju ese lo fesi nigba ti Jesu bi i leere. Ibeere kan naa ni Jesu n bi wa lonii pe, Ki ni emi yoo se fun o? â€™â€™', 'ADURA:\r\n*Oluwa, ba ini mi pade bi o ti ba ti Batimeu pade ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 2Awon Oba 18-19', '2023-04-18'),
(194, '2023-07-10', '10, MONDAY JULY', 'EGUN YI SI IBUKUN', 'Ibi Kika: Numeri 23:4-11', 'Balaki ran Balaamu lati ran an lowo lati fi awon omo Israeli bu, sugbon Olorun yi egun naa pada si ibukun. Awon to nse ilara wa nwa iranlowo lodo awon eni buruku lati pa wa lara. Niwon igba ti ona wa ba dun mo Oluwa, ihale won ko le se ohunkohun ti o lodi si wa, dipo Oun yoo je ki awon ota wa ni alafia pelu wa.', 'ADURA:\r\n*Mo so gbogbo egun awon ota di ibukun sinu aye mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 2Awon Oba 20-21', '2023-04-18'),
(195, '2023-07-11', '11, TUESDAY JULY', 'ANGELI LOJU ISE', 'Ibi Kika: Numeri 22:24-31', 'Balaki ran Balaamu lati ran an lowo lati fi awon omo Israeli bu, sugbon Olorun yi egun naa pada si ibukun. Awon to nse ilara wa nwa iranlowo lodo awon eni buruku lati pa wa lara. Niwon igba ti ona wa ba dun mo Oluwa, ihale won ko le se ohunkohun ti o lodi si wa, dipo Oun yoo je ki awon ota wa ni alafia pelu wa.', 'ADURA:\r\n*Gbogbo asoju okunkun ti o bere irin ajo si mi yoo pade angeli iparun ni ona lati pa won run ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 2Awon Oba 22-23', '2023-04-18'),
(196, '2023-07-12', '12, WEDNESDAY JULY', 'AGBARA LATI NI', 'Ibi Kika: Deuteronomi 2:23-24', 'Mose so fun awon mo Israeli pe Olorun ti fun won ni ile Kenani lati ni, sugbon won ni lati ba won jagun. Awon ota wa ni enu-bode awon ohun-ini re (igbeyawo, ti emi, ti ara tabi ilera), ti yoo se idiwo fun o nigbagbogbo lati ni awon ohun-ini ti Olorun re fifun o, o nilo lati bawon ja, lati yo won kuro ni ona re.', 'ADURA:\r\n*Oluwa fun ni agbara ati oore-ofe pataki lati ja ati gba ohun-ini mi ni oruko Jesu.', '2023-04-19'),
(197, '2023-07-13', '13, THURSDAY JULY', 'ISE IYANU TI KO SE GBAGBO', 'Ibi Kika: Josua 5:1', 'Nigba ti gbogbo awon ota Josua ati awon omo Israeli ri bi odo Jodani ti gbe ki awon omo Israeli le soda, eru ba won. Eyi je ise iyanu ti o se pataki, o si je ki gbogbo awon ota won mo pe nitooto, Olorun alaaye wa pelu awon omo Israeli, O si le se ohun gbogbo. Nigbati Olorun ba se ise iyanu pataki kan ninu aye re, yoo pa awon ota re lenu mo, yoo je ki won teriba fun o.', 'ADURA:\r\n*Oluwa, iseyanu ti yoo mu ki awon ota mi wa ninu iberu nla je ki o sele ninu aye mi ni oruko Jesu.\r\n BIBELI KIKA/OJOJUMO: 1Kronika 1-4', '2023-04-19'),
(198, '2023-07-14', '14, FRIDAY JULY', 'OPIN LORI EGAN', 'Ibi Kika: Josua 5:7-9', 'Olorun ti yi egan ti o wa lara awon omoIsraeli kuro nipa ikola. Ni gbogbogbo, ikola tumo si, gige kuro tabi yiyo ohun alaimo kuro. Awon eru egan kan wa ti o so mo aye wa, eyi le je aini ojurere tabi awon oluranlowo ayanmo, titi Olorun yoo fi mu won lo, a ko le ni ominira laelae.', 'ADURA:\r\n*Mo mu gbogbo aso egan kuro ninu aye mi ni oruko Jesu Kristi.\r\nBIBELI KIKA/OJOJUMO: 1Kronika 5-8', '2023-04-19'),
(199, '2023-07-15', '15, SATURDAY JULY', 'EBENESERI', 'Ibi Kiika: 1Samueli 7:9-12', 'Itumo oro Ebeneseri niyi, bi Olorun ti ran wa lowo. 	Oro yii gbajumo ninu aye awon omo Israeli. O le se bakan naa fun e. Ni ikorita egan tabi itiju, Olorun yoo fi ara re han bi Ebeneseri ni oruko Jesu.', 'ADURA:\r\n*Oluwa se iyanu pataki kan ti yoo di enu awon ota mi, ti yoo si fi Olorun han bi Ebenesari mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 1Kronika 9-11', '2023-04-19'),
(200, '2023-07-16', '16, SUNDAY JULY', 'OWO OLORUN', 'Ibi Kika: 1Awon Oba 18:46', 'Oro ti owo Olorun n toka si agbara Oluwa ni ona iyanu. Owo Olorun si ba le Elijah, o si koja keke Ahabu. Okan ninu opolopo awon ohun ti owo Olorun se ninu aye wa ni pe o funni ni iyara. Pupo ninu awon elegbe re le ti lo siwaju re tabi saseyori ni ise-iranse, okowo tabi ibi ise re. Mase yo ara re lenu, owo Olorun yoo wa sori re yoo fun o ni iyara lati ba won ati paapaa lati ju won lo.', 'ADURA:\r\n*Baba, jeki owo re bale mi ki o si fun mi ni iyara ninu okowo, ise ati ise iranse mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 1Kronika 12-15', '2023-04-19'),
(201, '2023-07-17', '17, MONDAY JULY', 'ISOWEDOWO TO SII', 'Ibi Kika: 1Awon Oba 3:5-10', 'Solomoni Oba ru ebo nila si Oluwa ni Gibeoni Olorun si so fun un pe ki o beere ohun ti o fee. Olorun fun o ni isowedowo to si ni owuro yi, ati pe idaniloju wa pe, ibeere yen yoo gba.', 'ADURA:\r\n*Olorun ohun ti Emi yoo se ti yoo je ki o fun mi ni sowedowo sisii, Mo gba oore-ofe nla yen ni oruko Jesu Kristi.\r\nBIBELI KIKA/OJOJUMO: 1Kronika 16-20', '2023-04-19'),
(202, '2023-07-18', '18, TUESDAY JULY', 'ISELE TI KO WOPO', 'Ibi Kika: Genesisi 50:15-21', 'Awon arakunrin Josefu ta a gege bi eru fun awon ara Midiani, ti won si ta a si ile Potifari ni Ejipti. Won o mo pe awon isele to sajeji wonyi yoo mu Josefu je eni keji ni ipo ase ni Ejipti ati pe yoo gba aye la lowo iyan. Olorun mo ohun to n sele si e nisinsinyin, o le ri bi awon ota re se n da o loro. Sugbon, lojiji, Oun yio fi ara han gege bi Olorun re yoo si lo awon ipenija wonyen lati mu o wa sinu ogo nla bi O ti se fun Josefu.', 'ADURA:\r\n*Kini yo sele ti yoo mu mi de afin ogo, Oluwa, faye gba won loruko Jesu alagbara.\r\nBIBELI KIKA/OJOJUMO: 1Kronika 21-24', '2023-04-19'),
(203, '2023-07-19', '19, WEDNESDAY JULY', 'ISEGUN LORI EMI ALAYANLE', 'Ibi Kika: 1Kronika 21:1-7', 'Satani doju ija ko Dafidi Oba la\r\nti se ohun ti  ko to si Olorun, inu Olorun si baje. Satani, nipase emi alayanle le ru awon omo Olorun soke lati se lodÃ¬ si ife Olorun fun emi won, eyi si le ti won sinu iparun.', 'ADURA:\r\n*Oluwa fun mi ni isegun lapapo lori emi alayanle lati inu ijoba okunkun ti o fe pa emi mi run ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 1Kronika25-29', '2023-04-19'),
(204, '2023-07-20', '20, THURSDAY JULY', 'ITUSILE KURO LOWO OHUN TI AYE', 'Ibi Kika: 1Awon Oba 11:1-4', 'Solomoni je eni rere ti o nifee Olorun ni kutukutu igbesi aye re, sugbon ninu irin ajo aye re, o feran opolopo obinrin ajeji, awon obinrin ajeji wonyi mu ki o pada seyin sÃ­ titele awon olorun miiran. Nje ife wa fun Oluwa tun di tutu bi? Nje a ti gbagbe ife akoko wa bi? Se owo, oro ati okiki ko gba ife re ninu okan wa bi? Sayewo aye re loni ki o si ronupiwada.', 'ADURA:\r\n*Olorun, ya mi kuro ninu ohun aye ti o fe ya mi kuro ninu re loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 2Kronika 1-4', '2023-04-19'),
(205, '2023-07-21', '21, FRIDAY JULY', 'MAJEMU IYO', 'Ibi Kika: 2Kronika 13:1-5', 'Olorun ba Dafidi da majemu iyo, ko si si eni ti o le yipada. Iyo je ohun elo ile ti o se pataki pupo. O see lo lati pa aarun, toju, tabi dun awon nkan ni pataki ounje. Iyo wa ni ibi gbogbo o si je alaimalelo. Majemu ti Olorun ba Dafidi da tumo si pe ila idile re a pamo titi lae.', 'ADURA:\r\n*Ohun ti emi yoo se ti yoo mu Olorun ba mi da majemu nla, Mo gba oore-ofe yen lati se ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 2Kronika 5-8', '2023-04-19'),
(206, '2023-07-22', '22, SATURDAY JULY', 'AWON ENIYAN TO SE PATAKI', 'Ibi Kika: Iwe Owe 13:20', 'Iwe-mimo je ki a loye pe, ti o ba rin pelu awon ologbon tabi awon eniyan ti o se pataki yoo se afihan ninu igbesi aye ráº¹ daadaa. Eyi je nitori pe iwa won ati igbesi aye won yoo han ninu aye re, ati pe iranlowo yoo dide fun o.', 'ADURA:\r\n*Baba, dide loni ki o so mi po mo awon eniyan ti o se pataki si ayanmo mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 2Kronika 9-12\r\nMO: 2Kronika 5-8', '2023-04-19'),
(207, '2023-07-23', '23, SUNDAY JULY', 'EBUN TO TO', 'Ibi Kika: Oniwasu 9:9', 'Ebun ti o dara julo tabi eto fun enikan lati ni ni igbesi aye ni oko ati iyawo ti o to. Okan ninu awon ikuna ti o buru julo ti eniyan le ni ninu igbesi aye re ni ikuna igbeyawo. Nigbati okunrin kan ba kuna ni abala igbeyawo, o ti kuna ninu ohun gbogbo. Sugbon, nigba ti o ba ni en to to ni igbeyawo, o ti fere yanju gbogbo awon isoro ti o le ni ni aye re.', 'ADURA:\r\n*Oluwa, se oko tabi aya mi ni ebun ti o dara ju fun mi.\r\n *Fun gbogbo awon alai loko tabi aya, tin won fe oko tabi aya tiwon, Olorun da won loun loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 2Korinka 13-17', '2023-04-19'),
(208, '2023-07-24', '24, MONDAY JULY', 'IBANUJE PARI', 'Ibi Kika: Isaiah 25:7-9', 'Nigba ti Olorun ba seleri O gbe iwa otito re le oro re lati ri i daju pe o se. Lati inu oro re, Olorun ti se ileri lati fi opin si ibanuje ninu igbesi aye wa ati lati nu gbogbo omije wa nu, wa si odo re pelu awon eru re gbogbo, sii gbagbo, iwo yoo rii pe ibanuje yen yio pare.', 'ADURA:\r\n*Oluwa ninu ola re gbe iku mi ninu isegun ninu idile mi ki o nu omije mi nu loruko Jesu alagbara.\r\nBIBELI KIKA/OJOJUMO: 2Kronika 18-21', '2023-04-19'),
(209, '2023-07-25', '25, TUESDAY JULY', 'MO IDI RE', 'Ibi Kika: Luku 4:18-19', 'Fun gbogbo okunrin ti obinrin bi, idi kan wa ti a fi ran waa si ile aye. Idi je ifokansi ti a pinnu lati saseyori ohun kan tabi ifokansi kan. Okan lara idi Jesu lori ile aye ni lati fopin si gbogbo ise okunkun. Imoran re fun gbogbo eniyan lati sawari idi re ni igbesi aye re ni kutukutu, lati le se afoju sun re, ki a le mu abajade ti Olorun nfe. Eyi je nitori awon ayanmo ni a so mo idi yen, dide re ni dide won, ati isubu re ni isubu won.', 'ADURA:\r\n*Ore-ofe lati sawari idi mi ati lati rin ni kikun re, Mo gba ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 2Kronika 22-25', '2023-04-19'),
(210, '2023-07-26', '26, WEDNESDAY JULY', 'JE OMO-LEHIN ORO OLORUN', 'Ibi Kika: Jeremiah 35:13-19', 'Awon omo Rekabu tele gbogbo ilana ti Olorun pa lase fun won lati enu baba won, Olorun si bukun wá»n. Nigbati o ba tele gbogbo awon ilana ti Olorun, bi o ti wu ki idanwo naa ti le, ni ibukun Olorun yoo bukun fun o.', 'ADURA:\r\n*Oluwa, mo gbadura, fun oore-ofe pataki lati tele ilana atorunwa re loruko Jesu Kristi.\r\nBIBELI KIKA/OJOJUMO: 2Kronika 26-29', '2023-04-19'),
(211, '2023-07-27', '27, THURSDAY JULY', 'KO SI AHUN', 'Ibi Kika: Matteu 10:7-10', 'Leyin ti Jesu ti pase fawon omo eyin re pe ki won bere si i se ise ihinrere, o so fun won pe ki won mu won lara da, ki won fo awon adete mo, ki won le awon emi esu jade, ki won ma si ma gba owo fun ohun ti won se. Olorun ti fi wa si ipo oloro ki a ba le ran awon elomiran lowo. A ko nilo lati se ahun, Olorun fi fun wa ni ofe, o ye ki o bukun awon elomiran ni ofe lai reti lati gba ohunkohun ni ipadabo.', 'ADURA:\r\n*Gbogbo emi agara ni aye mi, jade ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 2Kronika 30-33', '2023-04-19'),
(212, '2023-07-28', '28, FRIDAY JULY', 'INA EMI MIMO', 'Ibi Kika: Ise Awon Aposteli 2:1-4', 'Ni ojo Pentikosi, Emi Mimo farahan bi ina, gbogbo awon omo-eyin si gba ahon ina. Eyi so won di eru si ijoba okunkun. Nigbati o ba ni ina Emi Mimo, o wa ni aifowokan fun okunkun ati awon egbe re.', 'ADURA:\r\n*Baba je ki ina Emi Mimo gbemi wo nisinyi loruko Jesu Kristi.\r\nBIBELIKIKA/OJOJUMO: 2Kronika 34-36', '2023-04-19'),
(213, '2023-07-29', '29, SATURDAY JULY', 'AMI ADETE', 'Ibi Kika: 2Awon Oba 5:1-5', 'Jagunjagun nla ni Naamani ni Siria sugbon o je adete, iru itiju wo ni. Nigba ti won ka iwe NaamanÃ¬, o dara gan-an, amo won menu kan sugbon kan, adete ni. Awon eniyan so ohun ti o dara nipa re, sibesibe, nigbagbogbo \'\'sugbon\'\' kan wa  ti a menuba.â€œSugbonâ€™â€™â€ tire le je aisan osese, aini owo, idaduro, ibanuje ati bebe lo, ati pe eyi le fi ami kan sile ninu aye re. Sugbon awa n sin Olorun alaaye, eni ti o le mu aye yen kuro, ki o si nu kuro nipase eje re iyebiye.', 'ADURA\r\n* Oluwa dide ki o fopin si gbogbo ami odi bi adete ni igbesi aye mi ni oruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: 1Tessalonika 1-5', '2023-04-19'),
(214, '2023-07-30', '30, SUNDAY JULY', 'AGBARA NINU ORO RE', 'Ibi Kika: Johannu 1:1-5', 'Oro Olorun kun fun agbara lati se igbala, bukun, ati lati mu imuse ati aseyori wa. Bibeli so pe, nibi ti oro oba ba wa, agbara wa nibe. Jesu ni oba awon oba, ati nibiti oro re ba wa, nibe ni ifihan agbara re. Gbagbo ninu Oro Olorun, agbara oro naa yoo han ninu aye re.', 'ADURA:\r\n*Oluwa je ki agbara ti o wa ninu oro re se iyanu ni aye ati idile mi ni oruko Jesu Kristi.\r\nBIBELI KIKA/OJOJUMO: 2Tesalonika 1-3', '2023-04-19'),
(215, '2023-07-31', '31, MONDAY JULY', 'SORA FUN IGBERAGA', 'Ibi Kika: Iwe Owe 11:2', 'Igberaga je ise ti nini tabi fifihan iyi ara-eni ti o ga ju tabi fifi awon ilosiwaju eniyan han. Ipari pari re je itiju nigbagbogbo. Igberaga yara pa eniyan ju aisan jejere. Igberaga ni Satani fi padanu ipo re ni orun. Lonakona ti o ba bere lati ni imo igberaga ninu aye re, jewo, ki o ronupiwada ni kiakia, ki o ma ba le padanu ipo re niwaju Olorun ati paapaa niwaju eniyan.', 'ADURA:\r\n*Oluwa, pa gbogbo eyikeyi igberaga ninu aye mi, kin ma ba pari sinu iparun ni oruko Jesu.\r\nBÃBÃ‰LÃŒ KIKA/OJOJUMO: 1Timoti 1-6', '2023-04-19'),
(216, '2023-08-01', '01, TUESDAY AUGUST', 'ADURA ISOTELE', 'Ibi Kika: Jobu 42:7-10', 'Pelu gbogbo ohun ti awon ore Jobu se si i. Olorun so fun pe ki o gbadura fun awon ore re, adura asotele yii sise fun won ati fun oun pelu. Leyin ti Jobu ti gbadura lona asotele fun awon ore re, Olorun bu kun un o si yi igbekun re pada.', 'ADURA:\r\n*Mo gba adura asotele ti yoo yi aye mi pada ni oruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: 2Timoti 1-4', '2023-04-19'),
(217, '2023-08-02', '02, WEDNESDAY AUGUST', 'OHUN IJA BUBURU PADA SODO ENI TO RANSE', 'Ibi Kika: Orin Dafidi 7:1-13', 'Gbogbo ibi ti awon ota se si Dafidi pada sodo eni to ranse nipase agbara Olorun. Dafidi ko saamu yii nigba ti Saulu n lepa emi re, sugbon Olorun gba a la, o si gba a. Gbogbo ohun ija awon ota re yoo pada si won loruko Jesu.', 'ADURA:\r\n*Gbogbo ohun ija ti a fi ranse ni ijoba okunkun, pada sori eni ta fi ranse loruko Jesu Kristi.\r\nBIBELI KIKA/OJOJUMO: Esra 1-3', '2023-04-19'),
(218, '2023-08-03', '03, THURSDAY AUGUST', 'AABO OLOHUN', 'Ibi Kika: Orin Dafidi 91:1-16', 'Aabo kansoso ti eniyan nilo ni aabo Olorun. Nitorinaa opolopo eniyan beru won si gba aabo labe ogun tabi aabo eniyan. Sugbon fun opolopo awon ti n wa labe ojiji Olodumare ni a o bo lowo gbogbo idekun awon ota.', 'ADURA:\r\n* Mo gbadura fun aabo Olorun lori emi ati idile mi ni oruko Jesu Kristi.\r\nBIBELI KIKA/ OJOJUMO: Esra 4-6', '2023-04-19'),
(219, '2023-08-04', '04, FRIDAY AUGUST', 'OKAN TO MOORE', 'Ibi Kika: Orin Dafidi 92:1-5', 'O se pataki fun awon onigbagbo lati fi okan imoore han lori ohun ti Olorun ti se fun wa. Imoore je ipo ti idupe, imolara ope si oninuure kan. Olorun ni oninure wa. O ti je olooto si wa ni gbogbo ona. E je ka tun fi imoore wa han si, ninu yiyin tabi fifunni ni ope idupe.', 'ADURA:\r\n*Mo gba okan lati fi imoore han si Olorun ni oruko Jesu Kristi.\r\nBIBELI KIKA/OJOJUMO: Esra 7-10', '2023-04-19'),
(220, '2023-08-05', '05, SATURDAY AUGUST', 'IBERU OLORUN', 'Ibi Kika: Iwe Owe 9:10', 'Lati ni iberu Olorun ninu okan je ona lati bu ola ati lati ni ogbon Olorun. Iberu nibi ko tumo si lati beru tabi gbon jeje, sugbon o tumo si ibowo si Olorun. Ta a ba ni iberu Olorun lokan, o maa n fun wa ni ogbon, a si mu emi wa gun sii.', 'ADURA:\r\n*Oluwa, fun mi ni oore-ofe pataki lati beru re ati lati gbe ninu ogbon re ni oruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: Nehemiah 1-4', '2023-04-19'),
(221, '2023-08-06', '06, SUNDAY AUGUST', 'ASIKO OLORUN TO DAAJU', 'Ibi Kika: Genesisi 18:9-14', 'Olorun wa nsise ni akoko ti o to. O be Sara wo ni akoko ti o to, o si bukun re pelu omokunrin kan. E ma sare siwaju Olorun, duro de, O si ran awon angeli, O si mu ileri se. Oun kii se okunrin ti o puro. Ni akoko tire oun yio gbe o soke. Duro de.', 'ADURA:\r\n*Akoko Olorun to ye fun aye mi yoo de nitoto ni oruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: Nehemiah 5-7', '2023-04-19'),
(222, '2023-08-07', '07, MONDAY AUGUST', 'AGBARA FUN ORO', 'Ibi Kika: Deuteronomi 8:18-20', 'Ko si eniti o ni agbara lati ni oro biko se Olorun Olodumare. Olorun lo nfunni ni agbara lati ni oro, nitorina jije oloro kii se ese, ipinnu Olorun ni fun wa. Owo je ohun eyei ati pe o je okan ninu awon ikan ti o le sise bi orisun ibukun si awon miiran.', 'ADURA:\r\n*Oluwa, mo gba agbara nla lati ni agbara Olorun fun oro ni oruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: Nehemiah 8-10', '2023-04-19'),
(224, '2023-08-09', '09, WEDNESDAY AUGUST', 'ADURA HANNA', 'Ibi Kika: 1Samueli 2:1-10', 'Adura ti Hanna gba yipada si eri ti o kan gbogbo iran lori ile aye. Nipase adura re, a bi wolii kan, eni ti o je eniyan Olorun tin fi ororo yan awon oba ti o sÃ¬ tun mu awon eniyan Israeli pada wa sodo Olorun. Se apejuwe adura re si Olorun, Mo si rii pe o dahun won ni oruko Jesu.', 'ADURA:\r\n*Oluwa, je ki adura mi pase eri nla ni oruko Jesu Kristi.\r\nBIBELI KIKA/OJOJUMO: Esteri 1-5', '2023-04-19'),
(225, '2023-08-10', '10, THURSDAY AUGUST', 'LETA ASE', 'Ibi Kika: Esteri 8:5-8', 'Nigba ti awon omo Juu wa labe inilara Hamani. A ko leta ti ase pelu oruka oba fun ominira awon Juu. Iwe ase le gbe okunrin kan si ipo ti o dara, o le tun tu itan ti elomiran ko, bakanna o tun le yi gbolohun iku pada. Gba leta igbega re.', 'ADURA:\r\n*Mo gbadura, a o ko iwe ase fun mi lati pa awon ota mi run ati gba ogo ti o sonu pada ni oruko Jesu Kristi.\r\nBIBELI KIKA/OJOJUMO: Esteri 6-10', '2023-04-19'),
(226, '2023-08-11', '11, FRIDAY AUGUST', 'OWO INA', 'Ibi Kika: Heberu 1:5-7', 'Eto Olorun dara atipe iyanu ni. O da awon angeli ni emi nigba ti awon iranse re si je owo ina. Awá»n iranse Olorun ko toka si awon eniyan Olorun nikan, sugbon gbogbo awon ti a pe ni oruko Kristi. Nitori naa, gege bi iranse Olorun, a gbodo kun fun itara ati ifororoyan Olorun eyi ti ko si ohun ti o le duro lodi si awon alakooso ati alagbara.', 'ADURA:\r\n*Olorun se emi ati idile mi ni ina ni oruko Jesu Kristi.\r\nBIBELI KIKA/OJOJUMO: Titu 1-3', '2023-04-19'),
(227, '2023-08-12', '12, SATURDAY AUGUST', 'ISEGUN LEYIN IDANWO', 'Ibi Kika: Luku 4:1-14', 'Satani dan Jesu wo ninu aginju, amo o segun Satani ati idanwo re patapata. Olorun ki danwo bi ti satani. Ti satani ba le dan Jesu wo, tani awa naa bii? A nilo lati ni idaniloju pe isegun wa wa ninu Kristi Jesu.', 'ADURA:\r\n*Oluwa, fun mi ni isegun patapata lori Satani ati idanwo re loruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: Filemoni', '2023-04-19'),
(228, '2023-08-13', '13, SUNDAY AUGUST', 'ASIRI SI ASEYORI', 'Ibi Kika: Isaiah 48:16-19', 'Ohunkohun ti o ba fe lati di ni aye, o nilo atileyin Olorun lati fi awon asiri ti o farapamo han o. Ife Olorun ni ki awon omo re ma se di otosi lae. Lati mo bi o se le se aseyori ni igbesi aye, lo ki o ko eko ni ese oluwa (Jesu Kristi) ati nipase awon eniyan oniwa-bi-Olorun ati olokiki ti o gba ileri Olorun nipa igbagbo ati suru.', 'ADURA:\r\n*Baba fi asiri aseyori laye han mi ni oruko nla Jesu Kristi.\r\nBIBELI KIKA/OJOJUMO: Jobu 1-5', '2023-04-19'),
(229, '2023-08-14', '14, MONDAY AUGUST', 'OHUN META SI IMUSE', 'Ibi Kika: Iwe Owe 4:1-7', 'Imuse je itelorun ti o jeyo lati mimo pe o gbe igbesi aye re ni imunadoko, sise awon ipinnu Olorun ati jije ibukun fun eda eniyan. Awon ohun meta ti o se iranlowo fun eniyan lati ni imuse ni igbesi aye ni imo pipe, ogbon ati oye Olorun.', 'ADURA:\r\n*Baba, mo gba awon ohun meta yii fun imuse mi ni oruko Jesu iyebiye.\r\nBIBELI KIKA/OJOJUMO: Jobu 6-10', '2023-04-19'),
(230, '2023-08-15', '15, TUESDAY AUGUST', 'GBAGBE NIPA OHUN ATIJO', 'Ibi Kika: Isaiah 43:14-18', 'Olorun n so fun emi ati eyin pe ki a dekun iranti awon ohun atijo ti ko gbe Olorun ga tabi gbe ogo re jade ninu aye wa. Nigbati o ko ba je ki ohun atijo lo, ohun titun ko le wa. Mase wo awon ikuna atijo re, awon asise ti o ti koja tabi awon ipinnu ti o ti se, nireti, maa wo iwaju ki o le rii ogo ati ohun tuntun ti Olorun ti pese sile fun o.', 'ADURA:\r\n*Mo gba oore-ofe pataki lati gbagbe awon nkan isaaju, mo gbe oju mi si awon ohun tuntun ti Olorun fe lati se ninu aye mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Jobu 11-15', '2023-04-19'),
(231, '2023-08-16', '16, WEDNESDAY AUGUST', 'IHAMORA OLORUN', 'Ibi Kika: Efesu 6:10-14', 'A wa ninu aye ija, asi ja pelu ijoba okunkun. Lati bori, a nilo lati gbe gbogbo ihamora Olorun wo, ki a mase di eni ide fun ijoba okunkun.', 'ADURA:\r\n*Oluwa fi gbogbo ihamora ododo bo mi, ki o si mu mi segun loruko Jesu Kristi.\r\nBIBELI KIKA/OJOJUMO: Jobu 16-20', '2023-04-19'),
(232, '2023-08-17', '17, THURSDAY AUGUST', 'AMI KRISTI', 'Ibi Kika: Galatia 6:15-18', 'Aami Kristi ni a se fun gbogbo eniyan ti o gba Jesu Kristi sinu aye won. Eyi mu ki won je eni ayan soto lati di ije fun awon eni buruku. Awon esu ati awon omo egbe re loye ami yii nigba ti won ba ri, nitorina o je ki awon onigbagbo je alailefowokan.', 'ADURA:\r\n*Mo gba ami ti Kristi lati gbe ju gbogbo awon idojuko esu ni oruko Jesu Kristi.\r\nBIBELI KIKA/OJOJUMO: Jobu 21-22', '2023-04-19'),
(233, '2023-08-18', '18, FRIDAY AUGUST', 'BERE NI ORUKO MI', 'Ibi Kika: Johannu 16:20-24', 'Gbogbo onigbagbo ni kikun, ofin, irapada eje ti a ra ni eto lati lo ni oruko Jesu, kii se gege bi irisi igberaga, sugbon gegebi ona ti ebe, lati mu ibeere wa lo si odo re. Oruko Jesu Kristi kun fun agbara ko si ohun ti e bere loruko Jesu ti ko ni se fun o.', 'ADURA:\r\n*Oruko iyebiye Jesu Kristi yoo si awon ilekun si imuse fun mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Jobu 23-30', '2023-04-19'),
(234, '2023-08-19', '19, SATURDAY AUGUST', 'IKU DI AAYE', 'Ibi Kika: Johannu 11: 39-44', 'Lasaru ku, won si sin in, amo Jesu lo sibi iboji re, o si gbe e dÃ¬de. Jesu ni ajinde ati iye ti o le mu gbogbo ipo tabi ohun pada si aye laika bi o ti le run.', 'ADURA:\r\n*Gbogbo ohun ti o ku ni gbogbo ona mi gba iye ni oruko nla ti Jesu Kristi.\r\nBIBELI KIKA/OJOJUMO: Jobu 31-36', '2023-04-19'),
(235, '2023-08-20', '20, SUNDAY AUGUST', 'ORI OKE IYIPADA OLOGO', 'Ibi Kika: Marku 9: 2-7', 'Jesu Kristi mu meta lara awon omo eyin re lo si ori oke iyipada ologo, won si ri awon eniyan ti orun. Iyipada oke je aaye ti a ti yipada ti a si yipada si aworan gidi ti Olorun fe ki a je.', 'ADURA:\r\n*Oluwa mu mi de ipo giga ti emi yio fi jerisi titobi re ninu aye mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Jobu 37-40', '2023-04-19'),
(236, '2023-08-21', '21, MONDAY AUGUST', 'ERE FUN IDAMEWA', 'Ibi Kika: Malaki 3:8-12', 'Idamewa ni mimu idamewa ninu gbogbo owo wa sinu ile Olorun. Eyi je ase lati odo Olohun ati pe enikeni ti o ba gbo, yoo gba ere ti o tele e. O je ilana lati pese fun alufa, iyen ni awon ti n sise ni pepe.', 'ADURA:\r\n*Oluwa mo gbadura fun oore-ofe pataki ti yoo ran mi lowo lati je olooto ninu sisan idamewa mi ati ere ti o tele igboran yi yio je ipin mi loruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: Orin Dafidi 1-10', '2023-04-19'),
(237, '2023-08-22', '22, TUESDAY AUGUST', 'MAJEMU RERE OLORUN', 'Ibi Kika: Orin Dafidi 89:30-34', 'Olorun wa je eni tin o pa majemu mo. Lati pa iro je okan lara awon ohun isipaya ninu awon iwe mimo ti Olorun ko le se. O je mimo patapata ati otito ninu ohun ti O so. Majemu rere ti Olorun da pelu re ki yoo da tabi yi pada laelae. Majemu rere Olorun fun emi re yoo se, duro de e.', 'ADURA:\r\n*Baba, majemu rere re fun idile mi je ki o wa si imuse ni aye mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Orin Dafidi 11-18', '2023-04-19'),
(238, '2023-08-23', '23, WEDNESDAY AUGUST', 'OKAN TIN GBORAN', 'Ibi Kika: Deuteronomi 28:1-13', 'Awon ibukun wa ti o tele gbogbo omo Olorun onigboran. A ko o ninu oro re awon ibukun ti o tele omo Olorun ti o gboran, gba oore-ofe lati je omo igboran ni oruko Jesu.', 'ADURA:\r\n*Oluwa fun mi ni okan lati gboran si o ni gbogbo ojo aye mi ni oruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: Orin Dafidi 19-23', '2023-04-19'),
(239, '2023-08-24', '24, THURSDAY AUGUST', 'ALFA ATI OMEGA', 'Ibi Kika: Ifihan 1:6-8', 'Alfa ati Omega tumo si ibere ati ipari ohun gbogbo. Olorun ni Alfa ati Omega. O mo ibere lati opin ati opin lati ibere.', 'ADURA:\r\n*Olorun, je alfa ati Omega ti aye mi ni oruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: Orin Dafidi 24-30', '2023-04-19');
INSERT INTO `yoruba_devotions` (`id`, `current_day`, `headline`, `topic`, `text`, `text_reading`, `prayer`, `date_uploaded`) VALUES
(240, '2023-08-25', '25, FRIDAY AUGUST', 'IBA ARAENISORO BUBURU', 'Ibi Kika: 1Korinti 15:30-33', 'Egbekegbe eyikeyi ti eniyan ba n pa mo ti ki i se Olorun je ibi, ko si si ohun rere ti o le jade ninu re. Sora awon ore ti o le ba gbogbo ise rere re je, nitori iwa rere le baje nipa egbe buburu.', 'ADURA:\r\nOluwa gba mi lowo gbogbo egbe buburu, ki o so mi po mo awon eniyan ti o se pataki ni oruko Jesu. \r\nBIBELI KIKA/OJOJUMO: Orin Dafidi 31-35', '2023-04-19'),
(241, '2023-08-26', '26, SATURDAY AUGUST', 'AMIN', 'Ibi Kika: Efesu 6:22-24', 'Oro Heberu yii tumo si didurosinsin, nitori naa, o si tun je olooto. O tun tá»ka si báº¹eni yoo ri.', 'ADURA:\r\n*Oluwa je ki Amin re wa lori adura mi loruko Jesu alagbara.\r\nBIBELI KIKA/OJOJUMO: Orin Dafidi 36-38', '2023-04-19'),
(242, '2023-08-08', '08, TUESDAY AUGUST', 'IDASI ATORUNWA', 'Ibi Kika: Josua 10:1-14', 'Onija ni Olorun wa, O si lo agbara nla re lati ba Israeli jagun. Olorun fun Josua ni isegun nipase idasi atorunwa re. Nipa oro ti Josua so, Olorun mu ki oorun ati osupa duro jee titi awon omo Israeliyoo fi bori. Agbegbe wo ni igbesi aye re, se o se idasi si Olohun, daruko won fun Olorun ni bayi.', 'ADURA:\r\n*Owo agbara Olorun yio dide, yio si ja mi laya ni oruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: Nehemiah 11-13', '2023-04-19'),
(243, '2023-08-27', '27, SUNDAY AUGUST', 'OKAN ATI EMI TITUN', 'Ibi Kika: Esekieli 36:24-27', 'Nigba ti Olorun gba awon omo Israeli nide kuro lowo awon ota, O seleri okan ati emi tuntun fun won. O fun won ni okan ti n lepa Olorun, ti o si n rin gege bi ilana re. Si okan re sile fun loni, ki O le mu gbogbo okan okuta kuro ki o si yi pada si okan ara.', 'ADURA:\r\n*Oluwa fun mi ni okan ati emi titun re loruko Jesu Kristi.\r\nBIBELI KIKA/OJOJUMO: Orin Dafidi 39-43', '2023-04-19'),
(244, '2023-08-28', '28, MONDAY AUGUST', 'ANU OLORUN', 'Ibi Kika: Roomu 9:10-16', 'Oro Olorun je ki a ni oye pe, Olorun se aanu si awon eniyan ti o nife. Olohun ni Alase lori aanu Re, O si nfi han enikeni ti o ba fe. Aanu re kii se nipase awon ise eniyan, sugbon o da lori ife Re.', 'ADURA:\r\n*Oluwa so mi di alanfani aanu re loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Orin Dafidi 44-50', '2023-04-19'),
(245, '2023-08-29', '29, TUESDAY AUGUST', 'SETAN LABE ESE RE', 'Ibi Kika: Roomu 16:18-20', 'Iwe Romu wi pe, Satani li ao fi sabe ese wa. A ti mu Satani sabe ese wa, nipase agbara ninu iku ati ajinde Jesu. Woon n fo o si wewe, a segun re patapata, a si n mu un wa si aimale se ohun kohun ninu aye wa.', 'ADURA:\r\n*Ni oruko nla Jesu Kristi, a o fi satani sabe ese mi loruko Jesu Kristi.\r\nBIBELI KIKA/OJOJUMO: Orin Dafidi 51-60', '2023-04-19'),
(246, '2023-08-30', '30, WEDNESDAY AUGUST', 'ITOSONA TI KO TO', 'Ibi Kika: Ruutu 1:1-6', 'Naomi ati gbogbo idile re kuro ni orile-ede won lo si Moabu. O padanu oko re, awon omo meji, o si fi ile Moabu sile lowo ofo. Nigbakugba ti a ba bere si rin irin-ajo ti Olorun ko ni ipa ninu, opin re nigbagbogbo je ajalu.  Ko eko lati wa itosona Olorun saaju ki o to lo si irin-ajo eyikeyi tabi kopa ninu ohunkohun.', 'ADURA:\r\n*Oluwa mase je ki n lo si irin-ajo ti ko to tabi itosona ti ko to ni aye mi ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Orin Dafidi 61-67', '2023-04-19'),
(247, '2023-08-31', '31, THURSDAY AUGUST', 'ORI KII SE IRU', 'Ibi Kika: Deuteronomi 28:12-14', 'Ti o ba tele ilana ati ilana Olorun ni gbogbo ona, iwo yoo je ori, kii se iru. Gbigboran si gbogbo Oro Olorun yoo je ki ipada sehin jinna si wa, ati pe a ko ni ni iriri ohun ti o tumo si lati je iru tabi isale.', 'ADURA:\r\n*Baba Mo ko lati je iru bikose ori ni oruko nla Jesu.\r\nBIBELI KIKA/OJOJUMO: Orin Dafidi 68-71', '2023-04-19'),
(248, '2023-09-01', '01, FRIDAY SEPTEMBER', 'IRAWO TO NTAN', 'Ibi Ibi Kika: Matthew 2:2-10', 'Nigbati a bi Jesu, itan si ogo re lo polongo ibi re, eyi jeki awon Magi (awon amoye) wa lati ibi to jina si odo Jesu, kii se pelu owo ofifi sugbon pelu ebun. Itan si irawo ninu aye eniyan ma nru eniyan soke lati bukun fun o', 'ADURA:\r\n*Baba jeki irawo mi tan fun gbogbo aye lati rii ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Orin Dafidi 72-77', '2023-04-19'),
(249, '2023-09-02', '02, SATURDAY SEPTEMBER', 'ISEGUN LORI APA AYANMO RUN', 'Ibi Kika: 1Samueli 20:25-33', 'Dafidi je omo kekere pelu ayanmo to lagbara, sugbon Saulu pinu lati paarun, sugbon Olorun ko gba laye lati pa Dafidi. Idi eyi ni pe Olorun lo Jonathani lati ran Dafidi lowo de ibi ayanmo re, bi o ti le jepe baba re nwa lati paa. Niwon igba ti o ba w ani iha ti Olorun, ota yio kan gbiyanju ni, sugbon won koni le pa ayanmo re run.', 'ADURA:\r\n*Oluwa gba mi lowo awon apa ayanmo run ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Orin Dafidi 78-79', '2023-04-19'),
(250, '2023-09-03', '03, SUNDAY SEPTEMBER', 'ENI TO TO', 'Ibi Kika: 1Samueli 16:10-13', 'Nigbati Olorun nilo elomiran lati ropo Saulu gege bi oba ni Israeli, Olorun ran woli Samueli si idile Jesse lati fi ifamiororo yan eni to to. Eni to to nani Dafidi. Ni ipokipo ti a ba wa laye, a nilo eni to to kan ninu irin ajo aye wa, boya nipa isuna owo, tie mi, ilera tabi ti ara. A nilo awon eniyan toto kan ki irin ajo aye wa le ya.', 'ADURA: \r\n*Oluwa se mi ni eni toto kin le joko si ibi ogo loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Orin Dafidi 80-84', '2023-04-19'),
(251, '2023-09-04', '04, MONDAY SEPTEMBER', 'OWO IBI', 'Ibi Kika: 1Awon Oba 13:1-7', 'Olorun ran okan ninu awon ojise re si Jeroboamu lati sotele lodi si pepe ibi to gbe kale. O fe gbe owo ibi re soke lodi si iranse Olorun, sugbon Olorun gbe owo na. Ti Olorun ko ba gbeja iranse Olorun yii, Jeroboamu iba paa run. Bi o se sele nibi, bee lori ninu aye opolopo eniyan nitori pe won ma nduro tabi so otito, sugbon ma saniyan, Olorun yo ja fun o.', 'ADURA:\r\n*Gbogbo owo ibi ti a gbe soke lodi si mi, yio gbe danu loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Orin Dafidi 85-88', '2023-04-19'),
(252, '2023-09-05', '05, TUESDAY SEPTEMBER', 'OMO ODO ALAIGBORAN', 'Ibi Kika: 1Awon Oba 13:20-24', 'Iranse Olorun kan se aigboran si oro Olorun nipase Woli agba sugbon ere aigboran re n ani aye re. Bi o tile je pe iranse Olorun yi si aimokan gbagbo ninu iro ti woli agba pa fun, o si jiya re ni ona to lagbara. Sise aigboran si Olorun tin a opo ni aye ati ayanmo won. Ye ona re wo loni, ki o si yi pada.', 'ADURA: \r\n*Oluwa, mase jeki emi aigboran gbe mi wo loruko Jesu\r\n*Mo ko emi aigboran loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Orin Dafidi 89-91', '2023-04-26'),
(253, '2023-09-06', '06, WEDNESDAY SEPTEMBER', 'DAN GBOGBO EMI WO', 'Ibi Kika: 1Johaanu 4:1', 'Emi ninu ibi kika wa je iwa ati ise awon ti won ko jowo ara won fun otito. Ninu aye loni, opolopo woli eke ti dide, ti won si kun fun iro ati isoro odi. Won sotele pelu emi to lodi, won a si tan opolopo je. Ko yani lenu pe, iru awon eniyan be ni awon opo nsa to lo. Loruko Jesu, a o da won mo, won ko ni tan wa je loruko Jesu.', 'ADURA:\r\n*Oluwa ma je ki emi buruku tan mi je loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Orin Dafidi 92-100', '2023-05-08'),
(254, '2023-09-07', '07, THURSDAY SEPTEMBER', 'ISOJI TEMI', 'Ibi Kika: Hosea 6:2', 'Akoko ti awa bayi ninu aye yi mu kii opo kristiani rewesi ninu emi. A le da eni ti o dagba ninu emi mo nigbati a ba ri ipongbe re fun ohun ti Olorun. Ipongbe tumosi ilera pipe. Nigbati ipongbe ko ba si mo, aisan ti wole. Ani lati kepe Olorun fun isoji sori pepe wa to ti doku, ki a le gbona leekan si.', 'ADURA:\r\n*Oluwa, tun ina isoju da si inu mi, da ina sinu mi fun o, loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Orin Dafidi 101-104', '2023-05-08'),
(255, '2023-09-08', '08, FRIDAY SEPTEMBER', 'DIDURO DEDE NINU OLUWA', 'Ibi Kika: Ifihan 3:15-16', 'Iduro dede o je oro pataki ninu ijoba Olorun. O tumosi diduro dede pelu Olorun, Kristiani to duro sinsin yio duro pelu Olorun, a o le gba sihin tabi sohun pelu oni ruru esin. Ati pe, ko w ani iha ti Olorun nitori ohun tofe gba lowo Olorun, sugbon lati sin ati lati josin fun Olorun. Nigbati Kristiani ba ko bi a se nduro sinsin ninu Olorun, ko ni padanu ibasepo re pelu Olorun, sibe na Olorun a le gbekele ni ekun rere.', 'ADURA:\r\n*Baba, fun mi ni ore ofe lati duro sinsin ninu re loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Orin Dafidi 105-106', '2023-05-08'),
(256, '2023-09-09', '09, SATURDAY SEPTEMBER', 'MAJEMU ALAAFIA', 'Ibi Kika: Isaiah 53:1-5', 'Jesu ku fun o, ko le ni Alafia ninu aye, a gbe ko igi agbelebu. Bi o tile je pe ayanmo re niyi, sibe iku re ko sele lasan. Idi kan ti Jesu fi wa sinu aye niyi, lati fo ori satani nipase oku ati ajinde re, ati pe ki ole fun wa ni alafia ni gbogbo ona. Iku Jesu tun fi opin si iyapa to wa laarin Olorun ati eniyan. O si je ki won le pe wa ni omo Olorun lokunrin ati lobirin.', 'ADURA:\r\n*Oluwa fidi majemu alafia re mule ninu aye mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Orin Dafidi 107-109', '2023-05-08'),
(257, '2023-09-10', '10, SUNDAY SEPTEMBER', 'AWON ILEKUN SISI', 'Ibi Kika: Ise Aposteli 12:7-11', 'Ilekun sise tumosi anfani lati gba ibi kan, ile, itesiwaju, ibi to kun fun ogo laisi idena. Ni ibi kika wa, awon ijo gbadura fun Peteru, Olorun si ran angeli lati si ilekun tubu. Ninu aye wa gege bi Kristiani, a nilo lati ri iriri Ilekun ti a si lopolopo, kii se ilekun lasan. Eni to ni kokoro iyara ibusun lowo yio wole pelu irorun lati sun. Sugbon ti ko bani kokoro iyara idana, ebi yio paa. Jesu ni Olori kokoro ti o le si gbogbo ilekun nipa isuna owo, temi ati ti ilera pipe. Kepe loni lori awon ilekun to nilo lati si ninu aye re, yio si da o loun.', 'ADURA:\r\n*Awon ilekun oju rere, ire ati anu ti a titi lodi simi, ma si loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Orin Dafidi 110-118', '2023-05-08'),
(258, '2023-09-11', '11, MONDAY SEPTEMBER', 'OLORUN ISODI PUPO', 'Ibi Kika: Marku 6:35-44', 'Awa sin Olorun to ma nso ikan di pupo. Oni agbara lati so ipele kekere di nla. Akara kekere ati eja die, di pupo lowo Olori. Olorun isodi pupo le so ise kekere re, idawole re ati ipo re di nla. Nje iwo gbagbo?', 'ADURA:\r\n*Baba, jeki agbara isodi pupo farahan ninu aye mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Orin Dafidi 119:1-56', '2023-05-08'),
(259, '2023-09-12', '12, TUESDAY SEPTEMBER', 'ONA ABAYO', 'Ibi Kika: Eksodu 15:22-25', 'Leyin igbati awon omo Israeli koja okun pupa, won nilo omi, sugbon omi tiwon ri koro. Olorun da si oro na, o si fun Mose ni ona abayo si isoro won. Ko si ipenija tabi isoro ti Olorun ko ni ipalati yanjun. Nje oni ipenija ni ona kan tabi ekeji, lo ba Olorun!', 'ADURA:\r\n*Oluwa, fun mi ni ona abayo patapata lori gbogbo ipenija mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Orin Dafidi 119: 57-176', '2023-05-08'),
(260, '2023-09-13', '13, WEDNESDAY SEPTEMBER', 'ORUN TO SI', 'IbI Kika: Matteu 3:15-17', 'Ni ibi kika, a so fun wa pe, ni kete ti Johaanu se iribomi fun Jesu, orun sii, ohun kan si fohun latoke, o si fi idi jije omo Olorun ati ise iranse re mule ni aye. Lati akoko yen lo, Jesu ko sise labe orun to se.', 'ADURA:\r\n*Baba, si awon orun, ko si fi idi agbara re mule ninu aye mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Orin Dafidi 120-130', '2023-05-08'),
(261, '2023-09-14', '14, THURSDAY SEPTEMBER', 'IPESE OLORUN', 'Ibi Kika: 2 Awon Oba 17:2-6', 'Olorun pase fun eye iwo lati ma bo Elijah ni akoko iyan. Bi o tile je pe awon eniyan nke ni akoko yii, Olorun pese o tun toju Elijah. Ko si bi orile ede yi se le to, Olorun ti se tan, yio si ma pese fun awon omo tire, koni se bi akoko na se le to.', 'ADURA:\r\n*Baba, bami pade ni ikorita aini mi, ki o sip ese fun gbpgbo aini mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Orin Dafidi 131-138', '2023-05-08'),
(262, '2023-09-15', '15, FRIDAY SEPTEMBER', 'IBINU OLUWA', 'Ibi Kika: Numeri 12:1-10', 'Awon omo iya Mose dide lodi sii, won sir u ibinu Olorun soke si ara won. Gege bi Kristiani, a ni lati sora, ka ma soro odi si awon iranse Olorun, ki a ma ru ibinu Olorun sori ara wa. Miriam di adete loju ese leyin igba ti o soro odi si egbon re. Olorun ko so Aaroni di adete nitori ipo Alufa re. Eje ki a ma sora ki a si pa enu wa mo lori ohun ti a ma so nipa awon iranse Olorun.', 'ADURA:\r\n*Oluwa, ohun ti ma se tabi so, ti o ma ruu ibunu re simi, ranmilowo lati ma se loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Orin Dafidi 139-150', '2023-05-08'),
(263, '2023-09-16', '16, SATURDAY SEPTEMBER', 'OMO ODO OLOTO', 'Ibi Kika: Luku 19:16-17', 'Jije oloto tumosi fifi ara mo jin, ije olooto, idiro sinsin lenu ise. Omo odo pelu talenti mewa jere mewa si, a si ka kun olooto ati eni to fara jin. Fun gbobgo ise ti Olorun ba file wa lowo, ohun reti pe ka je oloto ati ka fara si ise na. Fun gbobgo jije olooto, ere nbe fun.', 'ADURA:\r\n*Baba, mo gba ore ofe lati di olotito ati lati je olotito si loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Iwe Owe 1-2', '2023-05-08'),
(264, '2023-09-17', '17, SUNDAY SEPTEMBER', 'ALEJO LATOKE', 'Ibi Kika: Johaanu 2:1-10', 'A pe Jesu si ibi igbeyawo akoko ninu majemu titun. Jesu so egan won di iyanu. Nigbati o ba pe Jesu sinu ipo to nla koja, yio so egan re di ogo. Pinu loni, lati mase ohunkohun lai pe Jesu si.', 'ADURA:\r\n*Olorun Baba, Omo ati Emi mimo, mo pe yin sinu irin ajo aye mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Iwe Owe 3', '2023-05-08'),
(265, '2023-09-18', '18, MONDAY SEPTEMBER', 'ASILO ORE OFE', 'Ibi Kika: Onidajo 16:4-20', 'Samsoni je akanda omo lati odo Olorun. O kun fun agbara sugbon o si oreofe Olorun lori aye re lo. Igba akoko ti Delila fe mo idi agbara re, eyi ye ko je ikolo fun, sibee ko gboran si ikilo. Gbogbo onigbagbo lo kun fun ore ofe kan tabi ekeji, sugbon nigbati a ba si ore ofe lo nipase aigboran, a o jiya lopolopo.', 'ADURA:\r\n*Baba ran mi lowo, mase jeki nsi ore ofe lo ninu aye mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Iwe Owe 4-6', '2023-05-08'),
(266, '2023-09-19', '19, TUESDAY SEPTEMBER', 'ADURA JABESI', 'Ibi Kika: 2Kronika 4:9-10', 'Ipo ti Jabesi wa buru lopolopo, sugbon o pe Olorun lati yi igba re pada. Olorun dahun adura re. Gege bi onigbagbo, awa na leto ninu aye lati Gbagbo wipe nigbati a ba gbadura lati inu okan wa, Olorun yio gbo nitoripe, ki se ojusaju enikeni.', 'ADURA: \r\n*Olorun to dahun adura Jabesi, yio dahun adura mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Iwe Owe 7-11', '2023-05-08'),
(267, '2023-09-20', '20, WEDNESDAY SEPTEMBER', 'JE KI AWON OTA MI TUKA', 'Ibi Kika: Orin Dafidi 68:1-6', 'Nigbati awon ota poju fun Dafidi, o kepe Olorun lati tun won ka. Tani o nko oju si nigba ti awon ota ba died ogun si o? Dafidi kepe Olorun nipa awon ota re, Olorun si da lohun. Kepe Olorun loni lori ipenija tabi isoro ti o le ma la koja, yio si fun o ni isegun patapata.', 'ADURA:\r\n*Oluwa dide, ki o si tu awon ota mi ka, ki o si fun mi ni isegun lori won ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Iwe Owe 12-14', '2023-05-08'),
(268, '2023-09-21', '21, THURSDAY SEPTEMBER', 'OTA IDILE', 'Ibi Kika: Matteu 26:14-23', 'Judasi Iskarioti je ota idile nla fun Jesu Kristi. Ota idile tire le je ore tabi eni to sun mo o. Judasi je okan lara awon omo eyin Jesu, sibe o daa Jesu. Mo gbadura fun o, Olorun yio tu asiri awon ota idile re loruko Jesu.', 'ADURA:\r\n*Oluwa, fun mi ni isegun lori ota idile mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Iwe Owe 15-17', '2023-05-08'),
(269, '2023-09-22', '22, FRIDAY SEPTEMBER', 'IWE AKOSILE', 'Ibi Kika: Kolosse 2:14-15', 'Eje Jesu pa ofin Mose run, eyi ti ko fi aye sile fun igbala. Sugbon nipa eje Jesu, a se atumo ohun gbogbo. Ba kana, opolopo ni awon akosile ibi ti awon ota tiko lodi si wa, ma beru, agbara Olorun wa lati pa won run.', 'ADURA:\r\n*Gbogbo akosile ota lodi si mi ati idile mi, parun loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Iwe owe 18-20', '2023-05-08'),
(271, '2023-09-23', '23, SATURDAY SEPTEMBER', 'IBUKUN TI ABRAHAMU', 'Ibi Kika: Genesisi 12:2-3', 'Igbagbo Abrahamu ninu Oluwa sodi baba orile ede gbogbo, awon ileri naa si se ninu aye re. Igboran, je kokoro to mu Ibukun ti Abrahamu wa si imuse ninu aye re. Ise igboran wa si Olorun yio fa Ibukun Olorun sinu aye wa. Se a setan lati gboran?', 'ADURA:\r\n*Oluwa, mo je okan ninu irugbin Abrahamu pelu igbagbo, fun mi ni okan igboran ki gbogbo ileri Abrahamau le wa si imuse ninu aye wa loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Iwe Owe 21-23', '2023-05-08'),
(272, '2023-09-24', '24, SUNDAY SEPTEMBER', 'AWON OLOGBON ENIYAN', 'Ibi Kika: Iwe Owe 13:20', 'Enikeni to ba ba ologbon rin, yio fi iwa jo o ninu ogbon, pelu idaniloju, yio di nla ati wipe won yio se ajoyo re. Awon ti a ba nba rin tabi ni ni ore yio so ipele ti a ma de laye. Ba awon eniyan olokan pipe rin, yi si ni ipa to daa lori aye re.', 'ADURA: \r\n*Baba, so mi po mo awon ologbon ninu re loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Iwe Owe 24-26', '2023-05-08'),
(273, '2023-09-25', '25, MONDAY SEPTEMBER', 'OLUKO ILE NLA', 'Ibi Kika: Iwe Owe 14:1-4', 'Iwe Owe je ko ye wa wipe, ologbon okunrin tabi obinrin ma nko ile re. Eyi kii se fun obinrin nikan. Gbogbo ologbon okunrin tabi obinrin ye ko  ko bi a nse toju ile ati aye re. Mase gbe igbe aye bi omugo, ko ile, ise ati okowo re de ipele to ga, mo si ri pe Olorun yio ran o lowo ni oruko Jesu.', 'ADURA:\r\n*Oluwa, so mi di oluko ile rere, ti yio ma ko aye eniyan ati ile loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Iwe Owe 27-29', '2023-05-08'),
(274, '2023-09-26', '26, TUESDAY SEPTEMBER', 'ETO RERE', 'Ibi Kika: Luku 14:28-30', 'Ohun kan ni lati ni ilepa, bakana ohun miran ni lati ni eto bi a o se mu awon ilepa wonyi se. Gbogbo eniyan lo fe mu ilepa kan tabi omiran se, Olorun ti fun won ni ara, sugbon won koni bi a ti seto. Olorun yio ran wa lowo lati se gbogbo ko sese, sugbon a gbodo se awon eleyi ti a le se fun ara wa. Olorun ti fun o ni ara bi onisowo lokunrin tabi lobinrin, joko, se eto nipa okowo re, Olorun yio sise pelu re, a si muwa si imuse.', 'ADURA:\r\n*Mo gba ore ofe lati je eleto daradara fun ise akin nla ninu ise ranse, ise ati okowo mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Iwe owe 30', '2023-05-08'),
(275, '2023-09-27', '27, WEDNESDAY SEPTEMBER', 'EBO TO SE ITEWOGBA', 'Ibi Kika: 2Kronika 7:1-11', 'Solomoni se irubo nla si Olorun nigbati won nfi tempili Olorun lo le. Akoko Emi Mimo lawa yii, a o nilo lati fi eranko rubo mo. Ni bayi, aye wa ni lati je ebo aiye to je itewogba si Olorun. Agbodo ma gbe igbe aye iwa mimo ki a le je itewogba niwaju Oluwa.', 'ADURA:\r\n*Oluwa ran mi lowo lati gbe igbe aye iwa mimo, ki aye mi le je itewogba niwaju re ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Iwe Owe 31', '2023-05-08'),
(276, '2023-09-28', '28, THURSDAY SEPTEMBER', 'OWO TI A KO', 'Ibi Kika: Orin Dafidi 18:34-36', 'Iwe mimo je ko ye wa ninu iwe orin Dafidi wipe, Olorun nko owo wa lati jagun. Olorun ti fun w ani agbara lati doju ogun ko awon ota wa pelu isegun. Ma wo ota niran lati ba aye, ile, ise tabi ise ranse re je. Ko bi a se ndi ida emi mu, ti se oro Olorun, ki iwo ko le koju ija si satani. Bakana, o ti ko wa lati lo owo wa fun ise akin, nitorina mase je ole, wa nkan se.', 'ADURA:\r\n*Baba, ko owo mi lati se ise akin nla ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Oniwasu 1-3', '2023-05-08'),
(277, '2023-09-29', '29, FRIDAY SEPTEMBER', 'RAN IGBAGBO MI LOWO', 'Ibi Kika: Luku 22:31-33', 'Jesu Kristi mo ohun to ma sele si igbagbo Peteru ni ojo iwaju, nitorina, o gbadura fun. Jesu ni alagbawi wa, o si joko lowo otun Olorun lati sioe fun awa ayanfe. O si ngadura fun wa si be, ki a wa ko ma ba rewesi ninu igbagbo. Ma ranti eleyi ni gbogbo igba to ba dabi pe o tin re tabi o fe re e. ke pe Jesu. Oluwa ran igbagbo mi lowoâ€™â€™.', 'ADURA: \r\n*Oluwa, ran igbagbo mi lowo lati ma ye loruko Jesu. \r\nBIBELI KIKA/OJOJUMO: Oniwasu 4-7', '2023-05-08'),
(278, '2023-09-30', '30, SATURDAY SEPTEMBER', 'ARA SAMARIA RERE', 'Ibi Kika: Luku 10:33-37', 'Gbogbo omo Olorun lo nilo ara Samaria rere ti yio ran won lowo ti yio si ba won gberu aye won, ti yio sib a won pin ninu eru wiwo won, ki won ma ba so ireti nu ninu Oluwa.', 'ADURA:\r\n*Oluwa, so mi po mo ara Samaria rere ti yio ran mi lowo de ibi imuse laye ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Oniwasu 8-12, Orin Solomoni 1-4', '2023-05-08'),
(279, '2023-10-01', '01, SUNDAY OCTOBER', 'OMINIRA JE TI WA', 'Ibi Kika: Johaanu 8:6', 'Bibeli je ko ye wa wipe, bi Olorun ba so wa di ominira, awa di ominira ni tooto. Loni, a nse ajoyo ojo ti orile ede wa gba ominira, ajoyo kuro lowo oko eru ati iponiloju lati odo awon orile ede miran. Bi a se ntu wa  sile nipa tara, beeni a tu wa sile kuro ninu igbekun aisan, ati aarun. Loni, gbadura, jewo ohun nla ati ohun rere nipa ilu re kii o si se ajoyo itusile ominira orile Ede re.', 'ADURA:\r\n*Mo gbadura fun Nigeria, Pe yio dagba lati ipa de ipa ati lati inu ogo sinu ogo loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Orin Solomoni 5-8', '2023-05-08'),
(280, '2023-10-02', '02, MONDAY OCTOBER', 'BI BORI ALANTAKO', 'Ibi Kika: Luku 19:26-28', 'Jesu Oluwa dide lodi si awon alantako ti won ko fe ko joba le won lori. Ti Jesu ba le se iru eleyi, a o yato. A ni lati dide lodi si gbogbo idoju ko okunkun, dide lodi si gbgogbo itako to dide lodi si isegun, igbe dide ati ojurere wa, ki a ma ba kaba mo leyin aye wa.', 'ADURA:\r\n*Mo pase loruko Olorun, eyi gbogbo awon ti ko fe kin joba laye yii, e segbe loruko Jesu.\r\nBIBELI KIKA/ OJOJUMO: Heberu 1-4', '2023-05-08'),
(281, '2023-10-03', '03, TUESDAY OCTOBER', 'IWOLE OGO', 'Ibi Kika: Luku 19:35-38', 'Iwole Jesu sinu Jerusalemu je ti ogo.  Ni akoko ti a wa yi, a le pe ni itele pupa. A gba Jesu wo Jerusalemu togotogo pelu ariwo ogo. Eri ti o polongo re togotogo, Olorun yio faye gba ninu aye re loruko Jesu.', 'ADURA:\r\n*Olorun yio se o leso ati ola niwaju awon ota re loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Heberu 5-9\r\n\r\n\r\nBIBELI KIKA/ OJOJUMO: Heberu 1-4', '2023-05-08'),
(282, '2023-10-04', '04, WEDNESDAY OCTOBER', 'ASO OGO', 'Ibi Kika: Eksodu 28:1-5', 'Olorun fun Mose ni itoni ko se aso ogo fun Aaroni. Olorun le pase fun eniyan lati bukun fun o lona ologo ko si yii aso egan re pada isi aso ogo.', 'ADURA:\r\n*Baba mi tin be lorun yio wo mi laso ogo loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Heberu 10-13\r\n\r\n\r\n\r\nBIBELI KIKA/ OJOJUMO: Heberu 1-4', '2023-05-08'),
(283, '2023-10-05', '05, THURSDAY OCTOBER', 'ESE TO DARA', 'Ibi Kika: Isaiah 52:7', 'Ese awon olododo ni ekun rere atileyin Olorun, nitori won nkede iroyin ayo ti Oluwa. Olorun nreti wa lati kede iyin rere fun gbogbo alaigbagbo. Olorun si pe awon to se eyi ni eni to lese daradara.â€™â€™', 'ADURA:\r\n*Oluwa, ran mi lowo lati mu iyin rere lo si ibi ti a nilo re loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Jakobu 1-5\r\n\r\n\r\n\r\nBIBELI KIKA/ OJOJUMO: Heberu 1-4', '2023-05-08'),
(284, '2023-10-06', '06, FRIDAY OCTOBER', 'ILERA PIPE', 'Ibi Kika: 3Johannu 1-3', 'Okan ninu eto Olorun fun onigbagbo ni ka gbadun ilera latoke wa, ka sig be igbe aye to bori aisan ati arun.', 'ADURA:\r\n*Oluwa, mo gba ore ofe lati gbe igbe aye to bori aisan, arun mo si gba ilera latoke wa loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Isaiah 1-4', '2023-05-10'),
(285, '2023-10-07', '07, SATURDAY OCTOBER', 'GBIGBA LE OLORUN', 'Ibi Kika: Matteu 21:10-13', 'Nigbati Jesu de Jerusalemu, o wo inu tempeli, o si gba gbogbo awon ti won ta ati ti won ra jade. Awon eniyan nibi tumosi aisan, arun, irora, ohun atijo, aini ati ijakule ninu aye re. Sugbon Jesu wa nibi lati gba ohun gbogbo yii kuro ni aye wa loruko Jesu.', 'ADURA:\r\n*Ohun gbogbo to duna dura ise ibi ninu aye mi, mo gba won danu loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Isaiah 5-8', '2023-05-10'),
(286, '2023-10-08', '08, SUNDAY OCTOBER', 'ASOJU ONI IROYIN AYO', 'Ibi Kika: Luku 1:5-14', 'Nigbati Olorun fe ranse ayo ibi omo Zakaraya, angeli Gabrieli ni Olorun ran se lati jise iroyin ayo naa. Ibukun ma nti odo Olorun wa, o si ma nwa nipase angeli tabi eniyan. Mase je ki agara da o, nitori o daju pe Olorun yio ran angeli tabi eniyan lati fun o ni iroyin ayo.', 'ADURA:\r\n*Mo gbadura, asoju iroyin ayo yio pin iroyin ayo kanmi ati idile mi loni loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Isaiah 13-17', '2023-05-10'),
(287, '2023-10-09', '09, MONDAY OCTOBER', 'IJINLE OLORUN', 'Ibi Kika: Luku 1:30-35', 'A nsin Olorun to sise lona ijinle. Awon ohun ta o salaye sele ninu aye Maria iya Jesu. Fin awon onimo siensi, o dabi enipe ko se se fun obinrin lati loyun laisi okunrin.  Lona kona, iyanu re, aluyo, iwosan ati itusile re koni wa lona to fokan si, sugbon lona ijinle ti yio koja aye eniyan.', 'ADURA:\r\n*Olorun to sise lona ijinle yio jeri ara re ninu aye re loruko Jesu. \r\nBIBELI KIKA/OJOJUMO: Isaiah 13-17', '2023-05-10'),
(288, '2023-10-10', '10, TUESDAY OCTOBER', 'MO OLORUN RE', 'Ibi Kika: Danieli 11:32', 'Lati mo Olorun tumosi pe ka mo iru eni tin se ati ife re, a o si je ope nipa tito bi re ati bi o tin sise. Bi o ba se mo Olorun to, ni yio se farahan ninu aye re.', 'ADURA:\r\n*Oluwa, mo mo o gege bi olumubosipo, olugbala ati olupese, jeri awon nkan wonyi ninu aye mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Isaiah 18-21', '2023-05-10'),
(289, '2023-10-11', '11, WEDNESDAY OCTOBER', 'OHUN TO NILO GAN', 'Ibi Kika: Ise Awon Apostoli 3:1-10', 'Okunrin aro lenu ibode daradara fe owo lati odo Peteru ati Johaanu, sugbon kii se ohun ti o nilo gan niyi biko se pe ko dide, ko si ma rin. Opolopo lo gbagbo wipe owo ni olori ohun tiwon nilo lowolowo. Sugbon ye inu ara re wo, owo kan je ohun to fe, kii se ohun ti o nilo. O ni lati bere fun iranlowo Emi Mimo lati ran o lowo ki o le mo ohun ti o nilo.', 'ADURA:\r\n*Emi Mimo, ran mi lowo lati sawari ohun ti mo nilo loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Isaiah 22-25', '2023-05-10'),
(290, '2023-10-12', '12, THURSDAY OCTOBER', 'ITUSILE KURO LOWO EMI IRO', 'Ibi Kika: Ise Awon Aposteli 5:1-10', 'Ananias ati iyawo re paro lodi si Emi Mimo. Ona ti awon mejeji gba paro ni a rii ni ona etan, ti won jo mo nipa, kii se pe o kan dede sele. Ijiya to de ba won loju ese jasi iku. O se pataki ki a mo pe iro pipa ko dara. Satani lo wa nidi re, iro pipa si ni abajade.', 'ADURA:\r\n*Oluwa gba mi lowo emi iro ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Isaiah 26-29', '2023-05-10'),
(291, '2023-10-13', '13, FRIDAY OCTOBER', 'KI SE NIPA TARA TABI EJE', 'Ibi Kika: 2Korinti 10:1-5', 'Ona ti opolopo eniyan ngba wo ija emi, won Gbagbo wipe awon nba eran ara ati eje ja, rara o, sugbon o je ija temi. Esi abajade ija emi ma fara han loju aye.', 'ADURA:\r\n*Baba komi bi a tin ja ija emi ati pe ki o si fun mi ni isegun lori won loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Isaiah 30-33', '2023-05-10'),
(292, '2023-10-14', '14, SATURDAY OCTOBER', 'EBUN EMI', 'Ibi Kika: 2Korinti 10:1-5', 'Opolopo ebun ni Oluwa ti fifun onigbagbo, eyi si wa lati fii ru ijo soke ki won sii lo, ati lati fi jere si ijoba Olorun. A ni lati sawari awon ebun wa ka le lo won fun ogo Olorun. Ki se ohun ti a fin pa owo tabi lati fi itoni je. Bi o tile je pe a ri orisirisi ijo ti won fi ebun won tan elomiran je. Ko ye ki o ri be. A ni lati lo fun idagba soke ijo Olorun, kii se ka fi ji ninu ijo. E je ka kiyesi eyi.', 'ADURA:\r\n*Oluwa je ki ebun emi re farahan ninu aye mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Isaiah 34-37', '2023-05-10'),
(293, '2023-10-15', '15, SUNDAY OCTOBER', 'OLUTUMO OLORUN', 'Ibi Kika: Danieli 2:26-30', 'Oba Nebukadnessari la ala, sugbon o gba gbe ala re. O si nilo eniyan ti yio so itumo ala re fun. Danieli ni igbekele pipe ninu Olorun ti o mo ohun gbogbo, ti o si le tumo gbogbo ola. Nipase iranlowo Danieli so ala ati itumo ala na fun oba. Okan re nporuru bi? Boya lori ala re, ise tabi ipinnu re, so fun Olorun yio si tun fun o ni itumo ni ona re.', 'ADURA: \r\n*Olorun yio so mi po mo olutumo ti Olorun loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Isaiah 38-41', '2023-05-10'),
(294, '2023-10-16', '16, MONDAY OCTOBER', 'ORE ERUBO', 'Ibi Kika: Genesisi 8:20-22', 'Leyin igbati Olorun gba Noah ati idile re la kuro lowo ikun omi, o rubo si Olorun ati wipe ebo na je itewo gba lati odo Olorun. Nigba kugba ti Olorun ba gbawa lowo ibi tabi isoro kan, o ye ka fun Olorun ni ore lati fi imore wah an. Dafidi wipe, ohun ko ni fun Olorun ni ore ti ko na ohun ni nkan kan. E je ka ko iru iwa yi.', 'ADURA: \r\n*Mo gba ore ofe nla lati fun Olorun lore to yee loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Isaiah 42-45', '2023-05-10'),
(295, '2023-10-17', '17, TUESDAY OCTOBER', 'OJU RERE OLORUN ATI TI ENIYAN', 'Ibi Kika: Luku 2:52', 'Oju rere Olorun ati ti eniyan je eri pe a ni aaye si okan Olorun ati eniyan. A mu ki o wu awon eniyan lati lowo si aseyori re. Jesu ni iru iriri yii nigbati o sise iranse ni aye, awa na le ni.', 'ADURA:\r\n*Oluwa, mo gba oju rere Olorun ati ti eniyan loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Isaiah 46-50', '2023-05-10'),
(296, '2023-10-18', '18, WEDNESDAY OCTOBER', 'OGO OJO IWAJU', 'Ibi Kika: Filippi 2:6-11', 'Jesu Oluwa wa pese ojo ola to logo fun ara re. O fi orun sile lati wa gba araye la kuro lowo ese. O re ara re sile, o gbe aworan eniyan wo. Eni ti ko lese di elese ati eni ibaje. Iru irubo to ba ife Olorun mu wo lawa na ngbe lati ri pe awon okan di eni igbala layika wa? E jeki a safara we Jesu.', 'ADURA:\r\n*Oluwa, ohun ti ma se ti a pese ibi ojo waju ologo fun mi, je kin le se loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Isaiah 51-55', '2023-05-10'),
(297, '2023-10-19', '19, THURSDAY OCTOBER', 'AKOKO TI O TO', 'Ibi Kika: Johannu 5:1-9', 'Gbogbo igbese laye lo ni akoko ati igba tire. Okunrin alaisan to wa leti odo Betesda n duro de akoko ti angeli yio ruu omi fun iwosan, sugbon o ba Jesu pade, a si wo san. Nigbati akoko eniyan fun iyanu, igbesoke, iwosan ati itusile ba de, ko si ohun ti o le yi pada. A si je ki Olorun gba gbogbo ogo fun ara re.', 'ADURA:\r\n*Baba je ki akoko iyanu mi de loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Isaiah 56-60', '2023-05-10'),
(298, '2023-10-20', '20, FRIDAY OCTOBER', 'IJI GBO OHUN RE', 'Ibi Kika: Marku 4:36-41', 'Nigbati Jesu ati awon omo eyin re wa ninu oko fun irin ajo, won ni irir iji ni ona won, sugbon Jesu da iji na duro. Nigbati Jesu ba wa ninu oko aye re, igi yio de ni tooto, sugbon ko ni ni agbara lori re.', 'ADURA:\r\n*Gbogbo iji ninu irin aji aye mi, dawo duro loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Jeremiah 1-2', '2023-05-10'),
(299, '2023-10-21', '21, SATURDAY OCTOBER', 'AKOKO FIN ERI', 'Ibi Kika: Matteu 8:14', 'Adete wa sodo Jesu fun iwosa, Jesu si wosan, sugbon Kristi so fun pe ko lo so eri re fun alufa, ko si san ore iwosan eyi ti Mose pase. Eri ma nrun igbagbo awon eniyan soke, a si je ki awon eniyan tubo gbagbo Olorun sii. E je ka gbinyanju lati ma so eri ohun rere gbogbo ti Olorun se ninu aye wa, ki a si fi ore to dara tii leyin.', 'ADURA:\r\n*Baba, iyanu to ma mu mi jeri, je ko sele ninu aye mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Jeremiah 1-2', '2023-05-10'),
(300, '2023-10-22', '22, SUNDAY OCTOBER', 'WO MI SAN OLUWA', 'Ibi Kika: Matteu 8:5-13', 'Balogun orun kan wa, omo odo re saisan, o sig be oro na to Jesu wa. Balogun orun n ani igbagbo pipe ninu agbara Kristi wipe lai ri okunrin to saisan na, Jesu yio wosan patapata. O saisan bi? Gba Jesu gbo loni, yio si wo o san patapata.', 'ADURA:\r\n*Oluwa mi, oluwosan nla, wo mi san kuro ninu gbogbo aisan ati aarun loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Jeremiah 3-4', '2023-05-10'),
(301, '2023-10-23', '23, MONDAY OCTOBER', 'ETO MI NINU MAJEMU', 'Ibi Kika: Genesisi 17:5-8', 'Opolopo onigbagbo ni ko ni oye eto won ninu majemu nipase Abrahamu ati Jesu. Olorun ba Abrahamu da majemu siso di nla, awa gege bi irugbin Abrahamu, je alabapin ninu majemu yi, niwon igba ti a ba ti gba Jesu gege bi Oluwa ati Olugbala wa. Nitori na mase gbe igbe aye aimokan, so ara re po mo majemu yi, ki mo si ri bi yio se ma sise fun o.', 'ADURA:\r\n*Oluwa, mo so ara mi po mo gbogbo eto majemu ti mo ni ninu Kristi Jesu, mo si jewo re pe ki won bere si ni sise fun mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Jeremiah 5-6', '2023-05-10'),
(302, '2023-10-24', '24, TUESDAY OCTOBER', 'AGBARA ASEYORI', 'Ibi Kika: Josua 1:8', 'Aseyori tunmo si nini ipinnu, jije eni to tayo tabi aluyo. Agabara aseyori wa ninu oro Olorun. A o le se aseyori tabi mu ere jade nigba taba nko oro Olorun, se asaro, ki a si gbe igbese lori oro na.', 'ADURA:\r\n* Baba, agbara aseyori ba le mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Jeremiah 7-9', '2023-05-10'),
(303, '2023-10-25', '25, WEDNESDAY OCTOBER', 'ISERERE NIOA TARA', 'Ibi Kika: 3John 1:2', 'Iserere nipa tar ani opo omo Olorun ko ka si, ni Pataki julo awon iranse Olorun. O dara ka dagba ninu emi, o si se Pataki ki ama foju tembelu ara wa. Nigbati o ba re ara, emi yio fi sile, eyi koni je ka le pari ise wa laye. Bi o ba lo ara re lona ti ko dara, o le fa iku ojiji. O ni lati se rere ninu tara. Nitorina, igba kugba to ba re o simi, te bi ban pa o, jeun, ti orun ba nkun o, sun. Yio dara fun wa loruko Jesu.', 'ADURA:\r\n*Oluwa, mo gba ore ofe lati fi iye si itoju ara mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Jeremiah 10-12', '2023-05-10'),
(304, '2023-10-26', '26, THURSDAY OCTOBER', 'ITUSILE PATAPATA', 'Ibi Kika: Johannu 8:32', 'Ni gbogbo gbo, itusile tunmo si gbigba eniyan kuro lowo ide, ibi tabi wahala. O si tunmosi igbala. Okan lara awon irin ise itusile ni agbara oro Olorun, eje Jesu ati oruko Jesu. Lati fi itusile re si oju ise, lona kona, o le lo awon ohun ija meta wonyi.', 'ADURA:\r\n*Mo gba itusile mi patapata ni gbogbo ona aye mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Jeremiah 13-14', '2023-05-10'),
(305, '2023-10-27', '27, FRIDAY OCTOBER', 'JE KI WON JOBA', 'Ibi Kika: Genesisi 1:28', 'Jijoba tunmosi nini anfani si ohun kan, isegun kan, nini ase ati gbara lori nkan. Okan ninu awon Ibukun ti Olorun tusi ori eniyan leyin ti o da ni wipe ko ma joba. Ni eleyi ni okan re wipe aisan yen ko ni pa o, iji yen kon gbe o lo, ise ati iran yen ko ni ku, igbeyawo yen ko ni tuka nitori o ki se ope si jijoba re ninu Kristi Jesu.', 'ADURA:\r\n*Mo ni ijoba lori ohun gbogbo ti ko dara ninu aye mi, mo si bori won loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Jeremiah 15-18', '2023-05-10'),
(306, '2023-10-28', '28, SATURDAY OCTOBER', 'DIDE TAN IMOLE', 'Ibi Kika: Isaiah 60:1-3', 'Eto Olorun fun gbogbo onigbagbo ni ko tan, ki o si fi ogo Olorun han lori ile alaaye. Ogo ti yio farahan ninu aye wa yio pe akiyesi awon alaigbagbo ati awon oba, eyi yio si mu won terbia fun Olorun wa.Okan ninu awon Ibukun ti Olorun tusi ori eniyan leyin ti o da ni wipe ko ma joba. Ni eleyi ni okan re wipe aisan yen ko ni pa o, iji yen kon gbe o lo, ise ati iran yen ko ni ku, igbeyawo yen ko ni tuka nitori o ki se ope si jijoba re ninu Kristi Jesu.', 'ADURA:\r\n*Mo dide, ma tan ninu aye lati fi ogo Olorun han loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Jeremiah 19-21', '2023-05-10'),
(307, '2023-10-29', '29, SUNDAY OCTOBER', 'OMINIRA LOWO EGIPTI', 'Ibi Kika: Eksodu 12:30-35', 'Egipti si awon omo Israeli je ile ide ati iponju. Nigbati Farao ti ko mo Josefu joba, o pon awon omo Israeli loju o si fi won se ise to le. Won je eru ni ile Egipti fun irinwo ojilenirinwo din mewa (430years), sugbon ni oru kan, Olorun tu won sile kuro lowo Egipti. Bawo leyi se kan wa? Koni se iye odun ti o tin doju kan egan, itiju tabi asedanu lowo awon ota, Olorun le lo ojo kan, oru kan, tabi wakati kan lati gba o patapata kuro lowo ika eniyan.', 'ADURA:\r\n*Mo pase ominira mi kuro lowo gbogbo Egipti loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Jeremiah 22-24\r\n: Jeremiah 19-21', '2023-05-10'),
(308, '2023-10-30', '30, MONDAY OCTOBER', 'DIDA IFE OLORUN MO FUN AYE RE', 'Ibi Kika: Johannu 4:34', 'Ohun ti o mu ki aye eniyan ni itumo ni ki o mo ife Olorun, ki o si mu ife na se. Loju aye, kosi ohun to mu ki eniyan wa laye ju ko ri aseyori lori ise ti a ti a pase fun lati se. Gbogbo okunrin tabi obirin alagbara to ba ri, eyi sese fun won nitoripe won wa ninu ife Olorun fun aye won. Ije pataki wa, wa ninu ife Olorun. Sawari tire loni.', 'ADURA:\r\n*Oluwa, mo gba ore ofe lati mo ife Olorun fun aye mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Jeremiah 25-27', '2023-05-10'),
(309, '2023-10-31', '31, TUESDAY OCTOBER', 'GBE IGBE AYE BI EDA OTO', 'Ibi Kika: 8:27-40', 'Ohun ti o mu ki aye eniyan ni itumo ni ki o mo ife Olorun, ki o si mu ife na se. Loju aye, kosi ohun to mu ki eniyan wa laye ju ko ri aseyori lori ise ti a ti a pase fun lati se. Gbogbo okunrin tabi obirin alagbara to ba ri, eyi sese fun won nitoripe won wa ninu ife Olorun fun aye won. Ije pataki wa, wa ninu ife Olorun. Sawari tire loni.', 'ADURA:\r\n*Mo bere si ngbe igbe aye eda eto loruko Jesu\r\nBIBELI KIKA/OJOJUMO: Jeremiah 28-30', '2023-05-10'),
(310, '2023-11-01', '01, WEDNESDAY OCTOBER', 'AYO MI', 'Ibi Kika: FILIPPI 4:4', 'Ohun aye yi a le fun nii ni idunu. Owo le ra idunu fun eniyan, sugbon ayo ni ko se fowo ra. Ayo wa lati odo Olorun.', 'ADURA:\r\n*Ayo mi lori ebi, ise ati okowo mi, koni se ge danu loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Jeremiah 33-35', '2023-05-10'),
(311, '2023-11-02', '02, THURSDAY OCTOBER', 'ANU ALAILODIWON', 'Ibi Kika: Orin Dafidi 18:5', 'Ohun ti eniyan ni, lo maa n fun ni. Ko si eniyan kan ti o le fun eniyan ni anu lopolopo, nitori ohun naa nilo re. Olorun, ti ko nilo anu lati odo enikeni ni o le fun w ani anu lopolopo.', 'ADURA:\r\n*Ayo mi lori ebi, ise ati okowo mi, koni se ge danu loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Jeremiah 33-35', '2023-05-10'),
(312, '2023-11-03', '03, FRIDAY OCTOBER', 'IHALE OLORUN', 'Ibi Kika: Genesisi 31:24', 'Eniyan le hale mo enikeji re, ko ma si pa lara. Sugbon enikan ti ihale re ni ipa ni Olorun, ina ajonirun. Olorun too Labani wa ni oju orun lati kilo fun pe ki o ma pa Jakobu lara. Ero okan Labani tele ni ki o pa Jakobu lara ati lati mu pada wa si ile re, sugbon Olorun yii pada.', 'ADURA:\r\n*Olorun mii yoo died lati hale mo gbogbo eni to ndi itesiwaju ati ominira mi lowo loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Jeremiah 36-38', '2023-05-10'),
(313, '2023-11-04', '04, SATURDAY OCTOBER', 'ETO OLORUN', 'Ibi Kika: Jeremiah 29:11-13', 'Eto kan soso ti o le fidimule, to si le seni, ni eto ti Olorun ba fi onte lu. Olorun ti ni eto agbekale fun gbogbo eniyan, o si se Pataki fun wa lati mo ati lati se gege bi Olorun ti ko. Eto ti eniyan gbe kale fun ara re le kuna, sugbon ti Olorun kole kuna.', 'ADURA:\r\n*Gbogbo eto Olorun fun aye mi pelupelu bi odun yii tin lo sopin ni yoo wa si imuse loruko Jesu.\r\n\r\nBIBELI KIKA/OJOJUMO: Jeremiah 39-42', '2023-05-10'),
(314, '2023-11-05', '05, SUNDAY OCTOBER', 'MA FUN O NI ISIMI', 'Ibi Kika: Matteu 11:28', 'Kristi fi ipe fun gbogbo eniyan ti eru aye wa lorun won, pe ohun yoo fi isimi fun okan won. Ko si bi are ti mu o to, Jesu ti seleri isimi. Se ipo to wa ti su o; Igbe aye aisan, odiwon, tabi wahala ti su o bi? Wa sodo Kristi, abani gberu aye eni, yo gbe gbogbo eru yen kuro, yoo si fun o ni isimi yika.', 'ADURA:\r\n*Jesu abanigberu, yoo gbe gbogbo eru ti ko se anfani kuro laye mi loni loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Jeremiah 43-45', '2023-05-10'),
(315, '2023-11-06', '06, MONDAY OCTOBER', 'IGBALA TOOTO', 'Text: Ise Awon Aposteli 2:21', 'Igbala tumosi ominira, idande. Igbala dondonro wa, beni ojulowo igbala wa pelu. Sugbon Bibeli wipe â€˜â€™\r\nEnikeni, eyi tumo si wipe igbala wa fun gbogbo eniyan, paapa nigbati elese wa lati ronu piwada. Ilekun anu ati igbala shi sile fun gbogbo eniyan to ba ma too Jesus wa. Se o ti fi aye re fun Jesu bi? Sebe loni kii o to pe ju.', 'ADURA:\r\n*Oluwa, mo jowo aye mi fun o loni, gba okun mi kuro ninu iparun loni.\r\n*Lati oni lo, aye mi, ise mi ati idile mi a fi ere mimo Kristi han loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Jeremiah 46-48', '2023-05-10'),
(316, '2023-11-07', '07, TUESDAY OCTOBER', 'OGO OLORUN', 'Text: Psalms 19:1-7', 'Ogo tumosi aseyori, ise rere, igbega ati ipo giga. Ogo lati odo eniyan le kuru, tabi ko ma ba igba mu, sugbon ogo lati odo Olorun wa titi aye.', 'ADURA:\r\n*Ma wa laye lati fi ogo Olorun han loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Jeremiah 49-50', '2023-05-10'),
(317, '2023-11-08', '08, WEDNESDAY OCTOBER', 'MO NSE AKOSO', 'Text: Psalms 19:2', 'Okan lara ohun ti Olorun so jade fun eniyan ni ki won ma se alakoso. Ohun ti alakoso je ni ki eniyan se ikawo nkan. Olorun ti fun eniyan ni aye yii lati ma joba lori re. ti o ko pa gba nkan, o kole joba lori re.', 'ADURA:\r\n*Lati oni lo, mo joba lori esu ti o yo aye mi ati idile mi lenu. \r\n*mo pase fun owuro mi, ma fo ohun rere fun mi. Ale mi a kun fun ifihan ijinle eto Olorun fun aye mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Jeremiah 51', '2023-05-10'),
(318, '2023-11-09', '09, THURSDAY OCTOBER', 'JEKI OLORUN DIDE', 'Text: Psalms 19:2', 'Nigbati Olorun ogun ba dide, ogun bere niyen. Isegun daju nigbati olori ogun ba pelu awon omo ologun ninu ijagun. Nigbati awon omo Ologun ba lo si iwaju ogun lai si olori ogun pelu won, o di dandan ki won male bori ota won. Ma se daba lati ja ogun aye tabi ti idile fun ara re. Pe Oluwa awon omo ogun gege bi Olori ogun re.', 'ADURA: \r\n*Olorun yoo dide fun mi loni lati tuka awon eniyan to korajopo lodi simi ni oruko Jesu. \r\nBIBELI KIKA/OJOJUMO: Jeremiah 52', '2023-05-10'),
(319, '2023-11-10', '10, FRIDAY OCTOBER', 'MO JE ORISUN', 'Ibi Kika: Johannu 7:38', 'Lati inu ibi kika wa, eyi fidimule wipe enikeni tabi iran kan le je orisun omi iye, paapaa ti eni na ba je onigbagbo ninu Kristi Jesu. O tun tumo si wipe, aye wa le je orison Ibukun fun elomiran.', 'ADURA:\r\n*Nitori Olorun so, mo si gbagbo, aye mi yoo je orisun ayo, Ibukun ati ireti fun gbogbo eniyan loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Nahumu 1-3', '2023-05-10'),
(320, '2023-11-11', '11, SATURDAY OCTOBER', 'ORUKO TITUN', 'Ibi Kika: Isaiah 62:1-2', 'Obi a ma funni ni oruko, beni pelu ohun ti eniyan nla koja, tabi ohun to sele si eniyan. Ise eniyan a ma fun ni ni oruko. Ninu ibi kika yen, Olorun wipe, ohun ko ni simi tabi dake titi di igbati Sioni, Jerusalemu ati Juda, ma fii di eni igbala, ti o si gba oruko titun. Be jeje, ki a ma se sare ninu adura, titi di igba ti Olorun yoo fun wa ni oruko titun.', 'ADURA:\r\n*A o fi oruko titun pemi lati oni lo \r\n*Gbogbo ohun ti la koja to ti fun mi ni oruko ibi, a yii pada loni in oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Esekieli 1-2', '2023-05-10'),
(321, '2023-11-12', '12, SUNDAY OCTOBER', 'OLUTUN AYANMO SE', 'Ibi Kika: 1Chronicles 4:10', 'Jabesi tumo sipe oni banuje. A pe ni ibanuje nitori iya re bi ninu ibanuje. One kan gbogi ti o se ni wipe, o kepe Olorun, Olorun sii gbo. A ni gbogbo anfani ninu aye yi lati gbagbo ninu Olorun wipe ohun yoo gbo ohun wa nigba tab gbadura lati inu okan wa, nitori Olorun si wa ninu ikowo olutun ayanmo se. Olorun kii se oju saju enikeni.', 'ADURA:\r\n*Loni, mo gba ibapade Olorun fun atunse ayanmo mi sir ere loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Esekieli 3-5', '2023-05-10'),
(322, '2023-11-13', '13, MONDAY OCTOBER', 'DIDI NLA', 'Ibi Kika: Genesis 12:2', 'Titobi tabi didi nla je abuda Olorun. Niwon igba ti o da wa laworan re, awa naa gbodo fi eda re han. Abrahamu ni iriri didi nla. Awa gege bi onigbagbo, awa ninu majemu isopo ti Abrahamu. Didi nla ni lati farahan ninu aye wa, okowo wa ati idile wa.', 'ADURA:\r\n*Ni oruko Jesu Kristi, ma di nla ninu ilera ati oro.\r\nBIBELI KIKA/OJOJUMO: Esekieli 6-8', '2023-05-10'),
(323, '2023-11-14', '14, TUESDAY OCTOBER', 'IFIFUNNI', 'Ibi Kika: 2Korinti 9:7', 'Aye yii je nipa gbigba ati ififuni. Eni ti ko ba funu, aye re yoo sofo. Gbogbo ebun ti o ni itewogba ni iwaju Olorun je eyi ti o wa lati inu okan re, ti kii se nipa alaigbodo ma se. Jeki fifuni re wa lati okan didun.', 'ADURA:\r\n*Okan tii fun ni ni tootoo, Oluwa fifun mi ni Oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Esekieli 9-13', '2023-05-10'),
(324, '2023-11-15', '15, WEDNESDAY OCTOBER', 'ERE NLA', 'Ibi Kika: Genesisi 15:6', 'Ere nla ni abajade igiyanju. Ikore ni abajade irugbin. Abrahamu gba Olorun gbo dipo pe gbogbo ileri ti Olorun se fun ko ti wasi imuse. Sugbon igbagbo re muu kii o di baba orile de nla. Eyi ni ere nla re.', 'ADURA: \r\n*Igbagbo mi ninu Olorun a gbe iyanu lemi lowo loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Esekieli 14-17', '2023-05-10'),
(325, '2023-11-16', '16, THURSDAY OCTOBER', 'YIYAN RE', 'Ibi Kika: Josua 24:15', 'Eniyan ni ominira lati yan ayanmo ayeraye re, ati ohun gbogbo too se lojo aye re. Josua so fun awon omo Israeli pe kii won yan Olorun ti won yoo sin. Ranti, mimu odo Olorun, ni yiyan to dara julo, eyi sii mu eniyan je abori.', 'ADURA:\r\n*Mi o ni yan ipinnu ti o ma ba ayanmo mi je loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Esekieli 18-21', '2023-05-10'),
(326, '2023-11-17', '17, FRIDAY OCTOBER', 'OHUN ODI', 'Ibi Kika: Genesisi 3:3-5', 'Ohun nii ipa lati dari (fun ibi tabi ire), lati je gaba. Ohun ti o gbo, lo ma so bi igbeyin re yoo se ri.', 'ADURA:\r\n*Mo pa gbogbo ohun odi to sise rudurudu, asise ati aigboran si eto Olorun, mo palenumo loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Esekieli 22-24', '2023-05-15'),
(327, '2023-11-18', '18, SATURDAY OCTOBER', 'EYIN OJU OLORUN', 'Ibi Kika: Sekariah 2:8', 'Itoju wa, ipese, abo, aseyori, alaafia ati idabobo wa, je ohun ti o je Olorun loju. Gegebi eyin oju Olorun, Olurun fii owu towa ati lati dabobo wa, ota wa di ti Olorun, ore wa si di ti Olorun pelu. Gege bi o ti sele ninu aye Dafidi.', 'ADURA:\r\n*Nitori mo je eyin oju Olorun, ati emi ati ile mi, a di kosefowokan fun awon eniyan ibi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Esekieli 25-28', '2023-05-15'),
(328, '2023-11-19', '19, SUNDAY OCTOBER', 'MO JE ENI OTO', 'Ibi Kika: Genesisi 41:39', 'Titayo tumosi gbajumo, eni ta le rii, se apejuwe, eni to yatoninu aseyori ti elegbe re. Josefu je eni ti o yato laarin awon ologbon, nitori won ko le tumo ala Pharaoh ati lati fii ona abayo nipa ala re. Nibikibi ti a ba tib a ara wa, ki a je imole, ki a da yato ki a si je eni to ni ona abayo si nkan.', 'ADURA:\r\n*Ni oruko Jesu, a gbemi dide lati gbe igbe aye to ni ipa rere, ti aye yo se ajoyo re, mi o ni subu fun ota yoo.\r\nBIBELI KIKA/OJOJUMO: Esekieli 29-31', '2023-05-15'),
(329, '2023-11-20', '20, MONDAY OCTOBER', 'ASIRI OLORIN', 'Ibi Kika: Mark 4:11', 'Imo rere lo ma n je ka gbe igbe aye rere! Ohun ti o mo nipa ara re pelu ohun to se si ohun tomo ni yoo so e di eni mimo ati olokiki.  Olorun maa fii asiri han sii eni to ba pinu lati moo si. Iru ibasepo ti a b aba Olorun ni, ni yoo so iru asiri ti yoo fi han.', 'ADURA: \r\n*Mo si ara mi sile lati gba asiri ohun ikoko ti Olorun loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Esekeli 32-35', '2023-05-15'),
(330, '2023-11-21', '21, TUESDAY OCTOBER', 'ITUSILE PATAPATA', 'Ibi Kika: Kolosse 1:13', 'Itusile tumosi kii a tule lati inu irira, lati gba tabi lati wa ninu ominira. O si tumo si itusile kuro ninu ibi. Itusile patapata wa lati odo Olorun. Nipa itusile, eniyan lodiwon, sugbon Olorun alagbara ni agbara lati tuwa sile.', 'ADURA:\r\n*Nipa agbara ninu oruko Jesu, mo gba itusile kuro lowo agbara okunkun to fe yeye aye mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Esekieli 36-38', '2023-05-15'),
(331, '2023-11-22', '22, WEDNESDAY OCTOBER', 'IYIPADA KIAKIA', 'Ibi Kika: 1Samueli 2:7', 'Iyapada je ohun to pon dandan ninu aye. Gbogbo eniyan ni o gbodo ni iriri iyipada. Aye ti o sofo iyipada ma nwa loju kan. A rii iyipada kiakia ninu aye Hannah, obirin agan di olomo meje. Iyipada ti o de ba gbogbo ona aye re nbo lona.', 'ADURA: \r\n*Agbemi soke, mo gba iyipada, aye mi kuro ni oju kan ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Esekieli 39-42', '2023-05-15'),
(332, '2023-11-23', '23, THURSDAY OCTOBER', 'IPESE', 'Ibi Kika: 2Korinti 9:10', 'Lati ibere, Olorun ti je olupese fun gbogbo eda. Imo re nipa ipese Olorun ni o maa n tii eniyan sinu Ibukun ti Oga Ogo tip ese sile.', 'ADURA:\r\n*Enu mi yoo ma gbadun ounje didun ati ohun mimu ti o tura.\r\n*Ohun rere gbogbo aye lao fi temi lorun loruko Jesu.\r\n\r\nBIBELI KIKA/OJOJUMO: Esekieli 43-44', '2023-05-15'),
(333, '2023-11-24', '24, FRIDAY OCTOBER', 'IBAPADE AGBARA', 'Ibi Kika: Ise Awon Aposteli 1:8', 'Agbara ni a nfi gbe aye. Eni ti o ni agbara Olorun nii gbe aye irorun, ti o si je eru fun ijoba okunkun. Titi di igbasoke Jesu, Jesu ko jeki awon omo eyin bere ise iranse, sugbon o so fun won wipe ki won lo si iyara oke titi won yoo fi gba agbara. Idi eyi nipe, nipa agbara ni a fin gbele aye.', 'ADURA:\r\n*Mo gba agbara atorunwa fun igbedide ati aseyori ni iran mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Esekieli 45-46', '2023-05-15'),
(334, '2023-11-25', '25, SATURDAY OCTOBER', 'EDE TITUN', 'Ibi Kika: Ise Awon Aposteli 2:4', 'Ede titun tumo si eri titun, oro ti o yato si ti eniyan, nitori ohun titun ti Olorun ti se.', 'ADURA:\r\n*Olorun yoo fi orin titun ati eri titun si enu mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Esekieli 47-48', '2023-05-15'),
(335, '2023-11-26', '26, SUNDAY OCTOBER', 'IGBEGA OLORUN', 'Ibi Kika: Jakobu 1:17', 'Igbega ti ko se daduro ti odo Olorun wa. Ohun ire gbogbo wa lati odo Olorun ti ko yi pada; Baba wani orun \r\nni eto rere fun wa, o tun fii ohun rere fun wa lojojumo. Igbeja re ninu okowo ati ise re ko ni se daduro loruko Jesu.', 'ADURA: \r\n*Baba, je ki owo igbeja ati igbedide re wa mi ri loni loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 1Peteru 1-5', '2023-05-15'),
(336, '2023-11-27', '27, MONDAY OCTOBER', 'IRELE', 'Ibi Kika: Iwe Owe 22:4', 'Laisi irele, ko si eniyan! Oro, iyin ati aye ni igbekele ninu irele ati iberu Oluwa. Awon opolopo eniyan ti padanu igbeja, alaanu nitori aini irele. Amoye awon nkan ti a ti padanu nitori aisi irele.', 'ADURA:\r\n*Mo ko emi igberaja, igbe aye eni han to nso eniyan di eni le loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: 2Peteru 1-3', '2023-05-15'),
(337, '2023-11-28', '28, TUESDAY OCTOBER', 'AIKIYESARA', 'Ibi Kika: Esekieli 39:6', 'Aikiyesara je pukute to nge ayanmo!  Aikiyesara ni ai male kiyesara, ai bikita, tabi ai male foju si nkan. Esau \r\npadanu ohun ibi re si owo Jakobu nitori aikiyesara re. Ma sese iru asise yen.', 'ADURA:\r\n*Oluwa, nipa agbara re, mo gba idande patapata kuro lowo awon emi aikiyesara loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Danieli 1-2', '2023-05-15'),
(338, '2023-11-29', '29, WEDNESDAY OCTOBER', 'ASEGUN', 'Ibi Kika: 1Johaanu 5:4', 'Eni to bori ija ni asegun. Niwon igbati a ba ti di omo Olorun, a je asegun. A ni lati fii oruko â€˜â€™asegunâ€™â€™ yii han aisan, ijakule ati ipadanu pe ko lagbara to wa.', 'ADURA:\r\nMo bori aye, ese, satani ati gbogbo ise re loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Danieli 3-5', '2023-05-15'),
(339, '2023-11-30', '30, THURSDAY OCTOBER', 'IJEWO', 'Ibi Kika: Orin Dafidi 118:17', 'Ohunkohun ti a ba pe tabi jewo ni o nwa si imuse. Ojo mejilelogbon to kun ninu odun yii, ijewo re ni yoo so bi ola ati ayanmo re se ma rii.', 'IJEWO:\r\n*Emi kii yoo ku, ma gbe igbe aye to daa, ma si gbadun ire Oluwa ni ile alaaye.\r\nBIBELI KIKA/OJOJUMO: Danieli 6-8', '2023-05-15');
INSERT INTO `yoruba_devotions` (`id`, `current_day`, `headline`, `topic`, `text`, `text_reading`, `prayer`, `date_uploaded`) VALUES
(340, '2023-12-01', '01, FRIDAY DECEMBER', 'idupe', 'Text: Orin Dafidi 98:1', 'Idupe, ati orin iyin je ohun ti a gbodo se si Olorun to da aye wa si. Olorun ti se ohun pupo fun wa, to bee ge a ko le kaa tan. Idi eyi ni wipe, ore re po, akole kaa tan. Kosi ohun kan ti a le ma la koja, kii a ma se dake a ti iyin Olorun.', 'ADURA:\r\n*Halleluyah mi, tire ni Oluwa. Mi o ni sukun bi odun yii tin lo sopin loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Danieli 9-10', '2023-05-15'),
(341, '2023-12-02', '02, SATURDAY DECEMBER', 'OHUN EKUN', 'Text: ekun jeremiah 31:15', 'Iporuru okan ni ibanuje okan, ekun tabi ekun asunda. Awon eniyan ma nberu ti opin odun ba tin sumo nitori awon isele ajalu ti o ma nsele. Sugbon, gege bi onigbagbo, ma se beru nitori Oludande re ni agbara.', 'ADURA:\r\n*Bi odun yii tin lo sopin, emi ko lati sukun tabi sofo lori idile mi loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Danieli 11-12', '2023-05-15'),
(342, '2023-12-03', '03, SUNDAY DECEMBER', 'MU EGAN MI KURO', 'Ibi Kika: Orin Dafidi 123', 'Egan tumosi itiju tabi ikorira. Olorin Dafidi wipe, Olorun nikan ni eni ti o le mu itiju kuro ninu ate eniyan. A ti gbo nipa itan awon orisirisi eniyan ti won ko korira won laisi idi kan. Emi ikorira, ni o sise ninu aye eni naa. Iru eyan bee ko le ri alaanu.', 'ADURA:\r\n*Nipa anu Olorun, egan ati ibanuje mi ka kuro loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Hosea 1-4', '2023-05-15'),
(343, '2023-12-04', '04, MONDAY DECEMBER', 'ISE BUBURU', 'Ibi Kika: Onidajo 6:1', 'Ohunkohun ti eniyan ba funrugbin, o ni yio ka. Buburu ti eniyan ba se ni a gbe lehin re. Dajudaju ese re yoo wa o ri. Ese fayegba ijiya. Awon omo Israeli je orile-ede ti Olorun nifee si, won se buburu loju Olorun, won sÃ¬ jiya lopolopo, Olorun si fi won le awon ota won lowo. Ese si ilekun fun awon ota wa lati jewa ni ya. Ko o sile.', 'ADURA: \r\n*A o gba mi kuro ninu ofo eyi ti Satani fi lele lati so mi di ota Olorun. \r\nBIBELI KIKA/OJOJUMO: Hosea 5-9', '2023-05-15'),
(344, '2023-12-05', '05, TUESDAY DECEMBER', 'GBO OHUN MI', 'Ibi Kika: Awon Onidajo 6:7-8', 'Eti Olorun nigbagbogbo n tetisi eniti o bikita lati kepe e. Awon omo IsraelI ke pe Olorun nigba ti won wa ninu irora nla.  Nigbati o ba n koja larin awon akoko ti ko dara, kigbe si Olorun ninu adura.', 'ADURA: \r\n*Olorun yoo dahun gbogbo adura mi ni oruko Jesu. \r\nBIBELI KIKA/OJOJUMO: Hosea10-14', '2023-05-15'),
(345, '2023-12-06', '06, WEDNESDAY DECEMBER', 'IFE OTITO', 'Ibi Kika: 1Korinti 13:1', 'Okan ninu awon eso ti Emi ni ife. Ife je imolara lona ti o lagbara. Olorun ni eni akoko ti yoo fi ife tooto o han, nipa fÃ­fun wa ni omo re kan soso lati ra araye pada. Nigbati o ba nife, o fi han nipa fifuni. Ti a ko ba fi ife han si awon elomiran, lehinna a ko lati je omo ti Olorun bi. Je ki ife se afihan ninu fifunni ati ihuwasi re.', 'ADURA: \r\n*Emi o fi ife otito ti Kristi han agbaye nipase ebun mi loruko Jesu. \r\nBIBELI KIKA/OJOJUMO: Joeli 1-3', '2023-05-15'),
(346, '2023-12-07', '07, THURSDAY DECEMBER', 'IDARAPO MO AYE', 'Ibi Kika: Roomu 12:2', 'Eto aye tako eto ijoba Olorun. Lati jerisi si agbaye tumo si lati tele awon apeere ti agbaye. Aye ko ni nkan ti o dara lati fun wa, biko se iwa aye ati ibajáº¹ lasan. Idi niyen tÃ­ Paulu fi gba wa niyanju pe ka ma se te le awon apeere aye. Je ki a sora fun eyi.', 'ADURA: \r\n*Nipa oore-ofe Olorun, Emi ko ni ni ibamu pelu eto agbaye laibikita ohun ti aye yoo mu wa fun mi ni oruká» Jesu. \r\nBIBELI KIKA/OJOJUMO: Amosi 1-5', '2023-05-15'),
(347, '2023-12-08', '08, FRIDAY DECEMBER', 'ISE ARA', 'Ibi Kika: 1Korinti 3:3', 'Lati je ti ara tumo si pe ki ohun ti ara ma joba ninu aye wa, tabi ki a ma ni itelorun lori ohun ti eran-ara. Aye je ti ara ati pe ti a ko ba sora gegebi kristeni, a yoo tun huwa gege bi agbaye, nipasere,  a o ko awon ohun ti Olorun sile.', 'ADURA:\r\n*Mo bori gbogbo iwa ti ara loruko Jesu. \r\nBIBELI KIKA/OJOJUMO: Amosi 6-9', '2023-05-15'),
(348, '2023-12-09', '09, SATURDAY DECEMBER', 'IFERAN ARA', 'Ibi Kika: Heberu 13:1', 'Ibasepo ti ara ma nwa nugbati o ba fidimule ninu ife. Ma se rewesi ni fÃ­fi ife han si elomiran, nitori ere re daju. Se afihan ife loni!', 'ADURA: \r\n*Oluwa, okan mi si sile, jeki okan mi la lati fi ife han awon elomiran loruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Obadiah 1', '2023-05-15'),
(349, '2023-12-10', '10, SUNDAY DECEMBER', 'ISE RERE MI KO NI SE E GBAGBE', 'Ibi Kika: Eksodu 1:8', 'A gbagbe ise rere Josefu leyin ti o ku, Farao tuntun si dide ti o je awon omo Israeli niya. Nitorina opolopo eniyan tun wa ninu bata yii. Gbogbo ise rere ti won se ni a ti gbagbe, titi kan ere won paapaa. Ise rere wa ko ni se e gbagbe.', 'ADURA: \r\n*Ise rere mi koni di igbagbe, ere mi koni fa idaduro loruko Jesu. \r\nBIBELI KIKA/OJOJUMO: Jona 1-4', '2023-05-15'),
(350, '2023-12-11', '11, MONDAY DECEMBER', 'IJEWO  NKAN SE PATAKI', 'Ibi Kika: 2Awon Oba 4:26', 'Ni gbogbo ipo ti eniyan le wa, ijewo wa se pataki pupo. Omo obinrin naa lati inu ese Iwe Mimo wa ti ku, sibe o si jewo pelu igbagbo pe â€˜â€™o daraâ€™â€™â€™. Ijewo awon eniyan fun ara won je buburu, awon elomiran feran lati ma je eegun lori ara won tabi awon miiran nigbakugba ti won ba se awon asise. Ijewo rere obÃ¬nrin nÃ¡Ã  nipa omo re mu omo re pada wa si iye nipase Wolii Eliá¹£a. Ni akoko ti o dara ati buburu, jewo awon ohun rere si igbesi aye re. Ko eko lati jewo awon ohun rere sinu igbesi aye re lojoojumo.', 'ADURA: \r\n*Mo jewo sinu aye mi, o ti dara fun mi, Mo wa loke nko si nisale o si dara fun emi mi ni oruko Jesu.\r\n BIBELI KIKA/OJOJUMO: 1Johannu 1-5', '2023-05-15'),
(351, '2023-12-12', '12, TUESDAY DECEMBER', 'KINI O WA LOWO RE?', 'Ibi Kika: Eksodu 4:2', 'Opolopo ni ohun ologo ni o wa lowo awon eniyan, sugbon won ko mo nipa re. Ni owo Mose, o ni opa kan lasan, ti o wa di opa ti a n lo fun ise ami ati ise iyanu. Ibeere kan ni fun gbogbo wa, wo ohun ti o wa ni owo re, je ki Olorun simi lori re yoo si farahan ni ogo.', 'ADURA: \r\n*Oluwa yoo ran mi lowo lati se awari ohun ologo to wa lowo mi, yoo si han ni oruko Jesu. \r\nBIBELI KIKA/OJOJUMO: 2Johanu, 3Johanu', '2023-05-15'),
(352, '2023-12-13', '13, WEDNESDAY DECEMBER', 'ITUSILE KURO LOWO OTA ANINILARA', 'Ibi Kika: Jeremiah 50:33-34', 'Ipalara je ohun ti o wuwo ti a mo lara, tabi idilowo ninu ara tabi okan. Nigba ti ininilara ba po, irewesÃ¬ ma ba eniyan, ko ni le wulo. Inilara n paala si eniyan, o si tun le din emi eniyan kuru. A dupe lowo Olorun, Olorun dide, o si da awon omo Israeli nide lowo aninilara won. Oun yoo gba o loni!', 'ADURA: \r\n*A gbami loni lowo gbogbo eyikeyi inira to di mi mu ni oruko Jesu. \r\nBIBELI KIKA/OJOJUMO: Mika 1-3', '2023-05-15'),
(353, '2023-12-14', '14, THURSDAY DECEMBER', 'IROYIN BURUKU YI SI RERE', 'Ibi Kika: Isaiah 38:1-5', 'Wolii Isaiah fi ise iku rÃ¡nse si Hesekaya á»Œba, á¹£ugbon Hesekaya ni imo lati mo pe a le ko iroyin buburu sile. Ko awon iroyin buburu sile ni gbogbo ona, nipase adura yi won pada si rere. Iwo kii yoo kuku ojiji.', 'ADURA: \r\n*Ni oruko Jesu, iku ojiji kii se ipin mi. \r\nBIBELI KIKA/OJOJUMO: Mika 4-5', '2023-05-15'),
(354, '2023-12-15', '15, FRIDAY DECEMBER', 'ALAABO WA', 'Ibi Kika: Orin Dafidi 33:20', 'Ni akoko yii, a ti n lo diedie si opin odun, eru n ba opolopo nitori aidaniloju aabo won, sugbon Olorun seleri lati je apata wa. Nitori naa, je ki okan re bale pe Olorun ni Ã¬bora re ni osan ati ni ale.', 'ADURA: \r\n*Oluwa yoo ma je abo mi laelae ati bi a ti n pari odun yi loruko Jesu. \r\nBIBELI KIKA/OJOJUMO: Mika 6-7', '2023-05-15'),
(355, '2023-12-16', '16, SATURDAY DECEMBER', 'FARA MO OLORUN RE', 'Ibi Kika: Onidajá» 8:34', 'Opolopo maa n gbagbe nipa Olorun nigba ti won ba ti de ipo ti won fe ninu igbesi aye won. Iwe Mimo kilo fun wa pe ki a ma se gbagbe Olorun leyinn ti a ba ti ko ile, ti a si ri isinmi fun okan wa. Alaimo ore nikan ni yoo gbagbe orisun re. Mase fi Olorun sile paapaa nigba ti o ba ti di alagbara fara mo o sii paapaa ni ipo oro ati opo.', 'ADURA: \r\n*Ninu opo mi, Emi ko ni fi Olorun sile tabi ko Olorun sile ni oruko Jesu. \r\nBIBELI KIKA/OJOJUMO: Habakuku 1-3', '2023-05-15'),
(356, '2023-12-17', '17, SUNDAY DECEMBER', 'OJO ISINMI', 'Ibi Kika: Genesisi 2:2-3', 'Isinmi je okan ninu awon abuda ti Olorun. O je ipo ominira kuro lowo ohun gbogbo ti o mu ni re eni tabi ti n dani laamu. Nitori naa, Olorun reti pe ki gbogbo eni ti a da ni aworan ara re te le eyi. Lehin ti ara re ba ti damu pupo, jowo sinmi. Jesu nigba ise ti ayanfunni lori ile aye ma n lo si ibi adawa kan lati sinmi. Nigbakugba ti ara re ba re o, sinmi ki iwá» ki o ma simi ni ninu iku.', 'ADURA: \r\n* Mo ko lati pa ara mi run nipase aibikita mi lori ilera ati ilera ara mi ni oruko Jesu. \r\nBIBELI KIKA/OJOJUMO: Sefanaya 1-3', '2023-05-15'),
(357, '2023-12-18', '18, MONDAY DECEMBER', 'YAN NI ONA OLOGBON', 'Ibi Kika: Josua 24:15', 'A n gbe ati ku nipase awon yiyan ti a se. Eeyan ni eto ominira lati yan ayanmo ayeraye ati ohun to ye ko maa se lojoojumo ninu igbesi aye re. Tani iwo yoo ma sÃ¬n, Olorun tabi olorun miran? Yan pelu ogbon. Sugbon fun opolopo awon ti o wa ni apa Oluwa, ayanmo ati ojo iwaju won ni aabo ninu Jesu.', 'ADURA: \r\n*Emi yoo ni itosona ati alaye to to lati se awon yiyan ti o to ni oruko Jesu.\r\n BIBELI KIKA/OJOJUMO: Hagai 1-2', '2023-05-15'),
(358, '2023-12-19', '19, TUESDAY DECEMBER', 'LO ASIKO OJO RE', 'Ibi Kika: Johannu 9:4', 'Akoko ojo tokasi akoko ti ko si okunkun. Akoko ti awon nkan le see se laisi idiwo. Jesu so fun awon omo eyin re pe ise oun gbodo se ni osan. Ojo tun tumo si akoko igba ewe eniyan, nigbati agbara wa ni ekunrere, nigbati awon egungun ati ara ba lagbara. A nilo lati lo akoko ojo wa, lati le mu ayanmo ati ipinnu Olorun fun igbesi aye wa se.  O dara lati se iwari idi re ni kutukutu igbesi aye, ki o le gbe won jade ki o leni ipa si igbesi aye ti o sopo mo o. Jesu lo akoko ojo re dara dara, O pari ise igbala laarin odun meta ati idaji. Ma se duro titi ale re yoo fi de, nigba ti agbara re ti pin, ki o to bere ise ti Olorun gbe le o lowo.', 'ADURA:\r\n*Mo ko gbogbo emi ole, Mo bere lati lo akoko ojo mi daradara ni oruko Jesu.\r\nBIBELI KIKA/OJOJUMO: Sakariah 1-5', '2023-05-15'),
(359, '2023-12-20', '20, WEDNESDAY DECEMBER', 'O DARA PUPO', 'Ibi Kika: Genesisi 1:31', 'Olorun, ti o da wa ni aworan re, o da ohun gbogbo, o si fi idi won mule daradara. Nitorina, ká» ohun gbogbo ti ko dara nipa re.', 'ADURA: \r\n*Gbogbo ohun buburu ni igbesi aye mi, idile ati ise ko ni eto lati duro, Mo pase fun o, jade ni oruko Jesu. \r\nBIBELI KIKA/OJOJUMO: Sakariah 6-9', '2023-05-15'),
(360, '2023-12-21', '21, THURSDAY DECEMBER', 'IROYIN BURUKU KII SE IPIN RE', 'Ibi Kika: Jobu 1:13-15', 'Iroyin je nikan ti o dara nigba ti o ba soro ayo. Nigba aye Joobu, awon isele buburu merin sele si i laarin ojo kan pere. Awon iranse re n mu iroyin buburu wa fun un. Ko eko lati soro sinu ojo re ni ipile ojoojumo. Paapaa iwe-mimo so pe, gege bi iwo ti pase fun owuro re? â€™â€™', 'ADURA: \r\n*Eti mi ko ni gbo iroyin buburu bi odun ti n lo sopin loruko Jesu. \r\nBIBELI KIKA/OJOJUMO: Sakariah 10-14', '2023-05-15'),
(361, '2023-12-22', '22, FRIDAY DECEMBER', 'ODUN MI GBA IRE', 'Ibi Kika: Orin Dafidi 65:11', 'Nigbati ade odun re pelu oore re, nigbana ni iwo o di eleri oju rere re. O ti so o ninu oro re, nitori ki okan re bale pe odun re ti de ade ohun rere ati ora gbogbo awon ohun rere ti o le ti lepa, yoo wa ni ilopo meji bi odun ti n pari.', 'ADURA: \r\n*Mo gba ohun to dara julo ninu odun ki o to pare ni oruko Jesu. \r\nBIBELI KIKA/OJOJUMO: Malaki 1-4', '2023-05-15'),
(362, '2023-12-23', '23, SATURDAY DECEMBER', 'IRE OLORUN', 'Ibi Kika: Orin Dafidi 23:6', 'Oni saamu na gbadura idaniloju pe oore Olorun yoo maa te le e dajudaju ni gbogbo ojo aye re. Nigbati ibukun Olorun ba nsan lojoojumo ati laisi idilowo ninu igbesi aye re, lehinna eniyan n gbadun oore re.', 'ADURA: \r\n*Emi yoo tesiwaju lati gbadun oore Olorun ni ile awon alaye ni oruko Jesu. \r\nBIBELI KIKA/OJOJUMO: Juuda', '2023-05-15'),
(363, '2023-12-24', '24, SUNDAY DECEMBER', 'AIYIPADA TO YII IKAN PADA', 'Ibi Kika: Danieli 2:20-21', 'Olorun ni aiyipada ti o le yipada awon akoko ati awon akoko sibesibe ko yipada. Oun ni olufihan gbogbo asiri. Fun gbogbo ipo ti ko dun ni igbesi aye re, Olorun le yi won pada nitori pe oun ni aiyipada ti o le yipada.', 'ADURA: \r\n*Olorun n yi igbesi aye mi pada ati gbogbo ipo ti ko dun ni oruko Jesu. \r\nBIBELI KIKA/OJOJUMO: Ifihan 1-3', '2023-05-15'),
(364, '2023-12-25', '25, MONDAY DECEMBER', 'IDAYATO GBOGBO OMODEO YII IKAN PADA', 'Ibi Kika: Isaiah 9:6', 'Gbogbo omo ti a bi si idile ni idayato tie. Iyato ti gbogbo omode ni ipinnu nipase agbara re. Gbogbo omo ni ayanmo alailegebe ati idi kan lati mu se ni igbesi aye. Ayanmo Jesu nigba omode ni a ti so tele saaju ibi re paapaa. Gege bi obi, a ni lati mo kadara awon omo wa koda ki won to bi won, ki won le to won dagba lona Olorun ati ni ona to to.', 'ADURA: \r\n*Gbogbo ayanmo awon omo mi ni yoo se ni oruko Jesu. \r\nBIBELI KIKA/OJOJUMO: Ifihan 4-6', '2023-05-15'),
(365, '2023-12-26', '26, TUESDAY DECEMBER', 'MI O NI PARI ODUN YII PELU EKUN', 'Ibi Kika: Jeremiah 31:15', 'Jijewo nkan ni agbara. Ikuna lati kede ohun ti o fe yoo je ki esu fi ohun ti o ni sinu re, ati pe awon ohun idogo esu je ohun ti o buru ju ati pe kii dun. Iberu eniyan maa n po si i nigba ti odun ba n lo sopin, sugbon gege bi omo Olorun, jewo re: â€˜Mi o ni fi ekun pari odun miâ€™.', 'ADURA: \r\n*Kigbe: Emi ko ni pari odun yi pelu ofo awon omo mi loruko Jesu. \r\nBIBELI KIKA/OJOJUMO: Ifihan 7-8', '2023-05-15'),
(366, '2023-12-27', '27, WEDNESDAY DECEMBER', 'JESU: OLUGBALA WA', 'Ibi Kika: Isaiah 7:14', 'Ibi Jesu ni asotele. O wa bi olugbala. O ngbanila O si tun n gba eda eniyan la. O mu eto ibi re se nÃ­pa gbigbala ati pipa eda eniyan ni ibasepo pelu Olorun.', 'ADURA: \r\n*Eje Jesu iyebiye ni a gba mi la ni oruko Jesu \r\nBIBELI KIKA/OJOJUMO: Ifihan 9-11', '2023-05-15'),
(367, '2023-12-28', '28, THURSDAY DECEMBER', 'AGBARA LATI DI', 'Ibi Kika: Johannu 1:12', 'Ni ijoba, okunrin tabi obinrin le di ohunkohun ti o fe. Ni ori agbelebu, a le yi igbesi aye pada. Gbogbo eyi ni a se se ni ikorita ti a ba gba Jesu gegebi Oluwa re. Nje o ti gbaa bi?', 'ADURA: \r\n*Bi mo ti fi aye mi fun Jesu loni, Mo gba agbara lati di ibukun fun iran mi ni oruko Jesu. \r\nBIBELI KIKA/OJOJUMO: Ifihan 12-14', '2023-05-15'),
(368, '2023-12-29', '29, FRIDAY DECEMBER', 'ODALE AYANMO', 'Ibi Kika: Matteu 26:21', 'Jesu mo eni to maa da a. Bo ti wu ko o je oninuure to, odale ore a si gbe iwa re yoo. Gegebi oro otito won ko bikita eni ti o je tabi bi o se sunmo won. Ki Oluwa yo won kuro ni ona re ni oruko Jesu.', 'ADURA: \r\n*Gbogbo awon odale to yimika ni Jesu yio tun asiri won ni oruko Jesu. \r\nBIBELI KIKA/OJOJUMO: Ifihan 15-17', '2023-05-15'),
(369, '2023-12-30', '30, SATURDAY DECEMBER', 'KO BI A TIN SETO', 'Ibi Kika: Luku 14:28-30', 'Igbaradi ti ko dara ma nfa eto ti ko dara ati awon abajade ti ode oni dogba si eto ti a se lana. Akole ka iye owo saaju ki o to bere kiko. Bi á»dun ti n pari, eto wo ni o n se fun odun to nbo, fun igbeyawo re, fun okowo yen ati fun ise re. Ki Olorun ran o lowo lati mu eto re se bi o ti n ko won sile loruko Jesu.', 'ADURA: \r\n*Oluwa, ran mi lowo lati seto aye mi gege bi ife re loruko Jesu. \r\nBIBELI KIKA/OJOJUMO: Ifihan 18-20', '2023-05-15'),
(370, '2023-12-31', '31, SUNDAY DECEMBER', 'O SESE', 'Ibi Kika: Matteu 19:26', 'Ninu iwe-itumo Olorun, oro naa â€˜â€™ai seeseâ€™â€™ ko si! Ni otito, oro naa \'\'ko see se\'\' pelu ipa Kristi tumo si \'\'Mole se\'\'. Se o tun gbagbo pe awon ireti yen tun le jasi aseyori? PELU RE, OHUN GBOGBO LE SE SE.', 'ADURA: \r\n*Kede: Iyanu mi se se \r\nIgbeyawo mi se se, kii yoo subu \r\nIran mi ti se se, ko ni ku ni oruko Jesu. \r\nBIBELI KIKA/OJOJUMO: Ifihan 21-22', '2023-05-15');
